/* Pulsing glow animation for running status dot */
@keyframes pulse-glow-indigo {
	0%,
	100% {
		box-shadow: 0 0 4px rgba(99, 102, 241, 0.4);
	}
	50% {
		box-shadow: 0 0 12px rgba(99, 102, 241, 0.8);
