  1→import { useCallback, useEffect, useMemo, useState } from "react";
  2→import type {
  3→	ProviderName,
  4→	RemediationPromptsResponse,
  5→	TargetAgent,
  6→} from "../hooks/useEvaluationApi";
  7→import { useEvaluationApi } from "../hooks/useEvaluationApi";
  8→import {
  9→	type ProviderStatus,
 10→	useProviderDetection,
 11→} from "../hooks/useProviderDetection";
 12→import { useSSE } from "../hooks/useSSE";
 13→import type { EvaluationOutput, Issue } from "../types/evaluation";
 14→import {
 15→	getImpactBadgeClass,
 16→	getImpactLabel,
 17→	getIssueSeverity,
 18→	getIssueType,
 19→	getSeverityColor,
 20→	getSeverityLevel,
 21→} from "../types/evaluation";
 22→import type {
 23→	IRemediationProgressState,
 24→	RemediationHistoryItem,
 25→	RemediationResult,
 26→} from "../types/remediation";
 27→import { RemediationHistory } from "./RemediationHistory";
 28→import { RemediationProgress } from "./RemediationProgress";
 29→import { CopyButton } from "./shared/CopyButton";
 30→import { Modal } from "./shared/Modal";
 31→
 32→type Phase = "idle" | "progress";
 33→
 34→interface RemediateTabProps {
 35→	evaluationId: string | null;
 36→	evaluationData: EvaluationOutput | null;
 37→	selectedIssueKeys: Set<string>;
 38→	issueKeyMap: Map<string, Issue>;
 39→	onRemoveIssue: (key: string) => void;
 40→	onClearAll: () => void;
 41→	cloudMode?: boolean;
 42→}
 43→
 44→export function RemediateTab({
 45→	evaluationId,
 46→	evaluationData,
 47→	selectedIssueKeys,
 48→	issueKeyMap,
 49→	onRemoveIssue,
 50→	onClearAll,
 51→	cloudMode = false,
 52→}: RemediateTabProps) {
 53→	const api = useEvaluationApi();
 54→	const providerDetection = useProviderDetection();
 55→
 56→	// Phase: idle (config visible) or progress (SSE running)
 57→	const [phase, setPhase] = useState<Phase>("idle");
 58→	const [targetAgent, setTargetAgent] = useState<TargetAgent>("agents-md");
 59→	const [selectedProvider, setSelectedProvider] =
 60→		useState<ProviderName>("claude");
 61→	const [isExecuting, setIsExecuting] = useState(false);
 62→	const [showConfirmModal, setShowConfirmModal] = useState(false);
 63→	const [executeError, setExecuteError] = useState<string | null>(null);
 64→
 65→	// History state
 66→	const [remediations, setRemediations] = useState<RemediationHistoryItem[]>(
 67→		[],
 68→	);
 69→
 70→	// SSE state
 71→	const [sseUrl, setSseUrl] = useState<string | null>(null);
 72→	const [, setActiveRemediationId] = useState<string | null>(null);
 73→	const [progressState, setProgressState] = useState<IRemediationProgressState>(
 74→		{
 75→			status: "queued",
 76→			logs: [],
 77→		},
 78→	);
 79→
 80→	// Legacy prompt generation state
 81→	const [isGenerating, setIsGenerating] = useState(false);
 82→	const [generateError, setGenerateError] = useState<string | null>(null);
 83→	const [prompts, setPrompts] = useState<RemediationPromptsResponse | null>(
 84→		null,
 85→	);
 86→
 87→	// Detect providers on mount
 88→	useEffect(() => {
 89→		if (providerDetection.status === "idle") {
 90→			providerDetection.detectProviders();
 91→		}
 92→	}, [providerDetection]);
 93→
 94→	// Load remediation history on mount / evaluationId change
 95→	const refreshHistory = useCallback(async () => {
 96→		if (!evaluationId) return;
 97→		const data = await api.getRemediationsForEvaluation(evaluationId);
 98→		setRemediations(data.remediations);
 99→		return data;
100→	}, [evaluationId, api]);
101→
102→	useEffect(() => {
103→		if (!evaluationId) return;
104→
105→		let cancelled = false;
106→
107→		async function loadRemediations() {
108→			const data = await api.getRemediationsForEvaluation(evaluationId!);
109→			if (cancelled) return;
110→
111→			setRemediations(data.remediations);
112→
113→			// If there's an active job, connect SSE
114→			if (data.activeJob) {
115→				setActiveRemediationId(data.activeJob.id);
116→				setSseUrl(
117→					`${window.location.origin}/api/remediation/${data.activeJob.id}/progress`,
118→				);
119→				setProgressState({
120→					status: data.activeJob.status as "queued" | "running",
121→					logs: [],
122→				});
123→				setPhase("progress");
124→			}
125→		}
126→
127→		loadRemediations();
128→
129→		return () => {
130→			cancelled = true;
131→		};
132→	}, [evaluationId, api]);
133→
134→	// Available providers for the selector
135→	const availableProviders = useMemo(() => {
136→		const providers: ProviderStatus[] = [];
137→		for (const p of providerDetection.providers.values()) {
138→			if (p.available && p.name !== "random") {
139→				providers.push(p);
140→			}
141→		}
142→		return providers;
143→	}, [providerDetection.providers]);
144→
145→	// Resolve selected issue keys to actual issues, split by type
146→	const { errorEntries, suggestionEntries } = useMemo(() => {
147→		const errors: { key: string; issue: Issue }[] = [];
148→		const suggestions: { key: string; issue: Issue }[] = [];
149→
150→		for (const key of selectedIssueKeys) {
151→			const issue = issueKeyMap.get(key);
152→			if (!issue) continue;
153→			const type = getIssueType(issue);
154→			if (type === "error") {
155→				errors.push({ key, issue });
156→			} else {
157→				suggestions.push({ key, issue });
158→			}
159→		}
160→
161→		errors.sort(
162→			(a, b) => getIssueSeverity(b.issue) - getIssueSeverity(a.issue),
163→		);
164→		const impactOrder: Record<string, number> = {
165→			High: 0,
166→			Medium: 1,
167→			Low: 2,
168→		};
169→		suggestions.sort(
170→			(a, b) =>
171→				(impactOrder[a.issue.impactLevel ?? "Low"] ?? 2) -
172→				(impactOrder[b.issue.impactLevel ?? "Low"] ?? 2),
173→		);
174→
175→		return { errorEntries: errors, suggestionEntries: suggestions };
176→	}, [selectedIssueKeys, issueKeyMap]);
177→
178→	const totalSelected = errorEntries.length + suggestionEntries.length;
179→
180→	// SSE message handler
181→	const handleSSEMessage = useCallback(
182→		// biome-ignore lint/suspicious/noExplicitAny: SSE event data varies by type
183→		(event: any) => {
184→			const timestamp = new Date().toISOString();
185→
186→			switch (event.type) {
187→				case "remediation.started":
188→					setProgressState((prev) => ({
189→						...prev,
190→						status: "running",
191→						logs: [...prev.logs, { timestamp, message: "Remediation started" }],
192→					}));
193→					break;
194→
195→				case "remediation.progress": {
196→					const d = event.data ?? {};
197→					setProgressState((prev) => ({
198→						...prev,
199→						...(d.errorCount !== undefined && {
200→							errorCount: d.errorCount as number,
201→						}),
202→						...(d.suggestionCount !== undefined && {
203→							suggestionCount: d.suggestionCount as number,
204→						}),
205→						...(d.totalBatches !== undefined && {
206→							totalBatches: d.totalBatches as number,
207→						}),
208→						...(d.completedBatches !== undefined && {
209→							completedBatches: d.completedBatches as number,
210→						}),
211→						...(d.phase !== undefined && {
212→							currentPhase: d.phase as "errors" | "suggestions",
213→						}),
214→						...(d.runningTotalCostUsd !== undefined && {
215→							runningTotalCostUsd: d.runningTotalCostUsd as number,
216→						}),
217→						...(d.runningTotalDurationMs !== undefined && {
218→							runningTotalDurationMs: d.runningTotalDurationMs as number,
219→						}),
220→						...(d.runningTotalInputTokens !== undefined && {
221→							runningTotalInputTokens: d.runningTotalInputTokens as number,
222→						}),
223→						...(d.runningTotalOutputTokens !== undefined && {
224→							runningTotalOutputTokens: d.runningTotalOutputTokens as number,
225→						}),
226→					}));
227→					break;
228→				}
229→
230→				case "remediation.step.started": {
231→					const bi = event.data?.batchInfo as
232→						| { batchNumber: number; totalBatches: number }
233→						| undefined;
234→					const batchLabel = bi
235→						? ` (batch ${bi.batchNumber}/${bi.totalBatches})`
236→						: "";
237→					const issueSummaries = event.data?.issuesSummary as
238→						| string[]
239→						| undefined;
240→					setProgressState((prev) => ({
241→						...prev,
242→						currentStep: event.data?.step,
243→						batchInfo: bi,
244→						...(issueSummaries && {
245→							currentBatchIssues: issueSummaries,
246→						}),
247→						logs: [
248→							...prev.logs,
249→							{
250→								timestamp,
251→								message: `Step started: ${event.data?.step}${batchLabel}`,
252→							},
253→						],
254→					}));
255→					break;
256→				}
257→
258→				case "remediation.step.completed": {
259→					const bi2 = event.data?.batchInfo as
260→						| { batchNumber: number; totalBatches: number }
261→						| undefined;
262→					const batchLabel2 = bi2
263→						? ` (batch ${bi2.batchNumber}/${bi2.totalBatches})`
264→						: "";
265→					const step = event.data?.step as string | undefined;
266→					const batchDurationMs = event.data?.batchDurationMs as
267→						| number
268→						| undefined;
269→					const batchCostUsd = event.data?.batchCostUsd as number | undefined;
270→					const batchInputTokens = event.data?.batchInputTokens as
271→						| number
272→						| undefined;
273→					const batchOutputTokens = event.data?.batchOutputTokens as
274→						| number
275→						| undefined;
276→
277→					const hasBatchStats =
278→						batchDurationMs !== undefined && batchCostUsd !== undefined;
279→					let logMessage = `Step completed: ${step}${batchLabel2}`;
280→					if (hasBatchStats) {
281→						const phaseName =
282→							step === "executing_error_fix"
283→								? "Error fix"
284→								: "Suggestion enrich";
285→						const label = bi2
286→							? `${phaseName} batch ${bi2.batchNumber}/${bi2.totalBatches}`
287→							: phaseName;
288→						logMessage = `${label} completed: ${Math.round(batchDurationMs / 1000)}s, $${batchCostUsd.toFixed(4)}`;
289→					}
290→
291→					setProgressState((prev) => ({
292→						...prev,
293→						batchInfo: undefined,
294→						currentBatchIssues: undefined,
295→						...(hasBatchStats && {
296→							lastBatchStats: {
297→								durationMs: batchDurationMs!,
298→								costUsd: batchCostUsd!,
299→								inputTokens: batchInputTokens ?? 0,
300→								outputTokens: batchOutputTokens ?? 0,
301→							},
302→						}),
303→						logs: [...prev.logs, { timestamp, message: logMessage }],
304→					}));
305→					break;
306→				}
307→
308→				case "remediation.completed": {
309→					setProgressState((prev) => ({
310→						...prev,
311→						status: "completed",
312→						result: event.data?.result as RemediationResult | undefined,
313→						logs: [
314→							...prev.logs,
315→							{ timestamp, message: "Remediation completed" },
316→						],
317→					}));
318→					// Reset to idle, refresh history, clear selection
319→					setSseUrl(null);
320→					setActiveRemediationId(null);
321→					setPhase("idle");
322→					onClearAll();
323→					refreshHistory();
324→					break;
325→				}
326→
327→				case "remediation.failed":
328→					setProgressState((prev) => ({
329→						...prev,
330→						status: "failed",
331→						error: event.data?.error,
332→						logs: [
333→							...prev.logs,
334→							{
335→								timestamp,
336→								message: `Failed: ${event.data?.error?.message || "Unknown error"}`,
337→							},
338→						],
339→					}));
340→					setExecuteError(event.data?.error?.message || "Remediation failed");
341→					setSseUrl(null);
342→					setActiveRemediationId(null);
343→					setPhase("idle");
344→					refreshHistory();
345→					break;
346→			}
347→		},
348→		[onClearAll, refreshHistory],
349→	);
350→
351→	useSSE({
352→		url: sseUrl,
353→		onMessage: handleSSEMessage,
354→	});
355→
356→	// Execute remediation
357→	const handleExecute = useCallback(async () => {
358→		if (!evaluationId || totalSelected === 0) return;
359→
360→		setIsExecuting(true);
361→		setExecuteError(null);
362→		setPrompts(null);
363→
364→		try {
365→			const issues = [
366→				...errorEntries.map((e) => e.issue),
367→				...suggestionEntries.map((e) => e.issue),
368→			];
369→
370→			const response = await api.executeRemediation(
371→				evaluationId,
372→				issues,
373→				targetAgent,
374→				selectedProvider,
375→			);
376→
377→			setActiveRemediationId(response.remediationId);
378→			setSseUrl(response.sseUrl);
379→			setProgressState({ status: "queued", logs: [] });
380→			setPhase("progress");
381→		} catch (err) {
382→			const message =
383→				err instanceof Error ? err.message : "Failed to start remediation";
384→			setExecuteError(message);
385→		} finally {
386→			setIsExecuting(false);
387→		}
388→	}, [
389→		evaluationId,
390→		totalSelected,
391→		errorEntries,
392→		suggestionEntries,
393→		targetAgent,
394→		selectedProvider,
395→		api,
396→	]);
397→
398→	// Generate prompts (legacy copy-paste mode)
399→	const handleGeneratePrompts = useCallback(async () => {
400→		if (!evaluationId || totalSelected === 0) return;
401→
402→		setIsGenerating(true);
403→		setGenerateError(null);
404→
405→		try {
406→			const issues = [
407→				...errorEntries.map((e) => e.issue),
408→				...suggestionEntries.map((e) => e.issue),
409→			];
410→			const result = await api.generateRemediationPrompts(
411→				evaluationId,
412→				issues,
413→				targetAgent,
414→			);
415→			setPrompts(result);
416→		} catch (err) {
417→			setGenerateError(
418→				err instanceof Error ? err.message : "Failed to generate prompts",
419→			);
420→		} finally {
421→			setIsGenerating(false);
422→		}
423→	}, [
424→		evaluationId,
425→		totalSelected,
426→		errorEntries,
427→		suggestionEntries,
428→		targetAgent,
429→		api,
430→	]);
431→
432→	// Delete a remediation from history
433→	const handleDeleteRemediation = useCallback(
434→		async (remediationId: string) => {
435→			await api.deleteRemediation(remediationId);
436→			setRemediations((prev) => prev.filter((r) => r.id !== remediationId));
437→		},
438→		[api],
439→	);
440→
441→	if (!evaluationData) {
442→		return (
443→			<div className="card text-center py-12">
444→				<p className="text-body-muted">
445→					No evaluation data. Run an evaluation first to generate remediation
446→					prompts.
447→				</p>
448→			</div>
449→		);
450→	}
451→
452→	// ─── PROGRESS PHASE ────────────────────────────────────────────────
453→	if (phase === "progress") {
454→		return (
455→			<div className="space-y-6">
456→				<div className="card">
457→					<h2 className="text-heading text-slate-100">Executing Remediation</h2>
458→					<p className="text-body-muted mt-1">
459→						Running {selectedProvider} agent to fix{" "}
460→						{totalSelected > 0 ? totalSelected : "selected"} issue
461→						{totalSelected !== 1 ? "s" : ""}...
462→					</p>
463→				</div>
464→				<RemediationProgress
465→					currentStep={progressState.currentStep}
466→					batchInfo={progressState.batchInfo}
467→					logs={progressState.logs}
468→					errorCount={progressState.errorCount}
469→					suggestionCount={progressState.suggestionCount}
470→					totalBatches={progressState.totalBatches}
471→					completedBatches={progressState.completedBatches}
472→					currentPhase={progressState.currentPhase}
473→					runningTotalCostUsd={progressState.runningTotalCostUsd}
474→					runningTotalDurationMs={progressState.runningTotalDurationMs}
475→					runningTotalInputTokens={progressState.runningTotalInputTokens}
476→					runningTotalOutputTokens={progressState.runningTotalOutputTokens}
477→					currentBatchIssues={progressState.currentBatchIssues}
478→				/>
479→			</div>
480→		);
481→	}
482→
483→	// ─── IDLE PHASE (config + history) ─────────────────────────────────
484→	return (
485→		<div className="space-y-6">
486→			<div className="card">
487→				<h2 className="text-heading text-slate-100">Remediation</h2>
488→				<p className="text-body-muted mt-1">
489→					Execute remediation to fix issues automatically, or generate
490→					copy-paste prompts
491→				</p>
492→			</div>
493→
494→			{/* Past Remediations History */}
495→			<RemediationHistory
496→				remediations={remediations}
497→				onDelete={handleDeleteRemediation}
498→				cloudMode={cloudMode}
499→			/>
500→
501→			{/* Empty state when no issues selected */}
502→			{totalSelected === 0 ? (
503→				<div className="card text-center py-12">
504→					<svg
505→						className="w-12 h-12 text-slate-600 mx-auto mb-4"
506→						fill="none"
507→						stroke="currentColor"
508→						viewBox="0 0 24 24"
509→					>
510→						<path
511→							strokeLinecap="round"
512→							strokeLinejoin="round"
513→							strokeWidth={1.5}
514→							d="M12 4v16m8-8H4"
515→						/>
516→					</svg>
517→					<p className="text-body-muted">
518→						No issues selected. Use the{" "}
519→						<span className="inline-flex items-center justify-center w-5 h-5 rounded bg-slate-700 text-slate-300 text-xs align-middle mx-0.5">
520→							+
521→						</span>{" "}
522→						button on issue cards in the Errors and Suggestions tabs.
523→					</p>
524→				</div>
525→			) : (
526→				<div className="card space-y-5">
527→					{/* Target Agent */}
528→					<div>
529→						<label className="text-label text-slate-300 block mb-2">
530→							Target for markdown file rendering
531→						</label>
532→						<div className="flex gap-2">
533→							{(
534→								[
535→									["agents-md", "AGENTS.md"],
536→									["claude-code", "Claude Code"],
537→									["github-copilot", "GitHub Copilot"],
538→								] as const
539→							).map(([value, label]) => (
540→								<button
541→									key={value}
542→									onClick={() => setTargetAgent(value)}
543→									className={`px-3 py-1.5 rounded text-sm transition-colors ${
544→										targetAgent === value
545→											? "bg-blue-600 text-white"
546→											: "bg-slate-700 text-slate-300 hover:bg-slate-600"
547→									}`}
548→								>
549→									{label}
550→								</button>
551→							))}
552→						</div>
553→					</div>
554→
555→					{/* Provider Selector */}
556→					<div>
557→						<label className="text-label text-slate-300 block mb-2">
558→							Pick the AI agent to execute the remediation
559→						</label>
560→						{providerDetection.status === "detecting" ? (
561→							<div className="text-sm text-slate-400">
562→								Detecting providers...
563→							</div>
564→						) : availableProviders.length > 0 ? (
565→							<div className="flex flex-wrap gap-2">
566→								{availableProviders.map((p) => (
567→									<button
568→										key={p.name}
569→										onClick={() => setSelectedProvider(p.name)}
570→										className={`px-3 py-1.5 rounded text-sm transition-colors ${
571→											selectedProvider === p.name
572→												? "bg-blue-600 text-white"
573→												: "bg-slate-700 text-slate-300 hover:bg-slate-600"
574→										}`}
575→									>
576→										{p.displayName}
577→									</button>
578→								))}
579→							</div>
580→						) : (
581→							<div className="text-sm text-slate-400">
582→								No providers detected. Defaulting to Claude.
583→							</div>
584→						)}
585→					</div>
586→
587→					{/* Selected Issues */}
588→					<div className="space-y-4">
589→						{errorEntries.length > 0 && (
590→							<IssueSection
591→								title="Errors"
592→								count={errorEntries.length}
593→								entries={errorEntries}
594→								onRemove={onRemoveIssue}
595→							/>
596→						)}
597→						{suggestionEntries.length > 0 && (
598→							<IssueSection
599→								title="Suggestions"
600→								count={suggestionEntries.length}
601→								entries={suggestionEntries}
602→								onRemove={onRemoveIssue}
603→							/>
604→						)}
605→						<div className="flex items-center justify-between">
606→							<span className="text-caption">
607→								{totalSelected} issue
608→								{totalSelected !== 1 ? "s" : ""} selected
609→							</span>
610→							<button
611→								onClick={onClearAll}
612→								className="text-sm text-slate-400 hover:text-slate-200 transition-colors"
613→							>
614→								Clear all
615→							</button>
616→						</div>
617→					</div>
618→
619→					{/* Action Buttons */}
620→					<div className="flex flex-wrap gap-3">
621→						<button
622→							onClick={() => setShowConfirmModal(true)}
623→							disabled={isExecuting || isGenerating}
624→							className="btn-primary"
625→						>
626→							{isExecuting ? (
627→								<span className="flex items-center gap-2">
628→									<svg
629→										className="animate-spin h-4 w-4"
630→										fill="none"
631→										viewBox="0 0 24 24"
632→									>
633→										<circle
634→											className="opacity-25"
635→											cx="12"
636→											cy="12"
637→											r="10"
638→											stroke="currentColor"
639→											strokeWidth="4"
640→										/>
641→										<path
642→											className="opacity-75"
643→											fill="currentColor"
644→											d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"
645→										/>
646→									</svg>
647→									Starting...
648→								</span>
649→							) : (
650→								`Execute Remediation (${totalSelected})`
651→							)}
652→						</button>
653→						<button
654→							onClick={handleGeneratePrompts}
655→							disabled={isExecuting || isGenerating}
656→							className="btn-secondary"
657→						>
658→							{isGenerating ? "Generating..." : "Generate Prompts"}
659→						</button>
660→					</div>
661→
662→					{executeError && (
663→						<p className="text-sm text-red-400 mt-2">{executeError}</p>
664→					)}
665→					{generateError && (
666→						<p className="text-sm text-red-400 mt-2">{generateError}</p>
667→					)}
668→				</div>
669→			)}
670→
671→			{/* Confirmation Modal */}
672→			<Modal
673→				isOpen={showConfirmModal}
674→				onClose={() => setShowConfirmModal(false)}
675→				title="Confirm Remediation"
676→				maxWidth="max-w-md"
677→			>
678→				<div className="space-y-4">
679→					<p className="text-sm text-slate-300">
680→						This will run the AI provider to remediate the selected issues.
681→						Please confirm the details below.
682→					</p>
683→					<div className="bg-slate-800/50 rounded-lg border border-slate-700/50 p-3 space-y-2 text-sm">
684→						{errorEntries.length > 0 && (
685→							<div className="flex justify-between text-slate-300">
686→								<span>Errors</span>
687→								<span className="font-semibold">{errorEntries.length}</span>
688→							</div>
689→						)}
690→						{suggestionEntries.length > 0 && (
691→							<div className="flex justify-between text-slate-300">
692→								<span>Suggestions</span>
693→								<span className="font-semibold">
694→									{suggestionEntries.length}
695→								</span>
696→							</div>
697→						)}
698→						<div className="flex justify-between text-slate-300">
699→							<span>Target for markdown file rendering</span>
700→							<span className="font-semibold">
701→								{targetAgent === "agents-md"
702→									? "AGENTS.md"
703→									: targetAgent === "claude-code"
704→										? "Claude Code"
705→										: "GitHub Copilot"}
706→							</span>
707→						</div>
708→						<div className="flex justify-between text-slate-300">
709→							<span>Provider</span>
710→							<span className="font-semibold">{selectedProvider}</span>
711→						</div>
712→					</div>
713→					<div className="flex justify-end gap-3 pt-2">
714→						<button
715→							onClick={() => setShowConfirmModal(false)}
716→							className="btn-secondary"
717→						>
718→							Cancel
719→						</button>
720→						<button
721→							onClick={() => {
722→								setShowConfirmModal(false);
723→								handleExecute();
724→							}}
725→							className="btn-primary"
726→						>
727→							Confirm
728→						</button>
729→					</div>
730→				</div>
731→			</Modal>
732→
733→			{/* Generated Prompts (legacy mode) */}
734→			{prompts && (
735→				<div className="space-y-6">
736→					{prompts.errorFixPrompt && (
737→						<PromptDisplay
738→							title={`Fix Errors Prompt (${prompts.errorCount} issues)`}
739→							content={prompts.errorFixPrompt}
740→						/>
741→					)}
742→					{prompts.suggestionEnrichPrompt && (
743→						<PromptDisplay
744→							title={`Enrich Suggestions Prompt (${prompts.suggestionCount} issues)`}
745→							content={prompts.suggestionEnrichPrompt}
746→						/>
747→					)}
748→				</div>
749→			)}
750→		</div>
751→	);
752→}
753→
754→function IssueSection({
755→	title,
756→	count,
757→	entries,
758→	onRemove,
759→}: {
760→	title: string;
761→	count: number;
762→	entries: { key: string; issue: Issue }[];
763→	onRemove: (key: string) => void;
764→}) {
765→	return (
766→		<div className="bg-slate-800/50 rounded-lg border border-slate-700/50 p-3">
767→			<div className="flex items-center justify-between mb-2">
768→				<span className="text-sm font-semibold text-slate-200">
769→					{title} ({count})
770→				</span>
771→			</div>
772→			<div className="space-y-1 max-h-48 overflow-y-auto custom-scrollbar">
773→				{entries.map(({ key, issue }) => {
774→					const numericSeverity = getIssueSeverity(issue);
775→					const severityLevel = getSeverityLevel(numericSeverity);
776→					const issueTitle =
777→						issue.title || issue.problem || issue.description || issue.category;
778→
779→					return (
780→						<div
781→							key={key}
782→							className="flex items-center gap-2 py-1 px-1 rounded hover:bg-slate-700/30 group"
783→						>
784→							<div
785→								className={`severity-dot severity-dot-${severityLevel} flex-shrink-0`}
786→							/>
787→							<div className="flex-shrink-0 w-[72px]">
788→								{issue.issueType === "error" ? (
789→									<span className={getSeverityColor(issue.severity)}>
790→										{severityLevel.charAt(0).toUpperCase() +
791→											severityLevel.slice(1)}
792→									</span>
793→								) : (
794→									<span className={getImpactBadgeClass(issue.impactLevel)}>
795→										{getImpactLabel(issue.impactLevel)}
796→									</span>
797→								)}
798→							</div>
799→							<span className="flex-1 min-w-0 text-sm text-slate-300 truncate">
800→								{issueTitle}
801→							</span>
802→							{issue.evaluatorName && (
803→								<span className="flex-shrink-0 text-xs text-slate-500 hidden sm:block">
804→									{issue.evaluatorName}
805→								</span>
806→							)}
807→							<button
808→								onClick={() => onRemove(key)}
809→								className="flex-shrink-0 text-slate-500 hover:text-red-400 transition-colors opacity-0 group-hover:opacity-100"
810→								title="Remove from remediation"
811→							>
812→								<svg
813→									className="h-4 w-4"
814→									fill="none"
815→									viewBox="0 0 24 24"
816→									stroke="currentColor"
817→								>
818→									<path
819→										strokeLinecap="round"
820→										strokeLinejoin="round"
821→										strokeWidth={2}
822→										d="M6 18L18 6M6 6l12 12"
823→									/>
824→								</svg>
825→							</button>
826→						</div>
827→					);
828→				})}
829→			</div>
830→		</div>
831→	);
832→}
833→
834→function PromptDisplay({ title, content }: { title: string; content: string }) {
835→	return (
836→		<div className="card">
837→			<div className="flex items-center justify-between mb-3">
838→				<h3 className="text-subheading text-slate-200">{title}</h3>
839→				<CopyButton text={content} variant="text" />
840→			</div>
841→			<div className="bg-slate-900/50 rounded-lg p-4 border border-slate-700/50 font-mono text-xs text-slate-300 max-h-96 overflow-y-auto custom-scrollbar whitespace-pre-wrap">
842→				{content}
843→			</div>
