   318→										);
   319→									})()}
   320→
   321→								{/* File changes */}
   322→								{result.fileChanges.length === 0 ? (
   323→									<p className="text-sm text-slate-500">
   324→										No file changes were made.
   325→									</p>
   326→								) : (
   327→									<div className="space-y-2">
   328→										{result.fileChanges.map((file: FileChange) => {
   329→											const fileSummaries = getFileSummaries(file.path, result);
   330→											return (
   331→												<FileChangeCard
   332→													key={file.path}
   333→													file={file}
   334→													defaultExpanded={result.fileChanges.length <= 3}
   335→													summaries={fileSummaries}
   336→												/>
   337→											);
   338→										})}
   339→									</div>
   340→								)}
   341→							</>
   342→						)
   343→					)}
   344→				</div>
   345→			)}
   346→		</div>
   347→	);
   348→}
   349→
   350→function CompactActionSummary({
   351→	summary,
   352→}: {
   353→	summary: NonNullable<RemediationResult["summary"]>;
   354→}) {
   355→	const allActions = [
   356→		...summary.errorFixActions,
   357→		...summary.suggestionEnrichActions,
   358→	];
   359→
   360→	return (
   361→		<div className="bg-slate-900/30 rounded-lg p-3">
   362→			<div className="flex items-center justify-between mb-2">
   363→				<span className="text-xs font-semibold text-slate-300">
   364→					Action Summary
   365→				</span>
   366→				<span className="text-xs text-slate-500">
   367→					{summary.addressedCount} addressed
   368→					{summary.skippedCount > 0 && `, ${summary.skippedCount} skipped`}
   369→				</span>
   370→			</div>
   371→			<div className="space-y-1">
   372→				{allActions.map((action) => (
   373→					<div
   374→						key={`${action.issueIndex}-${action.status}`}
   375→						className="flex items-start gap-2 text-xs"
   376→					>
   377→						{action.status === "skipped" ? (
   378→							<span className="text-slate-500 mt-0.5">—</span>
   379→						) : (
   380→							<span className="text-green-400 mt-0.5">✓</span>
   381→						)}
   382→						<span className="text-slate-400 flex-1">{action.summary}</span>
   383→						{action.outputType && (
   384→							<span
   385→								className={`inline-flex items-center px-1.5 py-0.5 rounded text-xs font-medium flex-shrink-0 output-type-${action.outputType}`}
   386→							>
   387→								{action.outputType.charAt(0).toUpperCase() +
   388→									action.outputType.slice(1)}
   389→							</span>
   390→						)}
   391→						{action.file && (
   392→							<span className="text-slate-600 font-mono">{action.file}</span>
   393→						)}
   394→					</div>
   395→				))}
   396→			</div>
   397→		</div>
   398→	);
   399→}
   400→
   401→function ScoreComparison({
   402→	before,
   403→	after,
   404→	afterGrade,
   405→}: {
   406→	before: number;
   407→	after: number;
   408→	afterGrade?: string;
   409→}) {
   410→	const delta = after - before;
   411→	const deltaSign = delta > 0 ? "+" : "";
   412→	const deltaColor =
   413→		delta > 0
   414→			? "text-green-400"
   415→			: delta < 0
   416→				? "text-red-400"
   417→				: "text-slate-400";
   418→
   419→	return (
   420→		<div className="flex items-center gap-2 text-xs">
   421→			<span className="text-slate-400">Score:</span>
   422→			<span className="text-slate-300">{before.toFixed(1)}</span>
   423→			<span className="text-slate-500">&rarr;</span>
   424→			<span className="text-slate-200 font-medium">{after.toFixed(1)}</span>
   425→			<span className={`font-medium ${deltaColor}`}>
   426→				({deltaSign}
   427→				{delta.toFixed(1)})
   428→			</span>
   429→			{afterGrade && <span className="text-slate-500">{afterGrade}</span>}
   430→		</div>
   431→	);
   432→}
   433→
   434→function getFileSummaries(
   435→	filePath: string,
   436→	result: RemediationResult,
   437→): RemediationAction[] {
   438→	if (!result.summary?.parsed) return [];
   439→
   440→	const allActions = [
   441→		...result.summary.errorFixActions,
   442→		...result.summary.suggestionEnrichActions,
   443→	];
   444→
   445→	return allActions.filter(
   446→		(a) => a.file && a.status !== "skipped" && filePath.endsWith(a.file),
   447→	);
   448→}
