   349→							{
   350→								timestamp,
   351→								message: `Failed: ${event.data?.error?.message || "Unknown error"}`,
   352→							},
   353→						],
   354→					}));
   355→					setExecuteError(event.data?.error?.message || "Remediation failed");
   356→					setSseUrl(null);
   357→					activeRemediationIdRef.current = null;
   358→					setActiveRemediationId(null);
   359→					setPhase("idle");
   360→					refreshHistory();
   361→					break;
   362→			}
   363→		},
   364→		[onClearAll, refreshHistory],
   365→	);
   366→
   367→	useSSE({
   368→		url: sseUrl,
   369→		onMessage: handleSSEMessage,
   370→	});
   371→
   372→	// Execute remediation
   373→	const handleExecute = useCallback(async () => {
   374→		if (!evaluationId || totalSelected === 0) return;
   375→
   376→		setIsExecuting(true);
   377→		setExecuteError(null);
   378→		setPrompts(null);
   379→
   380→		try {
   381→			const issues = [
   382→				...errorEntries.map((e) => e.issue),
   383→				...suggestionEntries.map((e) => e.issue),
   384→			];
   385→
   386→			const response = await api.executeRemediation(
   387→				evaluationId,
   388→				issues,
   389→				targetAgent,
   390→				selectedProvider,
   391→			);
   392→
   393→			activeRemediationIdRef.current = response.remediationId;
   394→			setActiveRemediationId(response.remediationId);
   395→			setSseUrl(response.sseUrl);
   396→			setProgressState({ status: "queued", logs: [] });
   397→			setPhase("progress");
   398→		} catch (err) {
   399→			const message =
   400→				err instanceof Error ? err.message : "Failed to start remediation";
   401→			setExecuteError(message);
   402→		} finally {
   403→			setIsExecuting(false);
   404→		}
   405→	}, [
   406→		evaluationId,
   407→		totalSelected,
   408→		errorEntries,
   409→		suggestionEntries,
   410→		targetAgent,
   411→		selectedProvider,
   412→		api,
   413→	]);
   414→
   415→	// Generate prompts (legacy copy-paste mode)
   416→	const handleGeneratePrompts = useCallback(async () => {
   417→		if (!evaluationId || totalSelected === 0) return;
   418→
   419→		setIsGenerating(true);
   420→		setGenerateError(null);
   421→
   422→		try {
   423→			const issues = [
   424→				...errorEntries.map((e) => e.issue),
   425→				...suggestionEntries.map((e) => e.issue),
   426→			];
   427→			const result = await api.generateRemediationPrompts(
   428→				evaluationId,
   429→				issues,
   430→				targetAgent,
   431→			);
   432→			setPrompts(result);
   433→		} catch (err) {
   434→			setGenerateError(
   435→				err instanceof Error ? err.message : "Failed to generate prompts",
   436→			);
   437→		} finally {
   438→			setIsGenerating(false);
   439→		}
   440→	}, [
   441→		evaluationId,
   442→		totalSelected,
   443→		errorEntries,
   444→		suggestionEntries,
   445→		targetAgent,
   446→		api,
