   836→function IssueSection({
   837→	title,
   838→	count,
   839→	entries,
   840→	onRemove,
   841→}: {
   842→	title: string;
   843→	count: number;
   844→	entries: { key: string; issue: Issue }[];
   845→	onRemove: (key: string) => void;
   846→}) {
   847→	return (
   848→		<div className="bg-slate-800/50 rounded-lg border border-slate-700/50 p-3">
   849→			<div className="flex items-center justify-between mb-2">
   850→				<span className="text-sm font-semibold text-slate-200">
   851→					{title} ({count})
   852→				</span>
   853→			</div>
   854→			<div className="space-y-1 max-h-48 overflow-y-auto custom-scrollbar">
   855→				{entries.map(({ key, issue }) => {
   856→					const numericSeverity = getIssueSeverity(issue);
   857→					const severityLevel = getSeverityLevel(numericSeverity);
   858→					const issueTitle =
   859→						issue.title || issue.problem || issue.description || issue.category;
   860→
   861→					return (
   862→						<div
   863→							key={key}
   864→							className="flex items-center gap-2 py-1 px-1 rounded hover:bg-slate-700/30 group"
   865→						>
   866→							<div
   867→								className={`severity-dot severity-dot-${severityLevel} flex-shrink-0`}
   868→							/>
   869→							<div className="flex-shrink-0 w-[72px]">
   870→								{issue.issueType === "error" ? (
   871→									<span className={getSeverityColor(issue.severity)}>
   872→										{severityLevel.charAt(0).toUpperCase() +
   873→											severityLevel.slice(1)}
   874→									</span>
   875→								) : (
   876→									<span className={getImpactBadgeClass(issue.impactLevel)}>
   877→										{getImpactLabel(issue.impactLevel)}
   878→									</span>
   879→								)}
   880→							</div>
   881→							<span className="flex-1 min-w-0 text-sm text-slate-300 truncate">
   882→								{issueTitle}
   883→							</span>
   884→							{issue.evaluatorName && (
   885→								<span className="flex-shrink-0 text-xs text-slate-500 hidden sm:block">
   886→									{issue.evaluatorName}
   887→								</span>
   888→							)}
   889→							<button
   890→								onClick={() => onRemove(key)}
   891→								className="flex-shrink-0 text-slate-500 hover:text-red-400 transition-colors opacity-0 group-hover:opacity-100"
   892→								title="Remove from remediation"
   893→							>
   894→								<svg
   895→									className="h-4 w-4"
   896→									fill="none"
   897→									viewBox="0 0 24 24"
   898→									stroke="currentColor"
   899→								>
   900→									<path
   901→										strokeLinecap="round"
   902→										strokeLinejoin="round"
   903→										strokeWidth={2}
   904→										d="M6 18L18 6M6 6l12 12"
   905→									/>
   906→								</svg>
   907→							</button>
   908→						</div>
   909→					);
   910→				})}
   911→			</div>
   912→		</div>
   913→	);
   914→}
