   916→function PromptDisplay({ title, content }: { title: string; content: string }) {
   917→	return (
   918→		<div className="card">
   919→			<div className="flex items-center justify-between mb-3">
   920→				<h3 className="text-subheading text-slate-200">{title}</h3>
