// Aggregate Packmind artifact totals across all remediations
const packmindTotals = useMemo(() => {
	let standards = 0;
	let skills = 0;
	for (const r of remediations) {
		const allActions = [
			...(r.summary?.errorFixActions ?? []),
			...(r.summary?.suggestionEnrichActions ?? []),
		];
