     1→// Context Scorer Module
     2→// Computes an overall quality grade (1-10) for repository AGENTS.md files
     3→// Algorithm: "Base + Setup Bonus - Issue Penalty"
     4→
     5→import { invokeClaudeWithRetry } from "@shared/claude/invoker";
     6→import { DEFAULT_TIMEOUT_MS } from "@shared/constants";
     7→import type {
     8→	ContextScoreGrade,
     9→	IContextScore,
    10→	IContextScoreBreakdown,
    11→	IProjectContext,
    12→	Issue,
    13→} from "@shared/types/evaluation";
    14→import { getIssueTypeFromEvaluatorName } from "./evaluator-types";
    15→
    16→/**
    17→ * Input for computing context score
    18→ */
    19→export interface IContextScorerInput {
    20→	issues: Issue[];
    21→	filesFound: number;
    22→	filesExpected?: number; // Optional: estimate of expected files based on repo structure
    23→	projectContext?: IProjectContext; // Optional: for setup bonuses and LOC normalization
    24→}
    25→
    26→// ============================================================================
    27→// Algorithm Constants
    28→// ============================================================================
    29→
    30→/**
    31→ * Base score - supportive starting point
    32→ */
    33→const BASE_SCORE = 6.0;
    34→
    35→/**
    36→ * Maximum bonus caps for progressive scaling
    37→ * Note: Individual bonuses now use logarithmic scaling instead of flat tiers
    38→ */
    39→const MAX_AGENTS_FILES_BONUS = 2.5; // Increased from 2.0 to reward more files
    40→const MAX_SKILLS_BONUS = 1.0; // Increased from 0.75 to reward more skills
    41→const MAX_LINKED_DOCS_BONUS = 1.0; // Increased from 0.75 to reward more docs
    42→
    43→/**
    44→ * Maximum total setup bonus
    45→ */
    46→const MAX_SETUP_BONUS = 4.5; // Increased from 3.5 to reward extensive documentation
    47→
    48→/**
    49→ * LOC-based issue allowances (free issues before penalties apply)
    50→ */
    51→const LOC_TIERS = {
    52→	small: { maxLOC: 5000, freeIssues: 5 },
    53→	medium: { maxLOC: 25000, freeIssues: 10 },
    54→	large: { maxLOC: 100000, freeIssues: 15 },
    55→	enterprise: { maxLOC: Number.POSITIVE_INFINITY, freeIssues: 20 },
    56→};
    57→
    58→/**
    59→ * Severity weights for issue penalty calculation (3-level system)
    60→ * Updated for 3-level system: High (8-10), Medium (6-7), Low (≤5)
    61→ * High weight reduced from 0.6 to 0.45 to be less punishing
    62→ */
    63→const SEVERITY_WEIGHTS = {
    64→	high: 0.45, // severity 8-10 (merged critical + high) - reduced from 0.6
    65→	medium: 0.15, // severity 6-7
    66→	low: 0.05, // severity ≤5
    67→};
    68→
    69→/**
    70→ * Issue type weights (suggestions weighted much lower than errors)
    71→ */
    72→const ISSUE_TYPE_WEIGHTS = {
    73→	error: 1.0,
    74→	suggestion: 0.2, // Suggestions count at 20%
    75→};
    76→
    77→/**
    78→ * Maximum issue penalty (soft cap)
    79→ */
    80→const MAX_ISSUE_PENALTY = 3.0;
    81→
    82→// ============================================================================
    83→// Helper Functions
    84→// ============================================================================
    85→
    86→/**
    87→ * Parse total lines of code from cloc summary
    88→ * @example "TypeScript: 15,234\nJavaScript: 3,421" -> 18655
    89→ */
    90→export function parseTotalLOC(clocSummary?: string): number {
    91→	if (!clocSummary) return 0;
    92→
    93→	let total = 0;
    94→	const lines = clocSummary.split("\n");
    95→	for (const line of lines) {
    96→		// Match patterns like "TypeScript: 15,234" or "JavaScript: 3421"
    97→		const match = line.match(/:\s*([\d,]+)/);
    98→		if (match?.[1]) {
    99→			// Remove commas and parse as number
   100→			const locValue = Number.parseInt(match[1].replace(/,/g, ""), 10);
   101→			if (!Number.isNaN(locValue)) {
   102→				total += locValue;
   103→			}
   104→		}
   105→	}
   106→	return total;
   107→}
   108→
   109→/**
   110→ * Get project size tier based on total LOC
   111→ */
   112→export function getProjectSizeTier(
   113→	totalLOC: number,
   114→): "small" | "medium" | "large" | "enterprise" {
   115→	if (totalLOC < LOC_TIERS.small.maxLOC) return "small";
   116→	if (totalLOC < LOC_TIERS.medium.maxLOC) return "medium";
   117→	if (totalLOC < LOC_TIERS.large.maxLOC) return "large";
   118→	return "enterprise";
   119→}
   120→
   121→/**
   122→ * Get free issue allowance for a project size tier
   123→ */
   124→function getIssueAllowance(
   125→	tier: "small" | "medium" | "large" | "enterprise",
   126→): number {
   127→	return LOC_TIERS[tier].freeIssues;
   128→}
   129→
   130→/**
   131→ * Calculate bonus for AGENTS.md files using logarithmic scaling
   132→ * Progressive scaling that continues to reward additional files:
   133→ * - 1 file → 1.5
   134→ * - 2 files → ~1.9
   135→ * - 3 files → ~2.13
   136→ * - 5 files → ~2.43
   137→ * - 10 files → 2.5 (max)
   138→ */
   139→export function calculateAgentsFilesBonus(fileCount: number): number {
   140→	if (fileCount <= 0) return 0;
   141→	// Base 1.5 for first file, then +0.4 * log2(fileCount) for additional files
   142→	const bonus = 1.5 + 0.4 * Math.log2(fileCount);
   143→	return Math.min(MAX_AGENTS_FILES_BONUS, Math.round(bonus * 100) / 100);
   144→}
   145→
   146→/**
   147→ * Calculate bonus for skills using logarithmic scaling
   148→ * Progressive scaling that continues to reward additional skills:
   149→ * - 1-3 skills → ~0.2-0.4
   150→ * - 8 skills → ~0.63
   151→ * - 20 skills → ~0.87
   152→ * - 30+ skills → 1.0 (max)
   153→ */
   154→export function calculateSkillsBonus(skillsCount: number): number {
   155→	if (skillsCount <= 0) return 0;
   156→	// 0.2 * log2(1 + skillsCount) for smooth scaling
   157→	const bonus = 0.2 * Math.log2(1 + skillsCount);
   158→	return Math.min(MAX_SKILLS_BONUS, Math.round(bonus * 100) / 100);
   159→}
   160→
   161→/**
   162→ * Calculate bonus for linked docs using logarithmic scaling
   163→ * Progressive scaling that continues to reward additional docs:
   164→ * - 1-2 docs → ~0.2-0.32
   165→ * - 6 docs → ~0.56
   166→ * - 15 docs → ~0.8
   167→ * - 30+ docs → 1.0 (max)
   168→ */
   169→export function calculateLinkedDocsBonus(linkedDocsCount: number): number {
   170→	if (linkedDocsCount <= 0) return 0;
   171→	// 0.2 * log2(1 + linkedDocsCount) for smooth scaling
   172→	const bonus = 0.2 * Math.log2(1 + linkedDocsCount);
   173→	return Math.min(MAX_LINKED_DOCS_BONUS, Math.round(bonus * 100) / 100);
   174→}
   175→
   176→/**
   177→ * Get severity category from numeric severity
   178→ * Updated for 3-level system: High (8-10), Medium (6-7), Low (≤5)
   179→ */
   180→function getSeverityCategory(severity: number): "high" | "medium" | "low" {
   181→	if (severity >= 8) return "high";
   182→	if (severity >= 6) return "medium";
   183→	return "low";
   184→}
   185→
   186→/**
   187→ * Calculate documentation maturity factor based on issues-per-file ratio
   188→ * Reduces penalty for well-documented repos with good coverage
   189→ *
   190→ * - 10 issues across 10 files (ratio 1.0) → 30% penalty reduction (factor 0.7)
   191→ * - 10 issues across 5 files (ratio 2.0) → 15% penalty reduction (factor 0.85)
   192→ * - 10 issues in 1 file (ratio 10.0) → no reduction (factor 1.0)
   193→ */
   194→export function getDocumentationMaturityFactor(issuesPerFile: number): number {
   195→	if (issuesPerFile <= 1) return 0.7; // 30% penalty reduction
   196→	if (issuesPerFile <= 2) return 0.85; // 15% penalty reduction
   197→	return 1.0; // No reduction
   198→}
   199→
   200→/**
   201→ * Get grade label from numeric score
   202→ * New thresholds: 8.5/6.5/4.5/3.0
   203→ */
   204→export function getGradeFromScore(score: number): ContextScoreGrade {
   205→	if (score >= 8.5) return "Excellent";
   206→	if (score >= 6.5) return "Good";
   207→	if (score >= 4.5) return "Fair";
   208→	if (score >= 3.0) return "Developing";
   209→	return "Getting Started";
   210→}
   211→
   212→/**
   213→ * Generate user-friendly one-liner explanation
   214→ */
   215→export function generateExplanation(
   216→	score: number,
   217→	grade: ContextScoreGrade,
   218→	setupBonus: number,
   219→	issuePenalty: number,
   220→	agentsFileCount: number,
   221→): string {
   222→	if (agentsFileCount === 0) {
   223→		return "No AGENTS.md files found. Create one to provide AI agents with essential context.";
   224→	}
   225→
   226→	if (grade === "Excellent") {
   227→		return `Great job! Your ${agentsFileCount} context file${agentsFileCount > 1 ? "s" : ""} provide excellent guidance for AI agents.`;
   228→	}
   229→
   230→	if (grade === "Good") {
   231→		if (issuePenalty > 0.5) {
   232→			return `Good foundation with ${agentsFileCount} context file${agentsFileCount > 1 ? "s" : ""}. Addressing a few issues would improve AI agent effectiveness.`;
   233→		}
   234→		return `Solid context setup. Consider adding more documentation links or skills to enhance AI assistance.`;
   235→	}
   236→
   237→	if (grade === "Fair") {
   238→		if (setupBonus < 2) {
   239→			return `${agentsFileCount} context file${agentsFileCount > 1 ? "s" : ""} provide basic guidance. Adding skills and linked docs would help AI agents more.`;
   240→		}
   241→		return `Context files exist but have issues that may confuse AI agents. Review the recommendations below.`;
   242→	}
   243→
   244→	if (grade === "Developing") {
   245→		return `Your context setup is developing. Focus on fixing high-severity issues first.`;
   246→	}
   247→
   248→	return `Your AI context needs significant improvement. Start by creating or enhancing your AGENTS.md file.`;
   249→}
   250→
   251→// ============================================================================
   252→// Core Scoring Functions
   253→// ============================================================================
   254→
   255→/**
   256→ * Compute context score based on issues, file count, and project context
   257→ *
   258→ * Algorithm: "Base + Setup Bonus - Issue Penalty × Maturity Factor"
   259→ * FinalScore = clamp(1, 10, BaseScore + SetupBonus - IssuePenalty)
   260→ *
   261→ * Where:
   262→ * - BaseScore = 6.0 (supportive starting point)
   263→ * - SetupBonus = 0 to 4.5 (rewards existing investment, progressive scaling)
   264→ * - IssuePenalty = 0 to 3.0 (soft cap with LOC-normalized allowance)
   265→ * - MaturityFactor = 0.7 to 1.0 (reduces penalty for well-covered repos)
   266→ */
   267→export function computeContextScore(
   268→	input: IContextScorerInput,
   269→): IContextScoreBreakdown {
   270→	const { issues, filesFound, projectContext } = input;
   271→
   272→	// =========================================================================
   273→	// 1. Calculate Setup Bonuses
   274→	// =========================================================================
   275→	const agentsFileCount = projectContext?.agentsFilePaths?.length ?? filesFound;
   276→	const skillsCount = projectContext?.skills?.length ?? 0;
   277→	const linkedDocsCount = projectContext?.linkedDocs?.length ?? 0;
   278→
   279→	const agentsFilesBonus = calculateAgentsFilesBonus(agentsFileCount);
   280→	const skillsBonus = calculateSkillsBonus(skillsCount);
   281→	const linkedDocsBonus = calculateLinkedDocsBonus(linkedDocsCount);
   282→
   283→	const totalSetupBonus = Math.min(
   284→		MAX_SETUP_BONUS,
   285→		agentsFilesBonus + skillsBonus + linkedDocsBonus,
   286→	);
   287→
   288→	// =========================================================================
   289→	// 2. Calculate LOC-Normalized Issue Penalty
   290→	// =========================================================================
   291→	const totalLOC = parseTotalLOC(projectContext?.clocSummary);
   292→	const projectSizeTier = getProjectSizeTier(totalLOC);
   293→	const issueAllowance = getIssueAllowance(projectSizeTier);
   294→
   295→	// Count issues by severity and type
   296→	let highCount = 0;
   297→	let mediumCount = 0;
   298→	let lowCount = 0;
   299→	let errorCount = 0;
   300→	let suggestionCount = 0;
   301→
   302→	// Calculate weighted issue count
   303→	let weightedIssueCount = 0;
   304→
   305→	for (const issue of issues) {
   306→		// Determine issue type
   307→		const evaluatorName = "evaluatorName" in issue ? issue.evaluatorName : "";
   308→		const issueType =
   309→			issue.issueType || getIssueTypeFromEvaluatorName(evaluatorName || "");
   310→		const isError = issueType !== "suggestion";
   311→
   312→		// Count by type
   313→		if (isError) {
   314→			errorCount++;
   315→		} else {
   316→			suggestionCount++;
   317→		}
   318→
   319→		// Count by severity (3-level system)
   320→		const severityCat = getSeverityCategory(
   321→			issue.issueType === "error" ? issue.severity : 5, // Map suggestions to low severity
   322→		);
   323→		switch (severityCat) {
   324→			case "high":
   325→				highCount++;
   326→				break;
   327→			case "medium":
   328→				mediumCount++;
   329→				break;
   330→			case "low":
   331→				lowCount++;
   332→				break;
   333→		}
   334→
   335→		// Calculate weighted value
   336→		const severityWeight = SEVERITY_WEIGHTS[severityCat];
   337→		const typeWeight = isError
   338→			? ISSUE_TYPE_WEIGHTS.error
   339→			: ISSUE_TYPE_WEIGHTS.suggestion;
   340→		weightedIssueCount += severityWeight * typeWeight;
   341→	}
   342→
   343→	// Calculate excess issues beyond allowance
   344→	// Use weighted count for fairer comparison
   345→	const excessIssues = Math.max(0, weightedIssueCount - issueAllowance * 0.5);
   346→
   347→	// Calculate penalty with soft cap
   348→	// Use logarithmic scaling for diminishing returns
   349→	const rawPenalty = Math.log2(1 + excessIssues) * 1.2;
   350→
   351→	// =========================================================================
   352→	// 2b. Apply Documentation Maturity Factor
   353→	// Reduces penalty for repos with good file coverage (low issues-per-file ratio)
   354→	// =========================================================================
   355→	const issuesPerFile =
   356→		agentsFileCount > 0 ? issues.length / agentsFileCount : issues.length;
   357→	const documentationMaturityFactor =
   358→		getDocumentationMaturityFactor(issuesPerFile);
   359→	const adjustedPenalty = rawPenalty * documentationMaturityFactor;
   360→	const issuePenalty = Math.min(MAX_ISSUE_PENALTY, adjustedPenalty);
   361→
   362→	// =========================================================================
   363→	// 3. Build Breakdown Object
   364→	// =========================================================================
   365→	const breakdown: IContextScoreBreakdown = {
   366→		baseScore: BASE_SCORE,
   367→		setupBonus: {
   368→			agentsFilesBonus: Math.round(agentsFilesBonus * 100) / 100,
   369→			skillsBonus: Math.round(skillsBonus * 100) / 100,
   370→			linkedDocsBonus: Math.round(linkedDocsBonus * 100) / 100,
   371→			total: Math.round(totalSetupBonus * 100) / 100,
   372→		},
   373→		issuePenalty: {
   374→			weightedIssueCount: Math.round(weightedIssueCount * 100) / 100,
   375→			issueAllowance,
   376→			excessIssues: Math.round(excessIssues * 100) / 100,
   377→			penalty: Math.round(issuePenalty * 100) / 100,
   378→		},
   379→		context: {
   380→			projectSizeTier,
   381→			totalLOC,
   382→			agentsFileCount,
   383→			skillsCount,
   384→			linkedDocsCount,
   385→			highIssues: highCount,
   386→			mediumIssues: mediumCount,
   387→			lowIssues: lowCount,
   388→			errorCount,
   389→			suggestionCount,
   390→			issuesPerFile: Math.round(issuesPerFile * 100) / 100,
   391→			documentationMaturityFactor,
   392→		},
   393→		// Legacy fields for backward compatibility
   394→		coveragePenalty: 0,
   395→		categoryDiversityPenalty: 0,
   396→		totalPenalty: Math.round(issuePenalty * 100) / 100,
   397→	};
   398→
   399→	return breakdown;
   400→}
   401→
   402→/**
   403→ * Calculate final score from breakdown
   404→ * Formula: clamp(1, 10, BaseScore + SetupBonus - IssuePenalty)
   405→ */
   406→export function calculateScore(breakdown: IContextScoreBreakdown): number {
   407→	const rawScore =
   408→		breakdown.baseScore +
   409→		breakdown.setupBonus.total -
   410→		breakdown.issuePenalty.penalty;
   411→
   412→	// Clamp between 1 and 10
   413→	const score = Math.max(1, Math.min(10, rawScore));
   414→
   415→	return Math.round(score * 10) / 10; // Round to 1 decimal
   416→}
   417→
   418→/**
   419→ * Generate AI explanation and recommendations for the score
   420→ */
   421→export async function generateScoreExplanation(
   422→	score: number,
   423→	breakdown: IContextScoreBreakdown,
   424→	issues: Issue[],
   425→	options: { verbose?: boolean; timeout?: number } = {},
   426→): Promise<{ summary: string; recommendations: string[] }> {
   427→	const { verbose = false, timeout } = options;
   428→
   429→	// Build a concise issue summary for the AI (high severity = 8+)
   430→	const errorIssues = issues.filter(
   431→		(i): i is Issue & { issueType: "error"; severity: number } =>
   432→			i.issueType === "error" && i.severity >= 8,
   433→	);
   434→	const topIssues = errorIssues
   435→		.slice(0, 5)
   436→		.map(
   437→			(i) =>
   438→				`- [${getSeverityCategory(i.severity).toUpperCase()}] ${i.category}: ${i.title || i.problem || i.description || "No description"}`,
   439→		)
   440→		.join("\n");
   441→
   442→	const grade = getGradeFromScore(score);
   443→	const noFilesGuidance =
   444→		breakdown.context.agentsFileCount === 0
   445→			? `\nIMPORTANT: No AGENTS.md files exist in this repository. Your FIRST recommendation MUST be to bootstrap/create an AGENTS.md file at the repository root. The other two recommendations should focus on what context gaps to address (e.g., project architecture, coding conventions, testing requirements, technology stack).\n`
   446→			: "";
   447→
   448→	const prompt = `You are analyzing the quality of AGENTS.md files in a repository. Based on the following evaluation data, generate:
   449→1. A 1-2 sentence summary explaining the overall quality
   450→2. Top 3 actionable recommendations for improvement
   451→${noFilesGuidance}
   452→Score: ${score}/10 (${grade})
   453→
   454→Setup Investment:
   455→- AGENTS.md files: ${breakdown.context.agentsFileCount} (+${breakdown.setupBonus.agentsFilesBonus} bonus)
   456→- Skills defined: ${breakdown.context.skillsCount} (+${breakdown.setupBonus.skillsBonus} bonus)
   457→- Linked docs: ${breakdown.context.linkedDocsCount} (+${breakdown.setupBonus.linkedDocsBonus} bonus)
   458→- Total setup bonus: +${breakdown.setupBonus.total}
   459→
   460→Issues Found:
   461→- High (8-10): ${breakdown.context.highIssues}
   462→- Medium (6-7): ${breakdown.context.mediumIssues}
   463→- Low (≤5): ${breakdown.context.lowIssues}
   464→- Errors: ${breakdown.context.errorCount}
   465→- Suggestions: ${breakdown.context.suggestionCount}
   466→- Issue penalty: -${breakdown.issuePenalty.penalty} (${breakdown.context.projectSizeTier} project, ${breakdown.issuePenalty.issueAllowance} issue allowance)
   467→
   468→${topIssues ? `Top Issues:\n${topIssues}` : "No high-severity issues found."}
   469→
   470→Respond in this exact JSON format:
   471→{
   472→  "summary": "Your 1-2 sentence summary here",
   473→  "recommendations": ["First recommendation", "Second recommendation", "Third recommendation"]
   474→}`;
   475→
   476→	try {
   477→		const response = await invokeClaudeWithRetry(prompt, {
   478→			verbose,
   479→			timeout: timeout ?? DEFAULT_TIMEOUT_MS,
   480→		});
   481→
   482→		if (response.result) {
   483→			// Parse JSON from response
   484→			const jsonMatch = response.result.match(/\{[\s\S]*\}/);
   485→			if (jsonMatch) {
   486→				const parsed = JSON.parse(jsonMatch[0]);
   487→				return {
   488→					summary: parsed.summary || getDefaultSummary(score, breakdown),
   489→					recommendations: Array.isArray(parsed.recommendations)
   490→						? parsed.recommendations.slice(0, 3)
   491→						: getDefaultRecommendations(breakdown),
   492→				};
   493→			}
   494→		}
   495→	} catch (error) {
   496→		if (verbose) {
   497→			console.error(
   498→				"[ContextScorer] Failed to generate AI explanation:",
   499→				error,
   500→			);
   501→		}
   502→	}
   503→
   504→	// Fallback to algorithmic summary
   505→	return {
   506→		summary: getDefaultSummary(score, breakdown),
   507→		recommendations: getDefaultRecommendations(breakdown),
   508→	};
   509→}
   510→
   511→/**
   512→ * Default summary when AI fails
   513→ */
   514→function getDefaultSummary(
   515→	score: number,
   516→	breakdown: IContextScoreBreakdown,
   517→): string {
   518→	const grade = getGradeFromScore(score);
   519→	const { highIssues, errorCount, suggestionCount, agentsFileCount } =
   520→		breakdown.context;
   521→
   522→	if (agentsFileCount === 0) {
   523→		return "No AGENTS.md files found. AI agents have no context guidance for this repository.";
   524→	}
   525→
   526→	if (grade === "Excellent") {
   527→		return "Your AGENTS.md files provide excellent context for AI agents with minimal issues.";
   528→	}
   529→	if (grade === "Good") {
   530→		return `Your AGENTS.md files provide good context with ${errorCount} minor errors to address.`;
   531→	}
   532→	if (grade === "Fair") {
   533→		return `Your AGENTS.md files need improvement. Found ${errorCount} errors and ${suggestionCount} suggestions.`;
   534→	}
   535→	if (grade === "Developing") {
   536→		return `Your AGENTS.md files have significant issues. ${highIssues} high-severity problems require attention.`;
   537→	}
   538→	return `Critical issues in AGENTS.md files. ${highIssues} high-severity problems found.`;
   539→}
   540→
   541→/**
   542→ * Default recommendations when AI fails
   543→ */
   544→function getDefaultRecommendations(
   545→	breakdown: IContextScoreBreakdown,
   546→): string[] {
   547→	const recommendations: string[] = [];
   548→	const {
   549→		agentsFileCount,
   550→		highIssues,
   551→		errorCount,
   552→		suggestionCount,
   553→		skillsCount,
   554→		linkedDocsCount,
   555→	} = breakdown.context;
   556→
   557→	// If no files exist, prioritize bootstrapping
   558→	if (agentsFileCount === 0) {
   559→		return [
   560→			"Bootstrap an AGENTS.md file at the repository root to provide essential AI context.",
   561→			"Document key context gaps: project architecture, coding conventions, and testing requirements.",
   562→			"Add technology stack details and critical workflows that AI agents need to understand.",
   563→		];
   564→	}
   565→
   566→	if (highIssues > 0) {
   567→		recommendations.push(
   568→			"Address high-severity issues first - they significantly impact AI agent effectiveness.",
   569→		);
   570→	}
   571→
   572→	if (skillsCount === 0 && recommendations.length < 3) {
   573→		recommendations.push(
   574→			"Add SKILL.md files to define reusable workflows for AI agents.",
   575→		);
   576→	}
   577→
   578→	if (linkedDocsCount === 0 && recommendations.length < 3) {
   579→		recommendations.push(
   580→			"Link relevant documentation from your AGENTS.md to provide deeper context.",
   581→		);
   582→	}
   583→
