   263→			$criticalCount: 0,
   264→			$highCount: metadata.highCount || 0,
   265→			$mediumCount: metadata.mediumCount || 0,
   266→			$curatedCount: metadata.curatedCount || 0,
   267→			$totalCostUsd: metadata.totalCostUsd || 0,
   268→			$totalDurationMs: metadata.totalDurationMs || 0,
   269→			$totalInputTokens: metadata.totalInputTokens || 0,
   270→			$totalOutputTokens: metadata.totalOutputTokens || 0,
   271→			$contextScore: metadata.contextScore?.score ?? null,
   272→			$contextGrade: metadata.contextScore?.grade ?? null,
   273→			$failedEvaluatorCount: metadata.failedEvaluators?.length || 0,
   274→			$gitBranch: null,
   275→			$gitCommitSha: null,
   276→			$isImported: 1,
   277→			$resultJson: JSON.stringify(result),
   278→			$finalPromptsJson: null,
   279→			$errorMessage: null,
   280→			$errorCode: null,
   281→			$createdAt: metadata.generatedAt || now,
   282→			$completedAt: metadata.generatedAt || now,
   283→		});
   284→
   285→		console.log(`[EvaluationRepository] Saved imported evaluation ${id}`);
   286→	}
   287→
   288→	/**
   289→	 * Get evaluation by ID with full result
   290→	 */
   291→	getEvaluationById(id: string): IEvaluationRecord | null {
   292→		const db = getDatabase();
   293→
   294→		const stmt = db.prepare<EvaluationRow, string>(`
   295→      SELECT * FROM evaluations WHERE id = ?
   296→    `);
   297→
   298→		const row = stmt.get(id);
   299→		if (!row) return null;
   300→
   301→		return this.rowToRecord(row);
   302→	}
   303→
   304→	/**
   305→	 * Get recent evaluations (metadata only, no result JSON)
   306→	 */
   307→	getRecentEvaluations(limit?: number): IEvaluationHistoryItem[] {
   308→		const db = getDatabase();
   309→
   310→		if (limit !== undefined) {
   311→			const stmt = db.prepare<EvaluationRow, number>(`
   312→        SELECT
   313→          id, repository_url, evaluation_mode, evaluators_count, status,
   314→          total_files, total_issues, critical_count, high_count, medium_count,
   315→          curated_count, total_cost_usd, total_duration_ms, total_input_tokens, total_output_tokens,
   316→          context_score, context_grade, failed_evaluator_count, is_imported,
   317→          error_message, error_code, created_at, completed_at
   318→        FROM evaluations
   319→        ORDER BY completed_at DESC
   320→        LIMIT ?
   321→      `);
   322→			const rows = stmt.all(limit);
   323→			return rows.map((row) => this.rowToHistoryItem(row));
   324→		}
   325→
   326→		const stmt = db.prepare<EvaluationRow, []>(`
   327→      SELECT
   328→        id, repository_url, evaluation_mode, evaluators_count, status,
   329→        total_files, total_issues, critical_count, high_count, medium_count,
   330→        curated_count, total_cost_usd, total_duration_ms, total_input_tokens, total_output_tokens,
   331→        context_score, context_grade, failed_evaluator_count, is_imported,
   332→        error_message, error_code, created_at, completed_at
   333→      FROM evaluations
   334→      ORDER BY completed_at DESC
   335→    `);
   336→
   337→		const rows = stmt.all();
   338→		return rows.map((row) => this.rowToHistoryItem(row));
   339→	}
   340→
   341→	/**
   342→	 * Delete evaluation by ID
   343→	 */
   344→	deleteEvaluation(id: string): boolean {
   345→		const db = getDatabase();
   346→
   347→		const stmt = db.prepare(`DELETE FROM evaluations WHERE id = ?`);
   348→		const result = stmt.run(id);
   349→
   350→		const deleted = result.changes > 0;
   351→		if (deleted) {
   352→			console.log(`[EvaluationRepository] Deleted evaluation ${id}`);
   353→		}
   354→
   355→		return deleted;
   356→	}
   357→
   358→	/**
   359→	 * Delete all evaluations
   360→	 */
   361→	deleteAllEvaluations(): number {
   362→		const db = getDatabase();
   363→
   364→		const stmt = db.prepare(`DELETE FROM evaluations`);
   365→		const result = stmt.run();
   366→
   367→		const deletedCount = result.changes;
   368→		console.log(
   369→			`[EvaluationRepository] Deleted all evaluations (${deletedCount} records)`,
   370→		);
   371→
   372→		return deletedCount;
   373→	}
   374→
   375→	/**
   376→	 * Get all completed evaluations with their full result JSON.
   377→	 * Used by the aggregated issues endpoint to extract issues across evaluations.
   378→	 */
   379→	getAllCompletedEvaluationsWithResults(): Array<{
   380→		id: string;
   381→		repositoryUrl: string;
   382→		completedAt: string;
   383→		result: EvaluationOutput;
   384→	}> {
   385→		const db = getDatabase();
   386→
   387→		const rows = db
   388→			.prepare<
   389→				Pick<
   390→					EvaluationRow,
   391→					"id" | "repository_url" | "completed_at" | "result_json"
   392→				>,
   393→				[]
   394→			>(
   395→				`SELECT id, repository_url, completed_at, result_json
   396→			FROM evaluations
   397→			WHERE status = 'completed' AND result_json IS NOT NULL
   398→			ORDER BY completed_at DESC`,
   399→			)
   400→			.all();
   401→
   402→		return rows
   403→			.map((row) => {
   404→				try {
   405→					return {
   406→						id: row.id,
   407→						repositoryUrl: row.repository_url,
   408→						completedAt: row.completed_at,
   409→						result: JSON.parse(row.result_json!) as EvaluationOutput,
   410→					};
   411→				} catch {
   412→					return null;
   413→				}
   414→			})
   415→			.filter((r): r is NonNullable<typeof r> => r !== null);
   416→	}
   417→
   418→	/**
   419→	 * Get evaluation count
   420→	 */
   421→	getCount(): number {
   422→		const db = getDatabase();
   423→		const stmt = db.prepare<{ count: number }, []>(
   424→			`SELECT COUNT(*) as count FROM evaluations`,
   425→		);
   426→		const result = stmt.get();
   427→		return result?.count || 0;
   428→	}
   429→
   430→	/**
   431→	 * Get top repositories by total cost (summed across all evaluations)
   432→	 */
   433→	getTopReposByCost(limit = 10): IRepoCostStat[] {
   434→		const db = getDatabase();
   435→
   436→		// Get total cost per repo and the latest result_json for LOC extraction
   437→		const rows = db
   438→			.prepare<
   439→				{
   440→					repository_url: string;
   441→					total_cost: number;
   442→					latest_result_json: string | null;
   443→				},
   444→				number
   445→			>(
   446→				`SELECT
   447→					e.repository_url,
   448→					SUM(e.total_cost_usd) as total_cost,
   449→					(SELECT e2.result_json FROM evaluations e2
   450→					 WHERE e2.repository_url = e.repository_url
   451→					   AND e2.status = 'completed' AND e2.result_json IS NOT NULL
   452→					 ORDER BY e2.completed_at DESC LIMIT 1) as latest_result_json
   453→				FROM evaluations e
   454→				WHERE e.status = 'completed'
   455→				GROUP BY e.repository_url
   456→				HAVING total_cost > 0
   457→				ORDER BY total_cost DESC
   458→				LIMIT ?`,
   459→			)
   460→			.all(limit);
   461→
   462→		return rows.map((row) => {
   463→			let totalLOC: number | null = null;
   464→			if (row.latest_result_json) {
   465→				try {
   466→					const result = JSON.parse(row.latest_result_json);
   467→					totalLOC =
   468→						result?.metadata?.contextScore?.breakdown?.context?.totalLOC ??
   469→						null;
   470→				} catch {
   471→					// ignore parse errors
   472→				}
   473→			}
   474→			return {
   475→				repositoryUrl: row.repository_url,
   476→				totalCostUsd: row.total_cost,
   477→				totalLOC,
   478→			};
   479→		});
   480→	}
   481→
   482→	/**
   483→	 * Get total cost aggregated by AI agent
   484→	 */
   485→	getCostByAgent(): IAgentCostStat[] {
   486→		const db = getDatabase();
   487→
   488→		const rows = db
   489→			.prepare<{ total_cost_usd: number; result_json: string | null }, []>(
   490→				`SELECT total_cost_usd, result_json
   491→				FROM evaluations
   492→				WHERE status = 'completed' AND total_cost_usd > 0`,
   493→			)
   494→			.all();
   495→
   496→		const costMap = new Map<string, number>();
   497→		for (const row of rows) {
   498→			let agent = "unknown";
   499→			if (row.result_json) {
   500→				try {
   501→					const result = JSON.parse(row.result_json);
   502→					agent = result?.metadata?.agent || "unknown";
   503→				} catch {
   504→					// ignore parse errors
   505→				}
   506→			}
   507→			costMap.set(agent, (costMap.get(agent) || 0) + row.total_cost_usd);
   508→		}
   509→
   510→		return Array.from(costMap.entries())
   511→			.map(([agent, totalCostUsd]) => ({ agent, totalCostUsd }))
   512→			.sort((a, b) => b.totalCostUsd - a.totalCostUsd);
   513→	}
   514→
   515→	/**
   516→	 * Convert database row to history item (without result JSON)
   517→	 */
   518→	private rowToHistoryItem(row: EvaluationRow): IEvaluationHistoryItem {
   519→		return {
   520→			id: row.id,
   521→			repositoryUrl: row.repository_url,
   522→			evaluationMode: row.evaluation_mode as "unified" | "independent" | null,
   523→			evaluatorsCount: row.evaluators_count,
   524→			status: row.status as "completed" | "failed",
   525→			totalFiles: row.total_files,
   526→			totalIssues: row.total_issues,
   527→			// Merge critical into high for backward compatibility with old DB records
   528→			highCount: row.critical_count + row.high_count,
   529→			mediumCount: row.medium_count,
   530→			lowCount: 0, // Not stored in old DB schema, will be 0 for legacy records
   531→			curatedCount: row.curated_count,
   532→			totalCostUsd: row.total_cost_usd,
   533→			totalDurationMs: row.total_duration_ms,
   534→			contextScore: row.context_score ?? undefined,
   535→			contextGrade: (row.context_grade as ContextScoreGrade) ?? undefined,
   536→			failedEvaluatorCount: row.failed_evaluator_count ?? 0,
   537→			gitBranch: row.git_branch || undefined,
   538→			gitCommitSha: row.git_commit_sha || undefined,
   539→			isImported: row.is_imported === 1 ? true : undefined,
   540→			parentEvaluationId: row.parent_evaluation_id || undefined,
   541→			sourceRemediationId: row.source_remediation_id || undefined,
   542→			errorMessage: row.error_message || undefined,
   543→			errorCode: row.error_code || undefined,
   544→			createdAt: row.created_at,
   545→			completedAt: row.completed_at,
   546→		};
   547→	}
   548→
   549→	/**
   550→	 * Convert database row to full record (with result JSON)
   551→	 */
   552→	private rowToRecord(row: EvaluationRow): IEvaluationRecord {
   553→		const historyItem = this.rowToHistoryItem(row);
   554→
   555→		return {
   556→			...historyItem,
   557→			result: row.result_json ? JSON.parse(row.result_json) : undefined,
   558→			finalPrompts: row.final_prompts_json
   559→				? JSON.parse(row.final_prompts_json)
   560→				: undefined,
   561→		};
   562→	}
   563→}
   564→
   565→// Singleton instance
   566→export const evaluationRepository = new EvaluationRepository();
