   127→	getBatchStatus: (batchId: string) => Promise<IBatchStatusResponse>;
   128→	getJobStatus: (jobId: string) => Promise<IJobStatusResponse>;
   129→	cancelJob: (jobId: string) => Promise<void>;
   130→	generateRemediationPrompts: (
