   150→	deleteRemediation: (remediationId: string) => Promise<void>;
   151→	downloadPatch: (remediationId: string) => Promise<void>;
   152→	importReport: (reportJson: unknown) => Promise<ImportReportResponse>;
   153→	evaluateRemediationImpact: (
