	const getBatchStatus = useCallback(
		async (batchId: string): Promise<IBatchStatusResponse> => {
			try {
				const response = await fetch(`/api/evaluate/batch/${batchId}`, {
					method: "GET",
					headers: { "Content-Type": "application/json" },
