import { describe, expect, test } from "bun:test";
import {
	generateRemediationPrompts,
	type RemediationInput,
	type RemediationIssue,
} from "./prompt-generator";

function makeError(
	overrides: Partial<RemediationIssue> = {},
): RemediationIssue {
	return {
		evaluatorName: "content-quality",
		category: "Content Quality",
		problem: "Contains marketing language",
		severity: 8,
		location: { file: "AGENTS.md", start: 10, end: 15 },
		snippet: "Welcome to our amazing project!",
		fix: "Replace with technical instructions",
		...overrides,
	};
}

function makeSuggestion(
	overrides: Partial<RemediationIssue> = {},
): RemediationIssue {
	return {
		evaluatorName: "context-gaps",
		category: "Context Gaps",
		description: "Missing testing framework documentation",
		impactLevel: "High",
		location: { file: "AGENTS.md", start: 0, end: 0 },
		recommendation: "Document Jest testing patterns",
		...overrides,
	};
}

const baseInput: RemediationInput = {
	targetAgent: "agents-md",
	contextFilePaths: ["AGENTS.md", "packages/api/AGENTS.md"],
	errors: [],
	suggestions: [],
	technicalInventorySection:
		"**Dependencies:** react, express\n**Scripts:** test, build",
	projectSummary: {
		languages: "TypeScript",
		frameworks: "React, Express",
		architecture: "Monorepo",
	},
};

describe("prompt-generator", () => {
	describe("generateRemediationPrompts", () => {
		test("returns empty prompts when no issues", () => {
			const result = generateRemediationPrompts(baseInput);

			expect(result.errorFixPrompt).toBe("");
			expect(result.suggestionEnrichPrompt).toBe("");
			expect(result.errorCount).toBe(0);
			expect(result.suggestionCount).toBe(0);
		});

		test("generates error fix prompt with correct structure", () => {
			const input: RemediationInput = {
				...baseInput,
				errors: [
					makeError({ severity: 9 }),
					makeError({ severity: 7, category: "Security" }),
				],
			};

			const result = generateRemediationPrompts(input);

			expect(result.errorCount).toBe(2);
			expect(result.errorFixPrompt).toContain("# Fix Documentation Issues");
			expect(result.errorFixPrompt).toContain("AGENTS.md");
			expect(result.errorFixPrompt).toContain("packages/api/AGENTS.md");
			expect(result.errorFixPrompt).toContain("Dependencies:** react, express");
			expect(result.errorFixPrompt).toContain("Languages: TypeScript");
			expect(result.errorFixPrompt).toContain("Issues to Fix (2)");
		});

		test("error fix prompt is target-agnostic", () => {
			const input: RemediationInput = {
				...baseInput,
				errors: [makeError()],
			};

			const result = generateRemediationPrompts(input);

			expect(result.errorFixPrompt).toContain(
				"AI agent documentation files listed below",
			);
			expect(result.errorFixPrompt).not.toContain("AGENTS.md files");
			expect(result.errorFixPrompt).not.toContain("CLAUDE.md files");
		});

		test("sorts errors by severity descending", () => {
			const input: RemediationInput = {
				...baseInput,
				errors: [
					makeError({ severity: 6, category: "Low" }),
					makeError({ severity: 10, category: "Critical" }),
					makeError({ severity: 8, category: "High" }),
				],
			};

			const result = generateRemediationPrompts(input);
			const criticalIdx = result.errorFixPrompt.indexOf("Critical");
			const highIdx = result.errorFixPrompt.indexOf("High");
			const lowIdx = result.errorFixPrompt.indexOf("Low");

			expect(criticalIdx).toBeLessThan(highIdx);
			expect(highIdx).toBeLessThan(lowIdx);
		});

		test("generates suggestion enrich prompt with correct structure", () => {
			const input: RemediationInput = {
				...baseInput,
				suggestions: [makeSuggestion()],
			};

			const result = generateRemediationPrompts(input);

			expect(result.suggestionCount).toBe(1);
			expect(result.suggestionEnrichPrompt).toContain("# Enrich Documentation");
			expect(result.suggestionEnrichPrompt).toContain("Documentation Gaps (1)");
			expect(result.suggestionEnrichPrompt).toContain("Impact**: High");
		});

		test("sorts suggestions by impact level", () => {
			const input: RemediationInput = {
				...baseInput,
				suggestions: [
					makeSuggestion({ impactLevel: "Low", category: "Low" }),
					makeSuggestion({ impactLevel: "High", category: "High" }),
					makeSuggestion({ impactLevel: "Medium", category: "Medium" }),
				],
			};

			const result = generateRemediationPrompts(input);
			const highIdx =
				result.suggestionEnrichPrompt.indexOf("context-gaps: High");
			const medIdx = result.suggestionEnrichPrompt.indexOf(
				"context-gaps: Medium",
			);
			const lowIdx = result.suggestionEnrichPrompt.indexOf("context-gaps: Low");

			expect(highIdx).toBeLessThan(medIdx);
			expect(medIdx).toBeLessThan(lowIdx);
		});

		test("includes snippet in error prompt", () => {
			const input: RemediationInput = {
				...baseInput,
				errors: [
					makeError({
						snippet: "Some problematic content\nwith multiple lines",
					}),
				],
			};

			const result = generateRemediationPrompts(input);

			expect(result.errorFixPrompt).toContain("> Some problematic content");
			expect(result.errorFixPrompt).toContain("> with multiple lines");
		});

		test("handles missing optional fields", () => {
			const input: RemediationInput = {
				...baseInput,
				technicalInventorySection: "",
				projectSummary: {},
				errors: [
					{
						evaluatorName: "test",
						category: "Test",
						location: { start: 0, end: 0 },
					},
				],
			};

			const result = generateRemediationPrompts(input);

			expect(result.errorFixPrompt).toContain("Not identified");
			expect(result.errorFixPrompt).not.toContain("## Technical Context");
		});

		test("includes relevance-checking instruction in both prompts", () => {
			const input: RemediationInput = {
				...baseInput,
				errors: [makeError()],
				suggestions: [makeSuggestion()],
			};

			const result = generateRemediationPrompts(input);

			expect(result.errorFixPrompt).toContain("false positives");
			expect(result.errorFixPrompt).toContain("own judgment");
			expect(result.suggestionEnrichPrompt).toContain("false positives");
			expect(result.suggestionEnrichPrompt).toContain("own judgment");
		});

		test("generates both prompts simultaneously", () => {
			const input: RemediationInput = {
				...baseInput,
				errors: [makeError()],
				suggestions: [makeSuggestion()],
			};

			const result = generateRemediationPrompts(input);

			expect(result.errorCount).toBe(1);
			expect(result.suggestionCount).toBe(1);
			expect(result.errorFixPrompt).toContain("# Fix Documentation Issues");
			expect(result.suggestionEnrichPrompt).toContain("# Enrich Documentation");
		});

		test("phantom file suggestion shows create action instead of file lines", () => {
			const input: RemediationInput = {
				...baseInput,
				suggestions: [
					makeSuggestion({
						isPhantomFile: true,
						location: {
							file: "packages/frontend/AGENTS.md",
							start: 1,
							end: 1,
						},
					}),
				],
			};

			const result = generateRemediationPrompts(input);

			expect(result.suggestionEnrichPrompt).toContain(
				"**Action**: Create new file at `packages/frontend/AGENTS.md`",
			);
			expect(result.suggestionEnrichPrompt).not.toContain("lines 1-1");
		});

		test("regular suggestion shows file and lines", () => {
			const input: RemediationInput = {
				...baseInput,
				suggestions: [
					makeSuggestion({
						location: { file: "AGENTS.md", start: 5, end: 20 },
					}),
				],
			};

			const result = generateRemediationPrompts(input);

			expect(result.suggestionEnrichPrompt).toContain(
				"**File**: AGENTS.md, lines 5-20",
			);
			expect(result.suggestionEnrichPrompt).not.toContain(
				"**Action**: Create new file at",
			);
		});

		test("mixed phantom and regular suggestions format correctly", () => {
			const input: RemediationInput = {
				...baseInput,
				suggestions: [
					makeSuggestion({
						impactLevel: "High",
						category: "Regular Gap",
						location: { file: "AGENTS.md", start: 10, end: 25 },
					}),
					makeSuggestion({
						impactLevel: "Medium",
						category: "Phantom Gap",
						isPhantomFile: true,
						location: {
							file: "packages/api/AGENTS.md",
							start: 1,
							end: 1,
						},
					}),
				],
			};

			const result = generateRemediationPrompts(input);

			expect(result.suggestionEnrichPrompt).toContain(
				"**File**: AGENTS.md, lines 10-25",
			);
			expect(result.suggestionEnrichPrompt).toContain(
				"**Action**: Create new file at `packages/api/AGENTS.md`",
			);
		});
	});

	describe("suggestion prompt - target agent routing", () => {
		test("agents-md target includes AGENTS.md standard instructions", () => {
			const input: RemediationInput = {
				...baseInput,
				targetAgent: "agents-md",
				suggestions: [makeSuggestion()],
			};

			const result = generateRemediationPrompts(input);

			expect(result.suggestionEnrichPrompt).toContain(
				"AGENTS.md is a universal",
			);
			expect(result.suggestionEnrichPrompt).toContain(
				"### Standard (AGENTS.md)",
			);
			expect(result.suggestionEnrichPrompt).toContain(
				"Append a section specific to the coding standards",
			);
			expect(result.suggestionEnrichPrompt).toContain(
				".agents/skills/<skill-name>/",
			);
		});

		test("claude-code target includes .claude/rules/ path instructions", () => {
			const input: RemediationInput = {
				...baseInput,
				targetAgent: "claude-code",
				suggestions: [makeSuggestion()],
			};

			const result = generateRemediationPrompts(input);

			expect(result.suggestionEnrichPrompt).toContain(
				"Claude Code uses CLAUDE.md",
			);
			expect(result.suggestionEnrichPrompt).toContain(
				"### Standard (Claude Code Rule)",
			);
			expect(result.suggestionEnrichPrompt).toContain(
				".claude/rules/<standard-slug>.md",
			);
			expect(result.suggestionEnrichPrompt).toContain("paths:");
			expect(result.suggestionEnrichPrompt).toContain("alwaysApply: true");
			expect(result.suggestionEnrichPrompt).toContain(
				".claude/skills/<skill-name>/",
			);
		});

		test("github-copilot target includes .github/instructions/ for standards and .github/skills/ for skills", () => {
			const input: RemediationInput = {
				...baseInput,
				targetAgent: "github-copilot",
				suggestions: [makeSuggestion()],
			};

			const result = generateRemediationPrompts(input);

			expect(result.suggestionEnrichPrompt).toContain(
				"GitHub Copilot uses `.github/copilot-instructions.md`",
			);
			expect(result.suggestionEnrichPrompt).toContain(
				"### Standard (GitHub Copilot Instruction)",
			);
			expect(result.suggestionEnrichPrompt).toContain(
				".github/instructions/<standard-slug>.md",
			);
			expect(result.suggestionEnrichPrompt).toContain(
				".github/skills/<skill-name>/",
			);
		});

		test("suggestion prompt includes decision criteria for all targets", () => {
			for (const target of [
				"agents-md",
				"claude-code",
				"github-copilot",
			] as const) {
				const input: RemediationInput = {
					...baseInput,
					targetAgent: target,
					suggestions: [makeSuggestion()],
				};

				const result = generateRemediationPrompts(input);

				expect(result.suggestionEnrichPrompt).toContain(
					"### Decision Criteria",
				);
				expect(result.suggestionEnrichPrompt).toContain(
					"Must the agent always know this?",
				);
				expect(result.suggestionEnrichPrompt).toContain(
					"Is it a constraint or a capability?",
				);
				expect(result.suggestionEnrichPrompt).toContain(
					"Does it need bundled resources?",
				);
				expect(result.suggestionEnrichPrompt).toContain(
					"Is it short and declarative?",
				);
			}
		});

		test("phantom file remapping instruction is present", () => {
			const input: RemediationInput = {
				...baseInput,
				suggestions: [makeSuggestion()],
			};

			const result = generateRemediationPrompts(input);

			expect(result.suggestionEnrichPrompt).toContain(
				"## Phantom File Remapping",
			);
			expect(result.suggestionEnrichPrompt).toContain(
				"Ignore evaluator-suggested paths",
			);
		});

		test("suggestion prompt includes outputType in JSON example", () => {
			const input: RemediationInput = {
				...baseInput,
				suggestions: [makeSuggestion()],
			};

			const result = generateRemediationPrompts(input);

			expect(result.suggestionEnrichPrompt).toContain('"outputType"');
			expect(result.suggestionEnrichPrompt).toContain('"standard"');
			expect(result.suggestionEnrichPrompt).toContain('"generic"');
		});

		test("generic update references correct file per target agent", () => {
			const expectations: Record<string, string> = {
				"agents-md": "AGENTS.md",
				"claude-code": "CLAUDE.md",
				"github-copilot": ".github/copilot-instructions.md",
			};

			for (const [target, expectedFile] of Object.entries(expectations)) {
				const input: RemediationInput = {
					...baseInput,
					targetAgent: target as "agents-md" | "claude-code" | "github-copilot",
					suggestions: [makeSuggestion()],
				};

				const result = generateRemediationPrompts(input);

				expect(result.suggestionEnrichPrompt).toContain(`### Generic Update`);
				expect(result.suggestionEnrichPrompt).toContain(`\`${expectedFile}\``);
			}
		});
	});
});

/**
 * Remediation prompt generator.
 *
 * Generates copy-paste-ready AI agent prompts from evaluation results:
 * - Error fix prompt: instructs an agent to fix documentation issues
 * - Suggestion enrich prompt: instructs an agent to add missing documentation
 *   with structured output routing (standard, skill, or generic update)
 */

import type { TargetAgent } from "@shared/types/remediation";

export interface RemediationIssue {
	evaluatorName: string;
	category: string;
	title?: string;
	problem?: string;
	description?: string;
	severity?: number;
	impactLevel?: string;
	location: { file?: string; start: number; end: number };
	snippet?: string;
	fix?: string;
	recommendation?: string;
	isPhantomFile?: boolean;
}

export interface RemediationInput {
	targetAgent: TargetAgent;
	contextFilePaths: string[];
	errors: RemediationIssue[];
	suggestions: RemediationIssue[];
	technicalInventorySection: string;
	projectSummary: {
		languages?: string;
		frameworks?: string;
		architecture?: string;
	};
}

export interface RemediationPrompts {
	errorFixPrompt: string;
	suggestionEnrichPrompt: string;
	errorCount: number;
	suggestionCount: number;
}

function formatIssueDescription(issue: RemediationIssue): string {
	return issue.problem || issue.description || issue.title || "No description";
}

function formatIssueFix(issue: RemediationIssue): string {
	return issue.fix || issue.recommendation || "Review and fix this issue";
}

function formatProjectLine(
	summary: RemediationInput["projectSummary"],
): string {
	const parts: string[] = [];
	if (summary.languages) parts.push(`Languages: ${summary.languages}`);
	if (summary.frameworks) parts.push(`Frameworks: ${summary.frameworks}`);
	if (summary.architecture) parts.push(`Architecture: ${summary.architecture}`);
	return parts.length > 0 ? parts.join(" | ") : "Not identified";
}

function formatErrorIssueBlock(issue: RemediationIssue, index: number): string {
	const lines: string[] = [];
	lines.push(`### ${index + 1}. ${issue.evaluatorName}: ${issue.category}`);
	lines.push(`**Severity**: ${issue.severity ?? "N/A"}/10`);

	if (issue.location.file) {
		lines.push(
			`**File**: ${issue.location.file}, lines ${issue.location.start}-${issue.location.end}`,
		);
	}

	lines.push(`**Problem**: ${formatIssueDescription(issue)}`);

	if (issue.snippet) {
		lines.push("**Current content**:");
		lines.push(`> ${issue.snippet.split("\n").join("\n> ")}`);
	}

	lines.push(`**Fix**: ${formatIssueFix(issue)}`);

	return lines.join("\n");
}

function formatSuggestionIssueBlock(
	issue: RemediationIssue,
	index: number,
): string {
	const lines: string[] = [];
	lines.push(`### ${index + 1}. ${issue.evaluatorName}: ${issue.category}`);
	lines.push(`**Impact**: ${issue.impactLevel ?? "N/A"}`);

	if (issue.isPhantomFile && issue.location.file) {
		lines.push(`**Action**: Create new file at \`${issue.location.file}\``);
	} else if (issue.location.file) {
		lines.push(
			`**File**: ${issue.location.file}, lines ${issue.location.start}-${issue.location.end}`,
		);
	}

	lines.push(`**Gap**: ${formatIssueDescription(issue)}`);

	if (issue.snippet) {
		lines.push("**Current content**:");
		lines.push(`> ${issue.snippet.split("\n").join("\n> ")}`);
	}

	lines.push(`**Recommendation**: ${formatIssueFix(issue)}`);

	return lines.join("\n");
}

/** Short context description for each target agent. */
function getAgentContextDescription(targetAgent: TargetAgent): string {
	switch (targetAgent) {
		case "agents-md":
			return "AGENTS.md is a universal AI agent documentation file. Standards are added as inline sections. Skills are placed in `.agents/skills/<skill-name>/`.";
		case "claude-code":
			return "Claude Code uses CLAUDE.md as its main documentation file. Standards are stored as rule files in `.claude/rules/<standard-slug>.md` with YAML frontmatter. Skills are placed in `.claude/skills/<skill-name>/`.";
		case "github-copilot":
			return "GitHub Copilot uses `.github/copilot-instructions.md` as its main documentation file. Standards are stored as instruction files in `.github/instructions/<standard-slug>.md` with YAML frontmatter. Skills are placed in `.github/skills/<skill-name>/`.";
	}
}

/** Returns the generic update file path for a given target agent. */
function getGenericUpdateFile(targetAgent: TargetAgent): string {
	switch (targetAgent) {
		case "agents-md":
			return "AGENTS.md";
		case "claude-code":
			return "CLAUDE.md";
		case "github-copilot":
			return ".github/copilot-instructions.md";
	}
}

/** Per-agent output type instructions for standards, skills, and generic updates. */
function getOutputTypeInstructions(targetAgent: TargetAgent): string {
	const genericFile = getGenericUpdateFile(targetAgent);

	const standardInstructions = getStandardInstructions(targetAgent);
	const skillInstructions = getSkillInstructions(targetAgent);

	return `### Output Types

For each suggestion, decide on ONE output type based on the decision criteria below.

**Standard** — A short, declarative rule file always loaded into context. Defines constraints and conventions the agent must follow at all times.

**Skill** — A folder with a SKILL.md file loaded on-demand via progressive disclosure. Provides procedural knowledge activated when a task matches its description.

**Generic Update** — A direct addition or edit to \`${genericFile}\`. Use for content that doesn't fit standards or skills (e.g., project structure, setup steps, architecture notes).

### Decision Criteria

1. **Must the agent always know this?** If yes -> standard. If only relevant during a specific task -> skill.
2. **Is it a constraint or a capability?** Constraints and guardrails -> standard. Capabilities and workflows -> skill.
3. **Is it short and declarative?** Bullet-point rules -> standard. Procedural paragraphs with sequenced steps -> skill.

When in doubt, prefer a standard. A single suggestion can produce both a standard (rules) and a skill (procedures).

${standardInstructions}

${skillInstructions}

### Generic Update

Add or edit content directly in \`${genericFile}\`. Preserve existing content. Add new sections at the most relevant location.`;
}

function getStandardInstructions(targetAgent: TargetAgent): string {
	switch (targetAgent) {
		case "agents-md":
			return `### Standard (AGENTS.md)

Append a section specific to the coding standards and guidelines in AGENTS.md, at the location where it is most relevant:

\`\`\`md
## Standard: <Standard Name>

<One-sentence summary describing the purpose and rationale of these rules.>

- <Rule 1, starting with a verb>
- <Rule 2, starting with a verb>
\`\`\``;

		case "claude-code":
			return `### Standard (Claude Code Rule)

Create a rule file at \`.claude/rules/<standard-slug>.md\`.

Choose one of two scoping modes in the YAML frontmatter:
- **Path-scoped** (\`paths: [...]\`): Rule applies only when working with files matching the glob patterns. Prefer this when the suggestions target specific file types or directories.
- **Always apply** (\`alwaysApply: true\`): Rule is loaded unconditionally. Use for universal constraints.

Supported glob patterns: \`**/*.ts\`, \`src/**/*\`, \`src/**/*.{ts,tsx}\`, \`{src,lib}/**/*.ts\`.

Path-scoped example:
\`\`\`md
---
paths:
  - "<glob pattern, e.g. src/**/*.ts>"
---

## Standard: <Standard Name>

<One-sentence summary describing the purpose and rationale of these rules.>

- <Rule 1, starting with a verb>
- <Rule 2, starting with a verb>
\`\`\`

Always-apply example:
\`\`\`md
---
alwaysApply: true
---

## Standard: <Standard Name>

<One-sentence summary describing the purpose and rationale of these rules.>

- <Rule 1, starting with a verb>
- <Rule 2, starting with a verb>
\`\`\``;

		case "github-copilot":
			return `### Standard (GitHub Copilot Instruction)

Create an instruction file at \`.github/instructions/<standard-slug>.md\`:

\`\`\`md
---
applyTo: '<glob pattern, e.g. **/*.ts>'
---

## Standard: <Standard Name>

<One-sentence summary describing the purpose and rationale of these rules.>

- <Rule 1, starting with a verb>
- <Rule 2, starting with a verb>
\`\`\``;
	}
}

function getSkillInstructions(targetAgent: TargetAgent): string {
	const skillDirMap: Record<TargetAgent, string> = {
		"agents-md": ".agents/skills",
		"claude-code": ".claude/skills",
		"github-copilot": ".github/skills",
	};
	const skillDir = skillDirMap[targetAgent];

	return `### Skill

Create a \`SKILL.md\` file at \`${skillDir}/<skill-name>/SKILL.md\`:

\`\`\`md
---
name: <Skill Name>
description: <When the agent should activate this skill. Be specific. Use third-person voice.>
---

## Purpose

<One or two sentences explaining what the skill does and why it exists.>

## When to Use

<Describe the triggers: what task, file type, or user request should activate this skill.>

## Instructions

<Step-by-step procedural instructions in imperative/infinitive form (verb-first, not second person). Keep concise, under 5k words.>
\`\`\``;
}

function buildErrorFixPrompt(input: RemediationInput): string {
	if (input.errors.length === 0) {
		return "";
	}

	// Sort by severity descending
	const sorted = [...input.errors].sort(
		(a, b) => (b.severity ?? 0) - (a.severity ?? 0),
	);

	const contextFilesList = input.contextFilePaths
		.map((p) => `- ${p}`)
		.join("\n");
	const issueBlocks = sorted
		.map((issue, i) => formatErrorIssueBlock(issue, i))
		.join("\n\n");

	return `# Fix Documentation Issues

## Role
You are fixing documentation quality issues in the AI agent documentation files listed below.
These files guide AI coding agents. Fixes must be precise and concise.

## Context Files
${contextFilesList}

${input.technicalInventorySection ? `## Technical Context\n${input.technicalInventorySection}\n` : ""}## Project
${formatProjectLine(input.projectSummary)}

## Issues to Fix (${input.errors.length})

${issueBlocks}

## Instructions
1. Read the target files from disk before making changes
2. Use your own judgment to assess each issue: these were produced by an automated evaluator and some may be false positives or irrelevant given the actual file content. Skip any issue that is not a real problem after reading the file
3. Fix each remaining issue, highest severity first
4. Preserve all correct existing content
5. Keep changes minimal — only fix what's genuinely flagged
6. Do not add commentary, headers, or sections beyond what's needed
7. After making all changes, output a JSON summary:
\`\`\`json
{
  "actions": [
    { "issueIndex": 1, "status": "fixed", "file": "AGENTS.md", "summary": "Replaced vague setup instructions with exact commands" },
    { "issueIndex": 2, "status": "skipped", "summary": "Not a real issue after reading the file" }
  ]
}
\`\`\`
Use issue numbers from above. Status: "fixed" or "skipped". Keep summaries under 15 words.`;
}

function buildSuggestionEnrichPrompt(input: RemediationInput): string {
	if (input.suggestions.length === 0) {
		return "";
	}

	// Sort by impact level: High > Medium > Low
	const impactOrder: Record<string, number> = { High: 0, Medium: 1, Low: 2 };
	const sorted = [...input.suggestions].sort(
		(a, b) =>
			(impactOrder[a.impactLevel ?? "Low"] ?? 2) -
			(impactOrder[b.impactLevel ?? "Low"] ?? 2),
	);

	const contextFilesList = input.contextFilePaths
		.map((p) => `- ${p}`)
		.join("\n");
	const issueBlocks = sorted
		.map((issue, i) => formatSuggestionIssueBlock(issue, i))
		.join("\n\n");

	const agentDescription = getAgentContextDescription(input.targetAgent);
	const outputTypeInstructions = getOutputTypeInstructions(input.targetAgent);

	return `# Enrich Documentation

## Role
You are enriching AI agent documentation for the target: **${agentDescription.split(".")[0]}**.
${agentDescription}

## Context Files
${contextFilesList}

${input.technicalInventorySection ? `## Technical Context\n${input.technicalInventorySection}\n` : ""}## Project
${formatProjectLine(input.projectSummary)}

## Documentation Gaps (${input.suggestions.length})

${issueBlocks}

${outputTypeInstructions}

## Phantom File Remapping
Evaluator-suggested file paths (e.g., \`packages/api/AGENTS.md\`) may not match the target agent's conventions. Ignore evaluator-suggested paths for new files and create files at the correct location per the output type instructions above.

## Instructions
1. Read the target files and scan the codebase before making changes
2. Use your own judgment to assess each gap: these were produced by an automated evaluator and some may be false positives or irrelevant given the actual codebase. Skip any suggestion that is not genuinely useful after reviewing the code
3. For each remaining gap, decide the output type (standard, skill, or generic update) using the decision criteria above
4. Address each gap, highest impact first
5. Add concise, accurate documentation based on actual codebase analysis
6. Preserve all correct existing content
7. Keep additions minimal — only add what's needed to close the gap
8. After making all changes, output a JSON summary:
\`\`\`json
{
  "actions": [
    { "issueIndex": 1, "status": "added", "file": ".claude/rules/testing.md", "summary": "Created testing conventions standard", "outputType": "standard" },
    { "issueIndex": 2, "status": "added", "file": "AGENTS.md", "summary": "Added architecture overview section", "outputType": "generic" },
    { "issueIndex": 3, "status": "skipped", "summary": "Already documented in existing section" }
  ]
}
\`\`\`
Use gap numbers from above. Status: "added" or "skipped". Keep summaries under 15 words. Include \`outputType\` ("standard", "skill", or "generic") for non-skipped actions.`;
}

export function generateRemediationPrompts(
	input: RemediationInput,
): RemediationPrompts {
	return {
		errorFixPrompt: buildErrorFixPrompt(input),
		suggestionEnrichPrompt: buildSuggestionEnrichPrompt(input),
		errorCount: input.errors.length,
		suggestionCount: input.suggestions.length,
	};
}
