   182→	// SSE event handler
   183→	const handleProgressEvent = useCallback(
   184→		(event: SSEEvent) => {
   185→			setProgressState((prev) => {
   186→				if (!prev) return prev;
   187→
   188→				const newState = { ...prev };
   189→
   190→				switch (event.type) {
   191→					// Clone events
   192→					case "clone.started": {
   193→						newState.logs = [
   194→							...prev.logs,
   195→							{
   196→								timestamp: new Date(),
   197→								type: "info",
   198→								message: "Cloning repository...",
   199→							},
   200→						];
   201→						break;
   202→					}
   203→
   204→					case "clone.completed": {
   205→						newState.logs = [
   206→							...prev.logs,
   207→							{
   208→								timestamp: new Date(),
   209→								type: "success",
   210→								message: "Repository cloned successfully",
   211→							},
   212→						];
   213→						break;
   214→					}
   215→
   216→					case "clone.warning": {
   217→						const data = (event as ICloneWarningEvent).data;
   218→						newState.logs = [
   219→							...prev.logs,
   220→							{
   221→								timestamp: new Date(),
   222→								type: "warning",
   223→								message: data.message,
   224→							},
   225→						];
   226→						break;
   227→					}
   228→
   229→					// Discovery events
   230→					case "discovery.started": {
   231→						newState.logs = [
   232→							...prev.logs,
   233→							{
   234→								timestamp: new Date(),
   235→								type: "info",
   236→								message: "Discovering context files...",
   237→							},
   238→						];
   239→						break;
   240→					}
   241→
   242→					case "discovery.completed": {
   243→						const data = (event as IDiscoveryCompletedEvent).data;
   244→						newState.logs = [
   245→							...prev.logs,
   246→							{
   247→								timestamp: new Date(),
   248→								type: "success",
   249→								message: `Found ${data.filesFound} context file(s)`,
   250→							},
   251→						];
   252→						break;
   253→					}
   254→
   255→					// Context sub-step events
   256→					case "context.cloc": {
   257→						const data = (event as IContextClocEvent).data;
   258→						if (data.status === "started") {
   259→							newState.logs = [
   260→								...prev.logs,
   261→								{
   262→									timestamp: new Date(),
   263→									type: "info",
   264→									message: "Analyzing codebase size...",
   265→								},
   266→							];
   267→						} else if (data.status === "completed") {
   268→							newState.logs = [
   269→								...prev.logs,
   270→								{
   271→									timestamp: new Date(),
   272→									type: "success",
   273→									message: `Codebase: ${data.totalLines?.toLocaleString() || "?"} lines across ${data.languageCount || "?"} languages`,
   274→								},
   275→							];
   276→						}
   277→						break;
   278→					}
   279→
   280→					case "context.folders": {
   281→						const data = (event as IContextFoldersEvent).data;
   282→						if (data.status === "started") {
   283→							newState.logs = [
   284→								...prev.logs,
   285→								{
   286→									timestamp: new Date(),
   287→									type: "info",
   288→									message: "Analyzing directory structure...",
   289→								},
   290→							];
   291→						} else if (data.status === "completed") {
   292→							newState.logs = [
   293→								...prev.logs,
   294→								{
   295→									timestamp: new Date(),
   296→									type: "success",
   297→									message: `Structure: ${data.folderCount || "?"} key directories`,
   298→								},
   299→							];
   300→						}
   301→						break;
   302→					}
   303→
   304→					case "context.analysis": {
   305→						const data = (event as IContextAnalysisEvent).data;
   306→						if (data.status === "started") {
   307→							newState.logs = [
   308→								...prev.logs,
   309→								{
   310→									timestamp: new Date(),
   311→									type: "info",
   312→									message: "Identifying project context...",
   313→								},
   314→							];
   315→						} else if (data.status === "completed") {
   316→							newState.logs = [
   317→								...prev.logs,
   318→								{
   319→									timestamp: new Date(),
   320→									type: "success",
   321→									message: "Project context identified",
   322→								},
   323→							];
   324→						}
   325→						break;
   326→					}
   327→
   328→					case "context.warning": {
   329→						const data = (event as IContextWarningEvent).data;
   330→						newState.logs = [
   331→							...prev.logs,
   332→							{
   333→								timestamp: new Date(),
   334→								type: "warning",
   335→								message: data.message,
   336→							},
   337→						];
   338→						break;
   339→					}
   340→
   341→					case "job.started": {
   342→						const data = (event as IJobStartedEvent).data;
   343→						newState.status = "running";
   344→						newState.totalFiles = data.totalFiles || 0;
   345→						newState.logs = [
   346→							...prev.logs,
   347→							{
   348→								timestamp: new Date(),
   349→								type: "info",
   350→								message: `Started evaluation (${data.evaluationMode} mode, ${data.totalFiles} file(s))`,
   351→							},
   352→						];
   353→						break;
   354→					}
   355→
   356→					case "file.started": {
   357→						const data = (event as IFileStartedEvent).data;
   358→						newState.currentFile = data.filePath;
   359→						newState.logs = [
   360→							...prev.logs,
   361→							{
   362→								timestamp: new Date(),
   363→								type: "info",
   364→								message: `Processing ${data.filePath}`,
   365→							},
   366→						];
   367→						break;
   368→					}
   369→
   370→					case "file.completed": {
   371→						newState.completedFiles = (prev.completedFiles || 0) + 1;
   372→						// Update percentage based on file progress
   373→						if (newState.totalFiles > 0) {
   374→							newState.percentage = Math.round(
   375→								(newState.completedFiles / newState.totalFiles) * 50,
   376→							);
   377→						}
   378→						break;
   379→					}
   380→
   381→					case "evaluator.progress": {
   382→						const data = (event as IEvaluatorProgressEvent).data;
   383→						newState.currentEvaluator = data.evaluatorName;
   384→						newState.completedEvaluators = data.evaluatorIndex;
   385→						newState.totalEvaluators = data.totalEvaluators;
   386→						// Update percentage based on evaluator progress
   387→						if (data.totalEvaluators > 0) {
   388→							const evaluatorProgress =
   389→								(data.evaluatorIndex / data.totalEvaluators) * 50;
   390→							newState.percentage = 50 + Math.round(evaluatorProgress);
   391→						}
   392→						// Include file context in log message when evaluating multiple files
   393→						const fileContext = data.currentFile
   394→							? ` on ${data.currentFile.split("/").pop()}`
   395→							: "";
   396→						newState.logs = [
   397→							...prev.logs,
   398→							{
   399→								timestamp: new Date(),
   400→								type: "info",
   401→								message: `Running ${data.evaluatorName}${fileContext} (${data.evaluatorIndex + 1}/${data.totalEvaluators})`,
   402→							},
   403→						];
   404→						break;
   405→					}
   406→
   407→					case "evaluator.completed": {
   408→						newState.completedEvaluators = (prev.completedEvaluators || 0) + 1;
   409→						break;
   410→					}
   411→
   412→					case "evaluator.retry": {
   413→						const data = event.data as {
   414→							evaluatorName: string;
   415→							attempt: number;
   416→							maxRetries: number;
   417→							error: string;
   418→							currentFile?: string;
   419→						};
   420→						const retriesRemaining = data.maxRetries - data.attempt;
   421→						newState.logs = [
   422→							...prev.logs,
   423→							{
   424→								timestamp: new Date(),
   425→								type: "warning",
   426→								message: `Retry ${data.attempt}/${data.maxRetries} for ${data.evaluatorName}: ${data.error.substring(0, 100)}${data.error.length > 100 ? "..." : ""} (${retriesRemaining} retries remaining)`,
   427→							},
   428→						];
   429→						break;
   430→					}
   431→
   432→					case "evaluator.timeout": {
   433→						const data = event.data as {
   434→							evaluatorName: string;
   435→							elapsedMs: number;
   436→							timeoutMs: number;
   437→							currentFile?: string;
   438→						};
   439→						const elapsedSec = Math.round(data.elapsedMs / 1000);
   440→						const timeoutSec = Math.round(data.timeoutMs / 1000);
   441→						newState.logs = [
   442→							...prev.logs,
   443→							{
   444→								timestamp: new Date(),
   445→								type: "error",
   446→								message: `Timeout: ${data.evaluatorName} exceeded ${timeoutSec}s limit (elapsed: ${elapsedSec}s)`,
   447→							},
   448→						];
   449→						break;
   450→					}
   451→
   452→					case "curation.started": {
   453→						const data = event.data;
   454→						const typeLabel =
   455→							data.issueType === "error"
   456→								? "errors"
   457→								: data.issueType === "suggestion"
   458→									? "suggestions"
   459→									: "issues";
   460→						newState.currentEvaluator = `Impact Curation (${typeLabel})`;
   461→						newState.logs = [
   462→							...prev.logs,
   463→							{
   464→								timestamp: new Date(),
   465→								type: "info",
   466→								message: `Curating top ${typeLabel} from ${data.totalIssues} total...`,
   467→							},
   468→						];
   469→						break;
   470→					}
   471→
   472→					case "curation.completed": {
   473→						const data = event.data;
   474→						const typeLabel =
   475→							data.issueType === "error"
   476→								? "errors"
   477→								: data.issueType === "suggestion"
   478→									? "suggestions"
   479→									: "issues";
   480→						newState.logs = [
   481→							...prev.logs,
   482→							{
   483→								timestamp: new Date(),
   484→								type: "success",
   485→								message: `Impact curation completed for ${typeLabel} (${data.curatedCount} selected)`,
   486→							},
   487→						];
   488→						break;
   489→					}
   490→
   491→					case "evaluation.warning": {
   492→						const data = event.data;
   493→						newState.logs = [
   494→							...prev.logs,
   495→							{
   496→								timestamp: new Date(),
   497→								type: "warning",
   498→								message: data.message || "Some evaluators encountered errors",
   499→							},
   500→						];
   501→						break;
   502→					}
   503→
   504→					case "job.completed": {
   505→						const data = (event as IJobCompletedEvent).data;
   506→						newState.status = "completed";
   507→						newState.percentage = 100;
   508→						newState.logs = [
   509→							...prev.logs,
   510→							{
   511→								timestamp: new Date(),
   512→								type: "success",
   513→								message: `Evaluation completed in ${Math.round(data.duration / 1000)}s`,
   514→							},
   515→						];
   516→						// Preserve logs for display in Summary after completion
   517→						setEvaluationLogs(newState.logs);
   518→						// Set the evaluation data
   519→						setEvaluationData(data.result);
   520→						setEvaluationMode("completed");
   521→						// Reset job state after a short delay (user is already at /evaluation/:jobId)
   522→						// Clear any existing timeout first
   523→						if (completionTimeoutRef.current) {
   524→							clearTimeout(completionTimeoutRef.current);
   525→							completionTimeoutRef.current = null;
   526→						}
   527→						completionTimeoutRef.current = setTimeout(() => {
   528→							setCurrentJobId(null);
   529→							setSSEUrl(null);
   530→							setProgressState(null);
   531→						}, 100);
   532→						// Refresh history to show new evaluation
   533→						historyApi.refresh();
   534→						break;
   535→					}
   536→
   537→					case "job.failed": {
   538→						const data = (event as IJobFailedEvent).data;
   539→						newState.status = "failed";
   540→						newState.errorMessage = data.error?.message || "Unknown error";
   541→						newState.errorCode = data.error?.code;
   542→						newState.logs = [
   543→							...prev.logs,
   544→							{
   545→								timestamp: new Date(),
   546→								type: "error",
   547→								message: `Evaluation failed: ${data.error?.message || "Unknown error"}`,
   548→							},
   549→						];
   550→						// Keep evaluationMode as 'evaluating' so ProgressPanel stays visible
   551→						// User can dismiss manually with the "Try Again" button
   552→						// Refresh history to show failed evaluation
   553→						historyApi.refresh();
   554→						break;
   555→					}
   556→				}
   557→
   558→				// Keep only last 50 logs
   559→				if (newState.logs.length > 50) {
   560→					newState.logs = newState.logs.slice(-50);
   561→				}
   562→
   563→				return newState;
   564→			});
   565→		},
   566→		[historyApi],
   567→	);
