   749→	// Load evaluation from URL on mount or when URL changes
   750→	// Handles both active jobs (reconnect SSE) and completed evaluations (load from database)
   751→	// biome-ignore lint/correctness/useExhaustiveDependencies: handleFileLoad is intentionally excluded - we want the latest version but don't want it to trigger re-runs
   752→	useEffect(() => {
   753→		if (!urlEvaluationId) return;
   754→		if (evaluationData && evaluationMode === "completed") return; // Already loaded
   755→		if (evaluationMode === "evaluating") return; // Currently evaluating
   756→
   757→		const loadEvaluationById = async () => {
   758→			setIsLoadingFromUrl(true);
   759→			setApiError(null);
   760→
   761→			try {
   762→				// Step 1: Check if it's an active job
   763→				const jobStatus = await api.getJobStatus(urlEvaluationId);
   764→
   765→				if (jobStatus.status === "completed" && jobStatus.result) {
   766→					// Job completed, result in memory - use it directly
   767→					handleFileLoad(jobStatus.result);
   768→					setCurrentRepositoryUrl(jobStatus.repositoryUrl || null);
   769→					setEvaluationMode("completed");
   770→					// Load feedback, bookmarks, and selections
   771→					try {
   772→						const feedback =
   773→							await feedbackApi.getFeedbackForEvaluation(urlEvaluationId);
   774→						setFeedbackMap(feedback);
   775→						if (!cloudMode) {
