  1338→			const maxSeverityB = getMaxCategoryGroupSeverity(groupsB);
  1339→
  1340→			if (maxSeverityA !== maxSeverityB) {
  1341→				return maxSeverityB - maxSeverityA;
  1342→			}
  1343→
  1344→			return fileA.localeCompare(fileB);
  1345→		});
  1346→
  1347→		// Return cross-file first, then sorted files
  1348→		return crossFileEntry ? [crossFileEntry, ...fileEntries] : fileEntries;
