     1→import React, { useState } from "react";
     2→import type { Metadata } from "../types/evaluation";
     3→import { ContextFilesBrowserModal } from "./shared/ContextFilesBrowserModal";
     4→import { LinkedDocsBrowserModal } from "./shared/LinkedDocsBrowserModal";
     5→import { SkillsBrowserModal } from "./shared/SkillsBrowserModal";
     6→
     7→interface ContextTabProps {
     8→	metadata: Metadata;
     9→}
    10→
