    90→		fill="none"
    91→		viewBox="0 0 24 24"
    92→		stroke="currentColor"
    93→	>
    94→		<path
    95→			strokeLinecap="round"
    96→			strokeLinejoin="round"
    97→			strokeWidth={2}
    98→			d="M9 5H7a2 2 0 00-2 2v12a2 2 0 002 2h10a2 2 0 002-2V7a2 2 0 00-2-2h-2M9 5a2 2 0 002 2h2a2 2 0 002-2M9 5a2 2 0 012-2h2a2 2 0 012 2m-6 9l2 2 4-4"
