   102→
   103→export const ContextTab: React.FC<ContextTabProps> = ({ metadata }) => {
   104→	const [showSkillsModal, setShowSkillsModal] = useState(false);
   105→	const [showLinkedDocsModal, setShowLinkedDocsModal] = useState(false);
   106→	const [contextFilesModalType, setContextFilesModalType] = useState<
   107→		| "agents"
   108→		| "claude"
   109→		| "copilot"
   110→		| "rules"
   111→		| "claude-code"
   112→		| "cursor-rules"
   113→		| "skills"
   114→		| null
   115→	>(null);
   116→
   117→	// Context files derivation
   118→	const agentsFilePaths = metadata.projectContext?.agentsFilePaths ?? [];
   119→	const contextFilesFromApi = metadata.projectContext?.contextFiles ?? [];
   120→
   121→	const contextFiles =
   122→		contextFilesFromApi.length > 0
   123→			? contextFilesFromApi
   124→			: agentsFilePaths.map((path) => {
   125→					const lowerPath = path.toLowerCase();
   126→					let type: "agents" | "claude" | "copilot" = "agents";
   127→					if (lowerPath.includes("claude")) {
   128→						type = "claude";
   129→					} else if (lowerPath.includes("copilot")) {
   130→						type = "copilot";
   131→					}
   132→					return {
   133→						path,
   134→						type,
   135→						content: "",
   136→						summary: "Re-run evaluation to load content and summary.",
   137→					};
   138→				});
   139→
   140→	const agentsMdPaths =
   141→		contextFilesFromApi.length > 0
   142→			? contextFilesFromApi
   143→					.filter((f) => f.type === "agents")
   144→					.map((f) => f.path)
   145→			: agentsFilePaths.filter((p) => p.toLowerCase().includes("agents"));
   146→	const claudeMdPaths =
   147→		contextFilesFromApi.length > 0
   148→			? contextFilesFromApi
   149→					.filter((f) => f.type === "claude")
   150→					.map((f) => f.path)
   151→			: agentsFilePaths.filter((p) => p.toLowerCase().includes("claude"));
   152→	const rulesPaths =
   153→		contextFilesFromApi.length > 0
   154→			? contextFilesFromApi.filter((f) => f.type === "rules").map((f) => f.path)
   155→			: agentsFilePaths.filter((p) => p.includes(".claude/rules/"));
   156→	const copilotInstructionsPaths =
   157→		contextFilesFromApi.length > 0
   158→			? contextFilesFromApi
   159→					.filter((f) => f.type === "copilot")
   160→					.map((f) => f.path)
   161→			: agentsFilePaths.filter((p) =>
   162→					p.toLowerCase().includes("copilot-instructions"),
   163→				);
   164→	const cursorRulesPaths =
   165→		contextFilesFromApi.length > 0
   166→			? contextFilesFromApi
   167→					.filter((f) => f.type === "cursor-rules")
   168→					.map((f) => f.path)
   169→			: agentsFilePaths.filter((p) => p.includes(".cursor/rules/"));
   170→
   171→	const agentsMdCount = agentsMdPaths.length;
   172→	const claudeMdCount = claudeMdPaths.length;
   173→	const rulesCount = rulesPaths.length;
   174→	const copilotInstructionsCount = copilotInstructionsPaths.length;
   175→	const cursorRulesCount = cursorRulesPaths.length;
   176→	const skills = metadata.projectContext?.skills ?? [];
   177→	const skillsCount = skills.length;
   178→	const linkedDocs = metadata.projectContext?.linkedDocs ?? [];
   179→	const linkedDocsCount = linkedDocs.length;
   180→
   181→	// Sorted for display
   182→	const sortedAgentsMdPaths = [...agentsMdPaths].sort((a, b) =>
   183→		a.localeCompare(b),
   184→	);
   185→	const sortedClaudeMdPaths = [...claudeMdPaths].sort((a, b) =>
   186→		a.localeCompare(b),
   187→	);
   188→	const sortedRulesPaths = [...rulesPaths].sort((a, b) => a.localeCompare(b));
   189→	const sortedCopilotInstructionsPaths = [...copilotInstructionsPaths].sort(
   190→		(a, b) => a.localeCompare(b),
   191→	);
   192→	const sortedCursorRulesPaths = [...cursorRulesPaths].sort((a, b) =>
   193→		a.localeCompare(b),
   194→	);
   195→	const sortedSkills = [...skills].sort((a, b) => a.name.localeCompare(b.name));
   196→	const sortedLinkedDocs = [...linkedDocs].sort((a, b) =>
   197→		a.path.localeCompare(b.path),
   198→	);
   199→
   200→	// Section visibility
   201→	const hasAgentsMd = agentsMdCount > 0;
   202→	const hasClaudeCode = claudeMdCount + rulesCount + skillsCount > 0;
   203→	const hasCopilot = copilotInstructionsCount > 0;
   204→	const hasCursor = cursorRulesCount > 0;
   205→	const hasLinkedDocs = linkedDocsCount > 0;
   206→	const hasAnything =
   207→		hasAgentsMd || hasClaudeCode || hasCopilot || hasCursor || hasLinkedDocs;
   208→
   209→	if (!hasAnything) {
   210→		return (
   211→			<div className="glass-card p-12 text-center">
   212→				<DocIcon className="w-12 h-12 text-slate-600 mx-auto mb-3" />
   213→				<p className="text-sm text-slate-400">
   214→					No context files found in this repository.
   215→				</p>
   216→			</div>
   217→		);
   218→	}
   219→
   220→	return (
   221→		<div className="space-y-4 animate-fade-in">
   222→			{/* AGENTS.md section */}
   223→			{hasAgentsMd && (
   224→				<SectionCard
   225→					icon={<DocIcon className="w-4 h-4 text-slate-300" />}
   226→					title="AGENTS.md"
   227→					count={agentsMdCount}
   228→					countLabel="file"
   229→				>
   230→					<FileRow
   231→						label="AGENTS.md"
   232→						icon={<DocIcon className="h-4 w-4 text-slate-500" />}
   233→						count={agentsMdCount}
   234→						paths={sortedAgentsMdPaths}
   235→						onAction={() => setContextFilesModalType("agents")}
   236→						actionLabel="Browse"
   237→					/>
   238→				</SectionCard>
   239→			)}
   240→
   241→			{/* Claude Code section */}
   242→			{hasClaudeCode && (
   243→				<SectionCard
   244→					icon={
   245→						<svg
   246→							className="w-4 h-4 text-slate-300"
   247→							fill="currentColor"
   248→							viewBox="0 0 16 16"
   249→						>
   250→							<path d="m3.127 10.604 3.135-1.76.053-.153-.053-.085H6.11l-.525-.032-1.791-.048-1.554-.065-1.505-.08-.38-.081L0 7.832l.036-.234.32-.214.455.04 1.009.069 1.513.105 1.097.064 1.626.17h.259l.036-.105-.089-.065-.068-.064-1.566-1.062-1.695-1.121-.887-.646-.48-.327-.243-.306-.104-.67.435-.48.585.04.15.04.593.456 1.267.981 1.654 1.218.242.202.097-.068.012-.049-.109-.181-.9-1.626-.96-1.655-.428-.686-.113-.411a2 2 0 0 1-.068-.484l.496-.674L4.446 0l.662.089.279.242.411.94.666 1.48 1.033 2.014.302.597.162.553.06.17h.105v-.097l.085-1.134.157-1.392.154-1.792.052-.504.25-.605.497-.327.387.186.319.456-.045.294-.19 1.23-.37 1.93-.243 1.29h.142l.161-.16.654-.868 1.097-1.372.484-.545.565-.601.363-.287h.686l.505.751-.226.775-.707.895-.585.759-.839 1.13-.524.904.048.072.125-.012 1.897-.403 1.024-.186 1.223-.21.553.258.06.263-.218.536-1.307.323-1.533.307-2.284.54-.028.02.032.04 1.029.098.44.024h1.077l2.005.15.525.346.315.424-.053.323-.807.411-3.631-.863-.872-.218h-.12v.073l.726.71 1.331 1.202 1.667 1.55.084.383-.214.302-.226-.032-1.464-1.101-.565-.497-1.28-1.077h-.084v.113l.295.432 1.557 2.34.08.718-.112.234-.404.141-.444-.08-.911-1.28-.94-1.44-.759-1.291-.093.053-.448 4.821-.21.246-.484.186-.403-.307-.214-.496.214-.98.258-1.28.21-1.016.19-1.263.112-.42-.008-.028-.092.012-.953 1.307-1.448 1.957-1.146 1.227-.274.109-.477-.247.045-.44.266-.39 1.586-2.018.956-1.25.617-.723-.004-.105h-.036l-4.212 2.736-.75.096-.324-.302.04-.496.154-.162 1.267-.871z" />
   251→						</svg>
   252→					}
   253→					title="Claude Code"
   254→					count={claudeMdCount + rulesCount + skillsCount}
   255→					countLabel="item"
   256→				>
   257→					<FileRow
   258→						label="CLAUDE.md"
   259→						icon={<DocIcon className="h-4 w-4 text-slate-500" />}
   260→						count={claudeMdCount}
   261→						paths={sortedClaudeMdPaths}
   262→						onAction={() => setContextFilesModalType("claude")}
   263→						actionLabel="Browse"
   264→					/>
   265→					<FileRow
   266→						label="Rules"
   267→						icon={<RulesIcon className="h-4 w-4 text-slate-500" />}
   268→						count={rulesCount}
   269→						paths={sortedRulesPaths}
   270→						onAction={() => setContextFilesModalType("rules")}
   271→						actionLabel="Browse"
   272→					/>
   273→					{skillsCount > 0 && (
   274→						<ItemRow
   275→							label="Skills"
   276→							icon={<SparklesIcon className="h-4 w-4 text-slate-500" />}
   277→							count={skillsCount}
   278→							chips={sortedSkills.slice(0, 5).map((s) => s.name)}
   279→							overflowCount={skillsCount > 5 ? skillsCount - 5 : undefined}
   280→							onAction={() => setShowSkillsModal(true)}
   281→							actionLabel="Display all"
   282→						/>
   283→					)}
   284→				</SectionCard>
   285→			)}
   286→
   287→			{/* GitHub Copilot section */}
   288→			{hasCopilot && (
   289→				<SectionCard
   290→					icon={
   291→						<svg
   292→							className="w-4 h-4 text-slate-300"
   293→							fill="none"
   294→							stroke="currentColor"
   295→							viewBox="0 0 24 24"
   296→						>
   297→							<path
   298→								strokeLinecap="round"
   299→								strokeLinejoin="round"
   300→								strokeWidth={2}
   301→								d="M15 12a3 3 0 11-6 0 3 3 0 016 0z"
   302→							/>
   303→							<path
   304→								strokeLinecap="round"
   305→								strokeLinejoin="round"
   306→								strokeWidth={2}
   307→								d="M2.458 12C3.732 7.943 7.523 5 12 5c4.478 0 8.268 2.943 9.542 7-1.274 4.057-5.064 7-9.542 7-4.477 0-8.268-2.943-9.542-7z"
   308→							/>
   309→						</svg>
   310→					}
   311→					title="GitHub Copilot"
   312→					count={copilotInstructionsCount}
   313→					countLabel="file"
   314→				>
   315→					<FileRow
   316→						label="Copilot Instructions"
   317→						icon={<DocIcon className="h-4 w-4 text-slate-500" />}
   318→						count={copilotInstructionsCount}
   319→						paths={sortedCopilotInstructionsPaths}
   320→						onAction={() => setContextFilesModalType("copilot")}
   321→						actionLabel="Browse"
   322→					/>
   323→				</SectionCard>
   324→			)}
   325→
   326→			{/* Cursor section */}
   327→			{hasCursor && (
   328→				<SectionCard
   329→					icon={
   330→						<svg
   331→							className="w-4 h-4 text-slate-300"
   332→							fill="none"
   333→							stroke="currentColor"
   334→							viewBox="0 0 24 24"
   335→						>
   336→							<path
   337→								strokeLinecap="round"
   338→								strokeLinejoin="round"
   339→								strokeWidth={2}
   340→								d="M15.232 5.232l3.536 3.536m-2.036-5.036a2.5 2.5 0 113.536 3.536L6.5 21.036H3v-3.572L16.732 3.732z"
   341→							/>
   342→						</svg>
   343→					}
   344→					title="Cursor"
   345→					count={cursorRulesCount}
   346→					countLabel="rule"
   347→				>
   348→					<FileRow
   349→						label="Cursor Rules"
   350→						icon={<RulesIcon className="h-4 w-4 text-slate-500" />}
   351→						count={cursorRulesCount}
   352→						paths={sortedCursorRulesPaths}
   353→						onAction={() => setContextFilesModalType("cursor-rules")}
   354→						actionLabel="Browse"
   355→					/>
   356→				</SectionCard>
   357→			)}
   358→
   359→			{/* Linked Docs section */}
   360→			{hasLinkedDocs && (
   361→				<SectionCard
   362→					icon={<LinkIcon className="w-4 h-4 text-slate-300" />}
   363→					title="Linked Docs"
   364→					count={linkedDocsCount}
   365→					countLabel="doc"
   366→				>
   367→					<ItemRow
   368→						label="Linked Docs"
   369→						icon={<LinkIcon className="h-4 w-4 text-slate-500" />}
   370→						count={linkedDocsCount}
   371→						chips={sortedLinkedDocs.slice(0, 3).map((d) => d.path)}
   372→						overflowCount={
   373→							linkedDocsCount > 3 ? linkedDocsCount - 3 : undefined
   374→						}
   375→						onAction={() => setShowLinkedDocsModal(true)}
   376→						actionLabel="Display all"
   377→					/>
   378→				</SectionCard>
   379→			)}
   380→
   381→			{/* Modals */}
   382→			<SkillsBrowserModal
   383→				isOpen={showSkillsModal}
   384→				onClose={() => setShowSkillsModal(false)}
   385→				skills={skills}
   386→			/>
   387→			<ContextFilesBrowserModal
   388→				isOpen={contextFilesModalType !== null}
   389→				onClose={() => setContextFilesModalType(null)}
   390→				contextFiles={contextFiles}
   391→				filterType={contextFilesModalType ?? undefined}
   392→			/>
   393→			<LinkedDocsBrowserModal
   394→				isOpen={showLinkedDocsModal}
   395→				onClose={() => setShowLinkedDocsModal(false)}
   396→				linkedDocs={linkedDocs}
   397→			/>
   398→		</div>
   399→	);
   400→};
   401→
   402→/** Section card with header */
   403→const SectionCard: React.FC<{
   404→	icon: React.ReactNode;
   405→	title: string;
   406→	count: number;
   407→	countLabel: string;
   408→	children: React.ReactNode;
   409→}> = ({ icon, title, count, countLabel, children }) => (
   410→	<div className="glass-card p-6">
   411→		<div className="flex items-center gap-2 mb-4">
   412→			<span className="w-6 h-6 bg-slate-700 rounded-lg flex items-center justify-center">
   413→				{icon}
   414→			</span>
   415→			<h3 className="text-base font-bold text-slate-100">{title}</h3>
   416→			<span className="text-xs text-slate-500 ml-1">
   417→				{count} {count === 1 ? countLabel : `${countLabel}s`}
   418→			</span>
   419→		</div>
   420→		<div className="space-y-1">{children}</div>
   421→	</div>
   422→);
   423→
   424→/** A row for a file-type context item (with paths) */
   425→const FileRow: React.FC<{
   426→	label: string;
   427→	icon: React.ReactNode;
   428→	count: number;
   429→	paths: string[];
   430→	onAction: () => void;
   431→	actionLabel: string;
   432→}> = ({ label, icon, count, paths, onAction, actionLabel }) => {
   433→	if (count === 0) return null;
   434→
   435→	return (
   436→		<div className="flex items-center justify-between py-2.5 px-3 rounded-lg hover:bg-slate-800/30 transition-colors">
   437→			<div className="flex items-center gap-3 flex-1 min-w-0">
   438→				<span className="flex-shrink-0">{icon}</span>
   439→				<span className="text-sm text-slate-300">{label}</span>
   440→				<span className="text-xs bg-slate-700/60 text-slate-400 px-1.5 py-0.5 rounded">
   441→					{count}
   442→				</span>
   443→				<div className="flex-1 min-w-0 ml-2">
   444→					<div className="flex flex-wrap gap-1.5">
   445→						{paths.slice(0, 3).map((path, i) => (
   446→							<span
   447→								key={i}
   448→								className="text-xs text-slate-500 bg-slate-800/50 px-1.5 py-0.5 rounded truncate max-w-48"
   449→								title={path}
   450→							>
   451→								{path}
   452→							</span>
   453→						))}
   454→						{paths.length > 3 && (
   455→							<span className="text-xs text-slate-600">
   456→								+{paths.length - 3} more
   457→							</span>
   458→						)}
   459→					</div>
   460→				</div>
   461→			</div>
   462→			<button
   463→				onClick={onAction}
   464→				className="flex items-center gap-1 text-xs text-indigo-400 hover:text-indigo-300 transition-colors ml-3 flex-shrink-0"
   465→			>
   466→				<span>{actionLabel}</span>
   467→				<ExternalLinkIcon />
   468→			</button>
   469→		</div>
   470→	);
   471→};
   472→
   473→/** A row for a non-file item (with chips preview) */
   474→const ItemRow: React.FC<{
   475→	label: string;
   476→	icon: React.ReactNode;
   477→	count: number;
   478→	chips: string[];
   479→	overflowCount?: number;
   480→	onAction: () => void;
   481→	actionLabel: string;
   482→}> = ({ label, icon, count, chips, overflowCount, onAction, actionLabel }) => (
   483→	<div className="flex items-center justify-between py-2.5 px-3 rounded-lg hover:bg-slate-800/30 transition-colors">
   484→		<div className="flex items-center gap-3 flex-1 min-w-0">
   485→			<span className="flex-shrink-0">{icon}</span>
   486→			<span className="text-sm text-slate-300">{label}</span>
   487→			<span className="text-xs bg-slate-700/60 text-slate-400 px-1.5 py-0.5 rounded">
   488→				{count}
   489→			</span>
   490→			<div className="flex-1 min-w-0 ml-2">
