   437→};
   438→
   439→/** Section card with header */
   440→const SectionCard: React.FC<{
   441→	icon: React.ReactNode;
   442→	title: string;
   443→	count: number;
   444→	countLabel: string;
   445→	children: React.ReactNode;
   446→}> = ({ icon, title, count, countLabel, children }) => (
   447→	<div className="glass-card p-6">
   448→		<div className="flex items-center gap-2 mb-4">
   449→			<span className="w-6 h-6 bg-slate-700 rounded-lg flex items-center justify-center">
   450→				{icon}
   451→			</span>
   452→			<h3 className="text-base font-bold text-slate-100">{title}</h3>
   453→			<span className="text-xs text-slate-500 ml-1">
   454→				{count} {count === 1 ? countLabel : `${countLabel}s`}
   455→			</span>
   456→		</div>
   457→		<div className="space-y-1">{children}</div>
   458→	</div>
   459→);
   460→
   461→/** A row for a file-type context item (with paths) */
   462→const FileRow: React.FC<{
   463→	label: string;
   464→	icon: React.ReactNode;
   465→	count: number;
   466→	paths: string[];
   467→	onAction: () => void;
   468→	actionLabel: string;
   469→}> = ({ label, icon, count, paths, onAction, actionLabel }) => {
   470→	if (count === 0) return null;
   471→
