 189→				switch (event.type) {
 190→					// Clone events
 191→					case "clone.started": {
 192→						newState.logs = [
 193→							...prev.logs,
 194→							{
 195→								timestamp: new Date(),
 196→								type: "info",
 197→								message: "Cloning repository...",
 198→							},
 199→						];
 200→						break;
 201→					}
 202→
 203→					case "clone.completed": {
 204→						newState.logs = [
 205→							...prev.logs,
 206→							{
 207→								timestamp: new Date(),
 208→								type: "success",
 209→								message: "Repository cloned successfully",
 210→							},
 211→						];
 212→						break;
 213→					}
 214→
 215→					case "clone.warning": {
 216→						const data = (event as ICloneWarningEvent).data;
 217→						newState.logs = [
 218→							...prev.logs,
 219→							{
 220→								timestamp: new Date(),
 221→								type: "warning",
 222→								message: data.message,
 223→							},
 224→						];
 225→						break;
 226→					}
 227→
 228→					// Discovery events
 229→					case "discovery.started": {
 230→						newState.logs = [
 231→							...prev.logs,
 232→							{
 233→								timestamp: new Date(),
 234→								type: "info",
 235→								message: "Discovering context files...",
 236→							},
 237→						];
 238→						break;
 239→					}
 240→
 241→					case "discovery.completed": {
 242→						const data = (event as IDiscoveryCompletedEvent).data;
 243→						newState.logs = [
 244→							...prev.logs,
 245→							{
 246→								timestamp: new Date(),
 247→								type: "success",
 248→								message: `Found ${data.filesFound} context file(s)`,
 249→							},
 250→						];
 251→						break;
 252→					}
 253→
 254→					// Context sub-step events
 255→					case "context.cloc": {
 256→						const data = (event as IContextClocEvent).data;
 257→						if (data.status === "started") {
 258→							newState.logs = [
 259→								...prev.logs,
 260→								{
 261→									timestamp: new Date(),
 262→									type: "info",
 263→									message: "Analyzing codebase size...",
 264→								},
 265→							];
 266→						} else if (data.status === "completed") {
 267→							newState.logs = [
 268→								...prev.logs,
 269→								{
 270→									timestamp: new Date(),
 271→									type: "success",
 272→									message: `Codebase: ${data.totalLines?.toLocaleString() || "?"} lines across ${data.languageCount || "?"} languages`,
 273→								},
 274→							];
 275→						}
 276→						break;
 277→					}
 278→
 279→					case "context.folders": {
 280→						const data = (event as IContextFoldersEvent).data;
 281→						if (data.status === "started") {
 282→							newState.logs = [
 283→								...prev.logs,
 284→								{
 285→									timestamp: new Date(),
 286→									type: "info",
 287→									message: "Analyzing directory structure...",
 288→								},
 289→							];
 290→						} else if (data.status === "completed") {
 291→							newState.logs = [
 292→								...prev.logs,
 293→								{
 294→									timestamp: new Date(),
 295→									type: "success",
 296→									message: `Structure: ${data.folderCount || "?"} key directories`,
 297→								},
 298→							];
 299→						}
 300→						break;
 301→					}
 302→
 303→					case "context.analysis": {
 304→						const data = (event as IContextAnalysisEvent).data;
 305→						if (data.status === "started") {
 306→							newState.logs = [
 307→								...prev.logs,
 308→								{
 309→									timestamp: new Date(),
 310→									type: "info",
 311→									message: "Identifying project context...",
 312→								},
 313→							];
 314→						} else if (data.status === "completed") {
 315→							newState.logs = [
 316→								...prev.logs,
 317→								{
 318→									timestamp: new Date(),
 319→									type: "success",
 320→									message: "Project context identified",
 321→								},
 322→							];
 323→						}
 324→						break;
 325→					}
 326→
 327→					case "context.warning": {
 328→						const data = (event as IContextWarningEvent).data;
 329→						newState.logs = [
 330→							...prev.logs,
 331→							{
 332→								timestamp: new Date(),
 333→								type: "warning",
 334→								message: data.message,
 335→							},
 336→						];
 337→						break;
 338→					}
 339→
 340→					case "job.started": {
 341→						const data = (event as IJobStartedEvent).data;
 342→						newState.status = "running";
 343→						newState.totalFiles = data.totalFiles || 0;
 344→						newState.logs = [
 345→							...prev.logs,
 346→							{
 347→								timestamp: new Date(),
 348→								type: "info",
 349→								message: `Started evaluation (${data.evaluationMode} mode, ${data.totalFiles} file(s))`,
 350→							},
 351→						];
 352→						break;
 353→					}
 354→
 355→					case "file.started": {
 356→						const data = (event as IFileStartedEvent).data;
 357→						newState.currentFile = data.filePath;
 358→						newState.logs = [
 359→							...prev.logs,
 360→							{
 361→								timestamp: new Date(),
 362→								type: "info",
 363→								message: `Processing ${data.filePath}`,
 364→							},
 365→						];
 366→						break;
 367→					}
 368→
 369→					case "file.completed": {
 370→						newState.completedFiles = (prev.completedFiles || 0) + 1;
 371→						// Update percentage based on file progress
 372→						if (newState.totalFiles > 0) {
 373→							newState.percentage = Math.round(
 374→								(newState.completedFiles / newState.totalFiles) * 50,
 375→							);
 376→						}
 377→						break;
 378→					}
 379→
 380→					case "evaluator.progress": {
 381→						const data = (event as IEvaluatorProgressEvent).data;
 382→						newState.currentEvaluator = data.evaluatorName;
 383→						newState.completedEvaluators = data.evaluatorIndex;
 384→						newState.totalEvaluators = data.totalEvaluators;
 385→						// Update percentage based on evaluator progress
 386→						if (data.totalEvaluators > 0) {
 387→							const evaluatorProgress =
 388→								(data.evaluatorIndex / data.totalEvaluators) * 50;
 389→							newState.percentage = 50 + Math.round(evaluatorProgress);
 390→						}
 391→						// Include file context in log message when evaluating multiple files
 392→						const fileContext = data.currentFile
 393→							? ` on ${data.currentFile.split("/").pop()}`
 394→							: "";
 395→						newState.logs = [
 396→							...prev.logs,
 397→							{
 398→								timestamp: new Date(),
 399→								type: "info",
 400→								message: `Running ${data.evaluatorName}${fileContext} (${data.evaluatorIndex + 1}/${data.totalEvaluators})`,
 401→							},
 402→						];
 403→						break;
 404→					}
 405→
 406→					case "evaluator.completed": {
 407→						newState.completedEvaluators = (prev.completedEvaluators || 0) + 1;
 408→						break;
 409→					}
 410→
 411→					case "evaluator.retry": {
 412→						const data = event.data as {
 413→							evaluatorName: string;
 414→							attempt: number;
 415→							maxRetries: number;
 416→							error: string;
 417→							currentFile?: string;
 418→						};
 419→						const retriesRemaining = data.maxRetries - data.attempt;
 420→						newState.logs = [
 421→							...prev.logs,
 422→							{
 423→								timestamp: new Date(),
 424→								type: "warning",
 425→								message: `Retry ${data.attempt}/${data.maxRetries} for ${data.evaluatorName}: ${data.error.substring(0, 100)}${data.error.length > 100 ? "..." : ""} (${retriesRemaining} retries remaining)`,
 426→							},
 427→						];
 428→						break;
 429→					}
 430→
 431→					case "evaluator.timeout": {
 432→						const data = event.data as {
 433→							evaluatorName: string;
 434→							elapsedMs: number;
 435→							timeoutMs: number;
 436→							currentFile?: string;
 437→						};
 438→						const elapsedSec = Math.round(data.elapsedMs / 1000);
 439→						const timeoutSec = Math.round(data.timeoutMs / 1000);
 440→						newState.logs = [
 441→							...prev.logs,
 442→							{
 443→								timestamp: new Date(),
 444→								type: "error",
 445→								message: `Timeout: ${data.evaluatorName} exceeded ${timeoutSec}s limit (elapsed: ${elapsedSec}s)`,
 446→							},
 447→						];
 448→						break;
 449→					}
 450→
 451→					case "curation.started": {
 452→						const data = event.data;
 453→						const typeLabel =
 454→							data.issueType === "error"
 455→								? "errors"
 456→								: data.issueType === "suggestion"
 457→									? "suggestions"
 458→									: "issues";
 459→						newState.currentEvaluator = `Impact Curation (${typeLabel})`;
 460→						newState.logs = [
 461→							...prev.logs,
 462→							{
 463→								timestamp: new Date(),
 464→								type: "info",
 465→								message: `Curating top ${typeLabel} from ${data.totalIssues} total...`,
 466→							},
 467→						];
 468→						break;
 469→					}
 470→
 471→					case "curation.completed": {
 472→						const data = event.data;
 473→						const typeLabel =
 474→							data.issueType === "error"
 475→								? "errors"
 476→								: data.issueType === "suggestion"
 477→									? "suggestions"
 478→									: "issues";
 479→						newState.logs = [
 480→							...prev.logs,
 481→							{
 482→								timestamp: new Date(),
 483→								type: "success",
 484→								message: `Impact curation completed for ${typeLabel} (${data.curatedCount} selected)`,
 485→							},
 486→						];
 487→						break;
 488→					}
 489→
 490→					case "evaluation.warning": {
 491→						const data = event.data;
 492→						newState.logs = [
 493→							...prev.logs,
 494→							{
 495→								timestamp: new Date(),
 496→								type: "warning",
 497→								message: data.message || "Some evaluators encountered errors",
 498→							},
 499→						];
 500→						break;
 501→					}
 502→
 503→					case "job.completed": {
 504→						const data = (event as IJobCompletedEvent).data;
 505→						newState.status = "completed";
 506→						newState.percentage = 100;
 507→						newState.logs = [
 508→							...prev.logs,
 509→							{
 510→								timestamp: new Date(),
 511→								type: "success",
 512→								message: `Evaluation completed in ${Math.round(data.duration / 1000)}s`,
 513→							},
 514→						];
 515→						// Preserve logs for display in Summary after completion
 516→						setEvaluationLogs(newState.logs);
 517→						// Set the evaluation data
 518→						setEvaluationData(data.result);
 519→						setEvaluationMode("completed");
 520→						// Reset job state after a short delay (user is already at /evaluation/:jobId)
 521→						// Clear any existing timeout first
 522→						if (completionTimeoutRef.current) {
 523→							clearTimeout(completionTimeoutRef.current);
 524→							completionTimeoutRef.current = null;
 525→						}
 526→						completionTimeoutRef.current = setTimeout(() => {
 527→							setCurrentJobId(null);
 528→							setSSEUrl(null);
 529→							setProgressState(null);
 530→						}, 100);
 531→						// Refresh history to show new evaluation
 532→						historyApi.refresh();
 533→						break;
 534→					}
 535→
 536→					case "job.failed": {
 537→						const data = (event as IJobFailedEvent).data;
 538→						newState.status = "failed";
 539→						newState.errorMessage = data.error?.message || "Unknown error";
 540→						newState.errorCode = data.error?.code;
 541→						newState.logs = [
 542→							...prev.logs,
 543→							{
 544→								timestamp: new Date(),
 545→								type: "error",
 546→								message: `Evaluation failed: ${data.error?.message || "Unknown error"}`,
 547→							},
 548→						];
 549→						// Keep evaluationMode as 'evaluating' so ProgressPanel stays visible
 550→						// User can dismiss manually with the "Try Again" button
 551→						// Refresh history to show failed evaluation
 552→						historyApi.refresh();
 553→						break;
 554→					}
 555→				}
 556→
 557→				// Keep only last 50 logs
 558→				if (newState.logs.length > 50) {
 559→					newState.logs = newState.logs.slice(-50);
 560→				}
 561→
 562→				return newState;
 563→			});
 564→		},
 565→		[historyApi],
 566→	);
 567→
 568→	// SSE connection
 569→	useSSE({
 570→		url: sseUrl,
 571→		onMessage: handleProgressEvent,
 572→		onOpen: () => {
 573→			setProgressState((prev) =>
 574→				prev
 575→					? {
 576→							...prev,
 577→							logs: [
 578→								...prev.logs,
 579→								{
 580→									timestamp: new Date(),
 581→									type: "info",
 582→									message: "Connected to progress stream",
 583→								},
 584→							],
 585→						}
 586→					: prev,
 587→			);
 588→		},
 589→	});
