 591→	// Cleanup completion timeout on unmount
 592→	useEffect(() => {
 593→		return () => {
 594→			if (completionTimeoutRef.current) {
 595→				clearTimeout(completionTimeoutRef.current);
 596→				completionTimeoutRef.current = null;
 597→			}
 598→		};
 599→	}, []);
 600→
 601→	// Handle URL submission
 602→	const handleUrlSubmit = useCallback(
 603→		async (
 604→			url: string,
 605→			evaluators: number,
 606→			provider?: ProviderName,
 607→			evaluatorFilter?: EvaluatorFilter,
