 639→					),
 640→					completedEvaluators: 0,
 641→					percentage: 0,
 642→					startTime: new Date(),
 643→					logs: [
 644→						{
 645→							timestamp: new Date(),
 646→							type: "info",
 647→							message: "Evaluation job submitted",
 648→						},
 649→					],
 650→				});
 651→
 652→				// Navigate immediately to the evaluation URL
 653→				navigate(`/evaluation/${response.jobId}`);
 654→			} catch (error) {
 655→				setEvaluationMode("idle");
 656→				setApiError(
 657→					error instanceof Error
 658→						? error.message
 659→						: "Failed to submit evaluation job",
 660→				);
 661→			}
 662→		},
 663→		[api, navigate],
 664→	);
 665→
 666→	// Handle batch URL submission
 667→	const handleBatchSubmit = useCallback(
 668→		async (
 669→			urls: string[],
 670→			_evaluators: number,
 671→			provider?: ProviderName,
 672→			evaluatorFilter?: EvaluatorFilter,
 673→			concurrency?: number,
 674→			selectedEvaluators?: string[],
 675→		) => {
 676→			setApiError(null);
 677→			try {
 678→				const response = await api.submitBatch(
