 718→	// Load evaluation from URL on mount or when URL changes
 719→	// Handles both active jobs (reconnect SSE) and completed evaluations (load from database)
 720→	// biome-ignore lint/correctness/useExhaustiveDependencies: handleFileLoad is intentionally excluded - we want the latest version but don't want it to trigger re-runs
 721→	useEffect(() => {
 722→		if (!urlEvaluationId) return;
 723→		if (evaluationData && evaluationMode === "completed") return; // Already loaded
 724→		if (evaluationMode === "evaluating") return; // Currently evaluating
 725→
 726→		const loadEvaluationById = async () => {
 727→			setIsLoadingFromUrl(true);
 728→			setApiError(null);
 729→
 730→			try {
 731→				// Step 1: Check if it's an active job
 732→				const jobStatus = await api.getJobStatus(urlEvaluationId);
 733→
 734→				if (jobStatus.status === "completed" && jobStatus.result) {
 735→					// Job completed, result in memory - use it directly
 736→					handleFileLoad(jobStatus.result);
 737→					setCurrentRepositoryUrl(jobStatus.repositoryUrl || null);
 738→					setEvaluationMode("completed");
 739→					// Load feedback, bookmarks, and selections
 740→					try {
 741→						const feedback =
 742→							await feedbackApi.getFeedbackForEvaluation(urlEvaluationId);
 743→						setFeedbackMap(feedback);
 744→						if (!cloudMode) {
 745→							const bookmarks =
 746→								await bookmarkApi.getBookmarksForEvaluation(urlEvaluationId);
 747→							setBookmarkSet(bookmarks);
 748→						}
 749→						const selections =
 750→							await selectionApi.getSelectionsForEvaluation(urlEvaluationId);
 751→						setSelectedIssueKeys(selections);
 752→					} catch (err) {
 753→						console.error("Failed to load metadata:", err);
 754→					}
 755→					setIsLoadingFromUrl(false);
 756→					return;
 757→				}
 758→
 759→				if (jobStatus.status === "failed") {
 760→					setApiError(jobStatus.error?.message || "Evaluation failed");
 761→					setIsLoadingFromUrl(false);
 762→					return;
 763→				}
