  34→/**
  35→ * Transform a remediation API response (which may be an in-memory job or DB record)
  36→ * into the frontend RemediationResult shape.
  37→ */
  38→function toRemediationResult(
  39→	data: RemediationForEvaluationResponse,
  40→): RemediationResult | null {
  41→	// In-memory job wraps result under `result`
  42→	if (data.result) {
  43→		return data.result;
  44→	}
  45→
  46→	// DB record has flat fields
  47→	if (data.fileChanges !== undefined) {
  48→		return {
  49→			fullPatch: data.fullPatch ?? "",
  50→			fileChanges: data.fileChanges ?? [],
  51→			totalAdditions: data.totalAdditions ?? 0,
  52→			totalDeletions: data.totalDeletions ?? 0,
  53→			filesChanged: data.filesChanged ?? 0,
  54→			totalDurationMs: data.totalDurationMs ?? 0,
  55→			totalCostUsd: data.totalCostUsd ?? 0,
  56→			totalInputTokens: data.totalInputTokens ?? 0,
  57→			totalOutputTokens: data.totalOutputTokens ?? 0,
  58→			summary: data.summary ?? undefined,
  59→			errorFixStats: data.promptStats?.errorFixStats,
  60→			suggestionEnrichStats: data.promptStats?.suggestionEnrichStats,
  61→		};
  62→	}
  63→
  64→	return null;
  65→}
  66→
  67→type Phase = "config" | "progress" | "results";
  68→
  69→interface RemediateTabProps {
  70→	evaluationId: string | null;
  71→	evaluationData: EvaluationOutput | null;
  72→	selectedIssueKeys: Set<string>;
  73→	issueKeyMap: Map<string, Issue>;
  74→	onRemoveIssue: (key: string) => void;
  75→	onClearAll: () => void;
  76→	cloudMode?: boolean;
  77→}
  78→
  79→export function RemediateTab({
  80→	evaluationId,
  81→	evaluationData,
  82→	selectedIssueKeys,
  83→	issueKeyMap,
  84→	onRemoveIssue,
  85→	onClearAll,
  86→	cloudMode = false,
  87→}: RemediateTabProps) {
  88→	const api = useEvaluationApi();
  89→	const providerDetection = useProviderDetection();
  90→
  91→	const [phase, setPhase] = useState<Phase>("config");
  92→	const [targetFileType, setTargetFileType] = useState<
  93→		"AGENTS.md" | "CLAUDE.md"
  94→	>("AGENTS.md");
  95→	const [selectedProvider, setSelectedProvider] =
  96→		useState<ProviderName>("claude");
  97→	const [isExecuting, setIsExecuting] = useState(false);
  98→	const [showConfirmModal, setShowConfirmModal] = useState(false);
  99→	const [showDeleteModal, setShowDeleteModal] = useState(false);
 100→	const [isDeleting, setIsDeleting] = useState(false);
 101→	const [executeError, setExecuteError] = useState<string | null>(null);
 102→
 103→	// SSE state
 104→	const [sseUrl, setSseUrl] = useState<string | null>(null);
 105→	const [remediationId, setRemediationId] = useState<string | null>(null);
 106→	const [progressState, setProgressState] = useState<IRemediationProgressState>(
 107→		{
 108→			status: "queued",
 109→			logs: [],
 110→		},
 111→	);
 112→
 113→	// Results state
 114→	const [result, setResult] = useState<RemediationResult | null>(null);
 115→
 116→	// Legacy prompt generation state
 117→	const [isGenerating, setIsGenerating] = useState(false);
 118→	const [generateError, setGenerateError] = useState<string | null>(null);
 119→	const [prompts, setPrompts] = useState<RemediationPromptsResponse | null>(
 120→		null,
 121→	);
 122→
 123→	// Detect providers on mount
 124→	useEffect(() => {
 125→		if (providerDetection.status === "idle") {
 126→			providerDetection.detectProviders();
 127→		}
 128→	}, [providerDetection]);
 129→
 130→	// Restore existing remediation on mount / evaluationId change
 131→	useEffect(() => {
 132→		if (!evaluationId) return;
 133→
 134→		let cancelled = false;
 135→
 136→		async function loadExistingRemediation() {
 137→			const data = await api.getRemediationForEvaluation(evaluationId!);
 138→			if (cancelled || !data) return;
 139→
 140→			if (data.status === "completed") {
 141→				const restoredResult = toRemediationResult(data);
 142→				if (restoredResult) {
 143→					setRemediationId(data.id);
 144→					setResult(restoredResult);
 145→					setPhase("results");
 146→				}
 147→			} else if (data.status === "running" || data.status === "queued") {
 148→				setRemediationId(data.id);
 149→				setSseUrl(
 150→					`${window.location.origin}/api/remediation/${data.id}/progress`,
 151→				);
 152→				setProgressState({
 153→					status: data.status as "queued" | "running",
 154→					logs: [],
 155→				});
 156→				setPhase("progress");
 157→			}
 158→			// "failed" or anything else: stay in config phase
 159→		}
 160→
 161→		loadExistingRemediation();
 162→
 163→		return () => {
 164→			cancelled = true;
 165→		};
 166→	}, [evaluationId, api]);
 167→
 168→	// Clear selection basket when remediation completes (fresh or restored)
 169→	useEffect(() => {
 170→		if (phase === "results") {
 171→			onClearAll();
 172→		}
 173→	}, [phase, onClearAll]);
 174→
 175→	// Available providers for the selector
 176→	const availableProviders = useMemo(() => {
 177→		const providers: ProviderStatus[] = [];
 178→		for (const p of providerDetection.providers.values()) {
 179→			if (p.available && p.name !== "random") {
 180→				providers.push(p);
 181→			}
 182→		}
 183→		return providers;
 184→	}, [providerDetection.providers]);
 185→
 186→	// Resolve selected issue keys to actual issues, split by type
 187→	const { errorEntries, suggestionEntries } = useMemo(() => {
 188→		const errors: { key: string; issue: Issue }[] = [];
 189→		const suggestions: { key: string; issue: Issue }[] = [];
 190→
 191→		for (const key of selectedIssueKeys) {
 192→			const issue = issueKeyMap.get(key);
 193→			if (!issue) continue;
 194→			const type = getIssueType(issue);
 195→			if (type === "error") {
 196→				errors.push({ key, issue });
 197→			} else {
 198→				suggestions.push({ key, issue });
 199→			}
 200→		}
 201→
 202→		errors.sort(
 203→			(a, b) => getIssueSeverity(b.issue) - getIssueSeverity(a.issue),
 204→		);
 205→		const impactOrder: Record<string, number> = {
 206→			High: 0,
 207→			Medium: 1,
 208→			Low: 2,
 209→		};
 210→		suggestions.sort(
 211→			(a, b) =>
 212→				(impactOrder[a.issue.impactLevel ?? "Low"] ?? 2) -
 213→				(impactOrder[b.issue.impactLevel ?? "Low"] ?? 2),
 214→		);
 215→
 216→		return { errorEntries: errors, suggestionEntries: suggestions };
 217→	}, [selectedIssueKeys, issueKeyMap]);
 218→
 219→	const totalSelected = errorEntries.length + suggestionEntries.length;
 220→
 221→	// SSE message handler
 222→	const handleSSEMessage = useCallback(
 223→		// biome-ignore lint/suspicious/noExplicitAny: SSE event data varies by type
 224→		(event: any) => {
 225→			const timestamp = new Date().toISOString();
 226→
 227→			switch (event.type) {
 228→				case "remediation.started":
 229→					setProgressState((prev) => ({
 230→						...prev,
 231→						status: "running",
 232→						logs: [...prev.logs, { timestamp, message: "Remediation started" }],
 233→					}));
 234→					break;
 235→
 236→				case "remediation.progress": {
 237→					const d = event.data ?? {};
 238→					setProgressState((prev) => ({
 239→						...prev,
 240→						...(d.errorCount !== undefined && {
 241→							errorCount: d.errorCount as number,
 242→						}),
 243→						...(d.suggestionCount !== undefined && {
 244→							suggestionCount: d.suggestionCount as number,
 245→						}),
 246→						...(d.totalBatches !== undefined && {
 247→							totalBatches: d.totalBatches as number,
 248→						}),
 249→						...(d.completedBatches !== undefined && {
 250→							completedBatches: d.completedBatches as number,
 251→						}),
 252→						...(d.phase !== undefined && {
 253→							currentPhase: d.phase as "errors" | "suggestions",
 254→						}),
 255→						...(d.runningTotalCostUsd !== undefined && {
 256→							runningTotalCostUsd: d.runningTotalCostUsd as number,
 257→						}),
 258→						...(d.runningTotalDurationMs !== undefined && {
 259→							runningTotalDurationMs: d.runningTotalDurationMs as number,
 260→						}),
 261→						...(d.runningTotalInputTokens !== undefined && {
 262→							runningTotalInputTokens: d.runningTotalInputTokens as number,
 263→						}),
 264→						...(d.runningTotalOutputTokens !== undefined && {
 265→							runningTotalOutputTokens: d.runningTotalOutputTokens as number,
 266→						}),
 267→					}));
 268→					break;
 269→				}
 270→
 271→				case "remediation.step.started": {
 272→					const bi = event.data?.batchInfo as
 273→						| { batchNumber: number; totalBatches: number }
 274→						| undefined;
 275→					const batchLabel = bi
 276→						? ` (batch ${bi.batchNumber}/${bi.totalBatches})`
 277→						: "";
 278→					const issueSummaries = event.data?.issuesSummary as
 279→						| string[]
 280→						| undefined;
 281→					setProgressState((prev) => ({
 282→						...prev,
 283→						currentStep: event.data?.step,
 284→						batchInfo: bi,
 285→						...(issueSummaries && { currentBatchIssues: issueSummaries }),
 286→						logs: [
 287→							...prev.logs,
 288→							{
 289→								timestamp,
 290→								message: `Step started: ${event.data?.step}${batchLabel}`,
 291→							},
 292→						],
 293→					}));
 294→					break;
 295→				}
 296→
 297→				case "remediation.step.completed": {
 298→					const bi2 = event.data?.batchInfo as
 299→						| { batchNumber: number; totalBatches: number }
 300→						| undefined;
 301→					const batchLabel2 = bi2
 302→						? ` (batch ${bi2.batchNumber}/${bi2.totalBatches})`
 303→						: "";
 304→					const step = event.data?.step as string | undefined;
 305→					const batchDurationMs = event.data?.batchDurationMs as
 306→						| number
 307→						| undefined;
 308→					const batchCostUsd = event.data?.batchCostUsd as number | undefined;
 309→					const batchInputTokens = event.data?.batchInputTokens as
 310→						| number
 311→						| undefined;
 312→					const batchOutputTokens = event.data?.batchOutputTokens as
 313→						| number
 314→						| undefined;
 315→
 316→					const hasBatchStats =
 317→						batchDurationMs !== undefined && batchCostUsd !== undefined;
 318→					let logMessage = `Step completed: ${step}${batchLabel2}`;
 319→					if (hasBatchStats) {
 320→						const phaseName =
 321→							step === "executing_error_fix"
 322→								? "Error fix"
 323→								: "Suggestion enrich";
 324→						const label = bi2
 325→							? `${phaseName} batch ${bi2.batchNumber}/${bi2.totalBatches}`
 326→							: phaseName;
 327→						logMessage = `${label} completed: ${Math.round(batchDurationMs / 1000)}s, $${batchCostUsd.toFixed(4)}`;
 328→					}
 329→
 330→					setProgressState((prev) => ({
 331→						...prev,
 332→						batchInfo: undefined,
 333→						currentBatchIssues: undefined,
 334→						...(hasBatchStats && {
 335→							lastBatchStats: {
 336→								durationMs: batchDurationMs!,
 337→								costUsd: batchCostUsd!,
 338→								inputTokens: batchInputTokens ?? 0,
 339→								outputTokens: batchOutputTokens ?? 0,
 340→							},
 341→						}),
 342→						logs: [...prev.logs, { timestamp, message: logMessage }],
 343→					}));
 344→					break;
 345→				}
 346→
 347→				case "remediation.completed": {
 348→					const remResult = event.data?.result as RemediationResult | undefined;
 349→					setProgressState((prev) => ({
 350→						...prev,
 351→						status: "completed",
 352→						result: remResult,
 353→						logs: [
 354→							...prev.logs,
 355→							{ timestamp, message: "Remediation completed" },
 356→						],
 357→					}));
 358→					if (remResult) {
 359→						setResult(remResult);
 360→					}
 361→					setPhase("results");
 362→					setSseUrl(null);
 363→					break;
 364→				}
 365→
 366→				case "remediation.failed":
 367→					setProgressState((prev) => ({
 368→						...prev,
 369→						status: "failed",
 370→						error: event.data?.error,
 371→						logs: [
 372→							...prev.logs,
 373→							{
 374→								timestamp,
 375→								message: `Failed: ${event.data?.error?.message || "Unknown error"}`,
 376→							},
 377→						],
 378→					}));
 379→					setExecuteError(event.data?.error?.message || "Remediation failed");
 380→					setPhase("config");
 381→					setSseUrl(null);
 382→					break;
 383→			}
 384→		},
 385→		[],
 386→	);
 387→
 388→	useSSE({
 389→		url: sseUrl,
 390→		onMessage: handleSSEMessage,
 391→	});
