 935→function IssueSection({
 936→	title,
 937→	count,
 938→	entries,
 939→	onRemove,
 940→}: {
 941→	title: string;
 942→	count: number;
 943→	entries: { key: string; issue: Issue }[];
 944→	onRemove: (key: string) => void;
 945→}) {
 946→	return (
 947→		<div className="bg-slate-800/50 rounded-lg border border-slate-700/50 p-3">
 948→			<div className="flex items-center justify-between mb-2">
 949→				<span className="text-sm font-semibold text-slate-200">
 950→					{title} ({count})
 951→				</span>
 952→			</div>
 953→			<div className="space-y-1 max-h-48 overflow-y-auto custom-scrollbar">
 954→				{entries.map(({ key, issue }) => {
 955→					const numericSeverity = getIssueSeverity(issue);
 956→					const severityLevel = getSeverityLevel(numericSeverity);
 957→					const issueTitle =
 958→						issue.title || issue.problem || issue.description || issue.category;
 959→
 960→					return (
