1044→		const groups = new Map<string, RemediationAction[]>();
1045→		for (const action of allActions) {
1046→			const key = action.issueTitle ?? "Other";
1047→			const existing = groups.get(key);
1048→			if (existing) existing.push(action);
1049→			else groups.set(key, [action]);
