1093→													strokeLinecap="round"
1094→													strokeLinejoin="round"
1095→													strokeWidth={2}
1096→													d="M20 12H4"
1097→												/>
1098→											</svg>
1099→										</span>
1100→									) : (
1101→										<span className="text-green-400 flex-shrink-0 mt-0.5">
1102→											<svg
1103→												className="w-4 h-4"
1104→												fill="none"
1105→												viewBox="0 0 24 24"
1106→												stroke="currentColor"
1107→											>
1108→												<path
1109→													strokeLinecap="round"
1110→													strokeLinejoin="round"
1111→													strokeWidth={2}
1112→													d="M5 13l4 4L19 7"
1113→												/>
1114→											</svg>
1115→										</span>
1116→									)}
1117→									<span className="text-sm text-slate-300 flex-1">
