1135→/**
1136→ * Get action summaries relevant to a specific file path.
1137→ */
1138→function getFileSummaries(
1139→	filePath: string,
1140→	result: RemediationResult,
1141→): RemediationAction[] {
1142→	if (!result.summary?.parsed) return [];
1143→
1144→	const allActions = [
1145→		...result.summary.errorFixActions,
1146→		...result.summary.suggestionEnrichActions,
1147→	];
1148→
1149→	return allActions.filter(
1150→		(a) => a.file && a.status !== "skipped" && filePath.endsWith(a.file),
1151→	);
1152→}
