 1→/**
 2→ * Renders parsed unified diff with syntax-colored lines.
 3→ */
 4→
 5→import { useMemo } from "react";
 6→import { parseDiff } from "../lib/diff-parser";
 7→
 8→interface DiffViewerProps {
 9→	diff: string;
10→}
11→
12→export function DiffViewer({ diff }: DiffViewerProps) {
13→	const hunks = useMemo(() => parseDiff(diff), [diff]);
14→
15→	if (hunks.length === 0) {
16→		return (
17→			<div className="diff-viewer">
18→				<span className="text-slate-500 text-xs italic">No changes</span>
19→			</div>
20→		);
21→	}
22→
23→	return (
24→		<div className="diff-viewer">
25→			{hunks.map((hunk, hi) => (
26→				<div key={hi}>
27→					{hunk.lines.map((line, li) => {
28→						let lineClass = "diff-line";
29→						let prefixClass = "diff-line-prefix";
30→						let prefix = " ";
31→
32→						switch (line.type) {
33→							case "header":
34→								lineClass += " diff-line-header";
35→								return (
36→									<div key={li} className={lineClass}>
37→										<span className="diff-line-number" />
38→										<span className="diff-line-number" />
39→										<span className="diff-line-prefix" />
40→										<span className="diff-line-content">{line.content}</span>
41→									</div>
42→								);
43→							case "addition":
44→								lineClass += " diff-line-addition";
45→								prefixClass += " text-green-400";
46→								prefix = "+";
47→								break;
48→							case "deletion":
49→								lineClass += " diff-line-deletion";
50→								prefixClass += " text-red-400";
51→								prefix = "-";
52→								break;
53→							case "context":
54→								break;
55→						}
56→
57→						return (
58→							<div key={li} className={lineClass}>
59→								<span className="diff-line-number">
60→									{line.oldLineNumber ?? ""}
61→								</span>
62→								<span className="diff-line-number">
63→									{line.newLineNumber ?? ""}
64→								</span>
65→								<span className={prefixClass}>{prefix}</span>
66→								<span className="diff-line-content">{line.content}</span>
67→							</div>
68→						);
69→					})}
70→				</div>
71→			))}
72→		</div>
73→	);
74→}
