 1→import { useCallback, useMemo } from "react";
 2→
 3→/**
 4→ * Custom hook for selection API operations (remediation picks)
 5→ * Follows the same pattern as useBookmarkApi
 6→ */
 7→export function useSelectionApi() {
 8→	const addSelections = useCallback(
 9→		async (evaluationId: string, issueKeys: string[]): Promise<void> => {
10→			const response = await fetch("/api/selections", {
11→				method: "POST",
12→				headers: { "Content-Type": "application/json" },
13→				body: JSON.stringify({ evaluationId, issueKeys }),
14→			});
15→
16→			if (!response.ok) {
17→				throw new Error("Failed to add selections");
18→			}
19→		},
20→		[],
21→	);
22→
23→	const removeSelection = useCallback(
24→		async (evaluationId: string, issueKey: string): Promise<void> => {
25→			const response = await fetch(
26→				`/api/selections?evaluationId=${evaluationId}&issueKey=${encodeURIComponent(issueKey)}`,
27→				{ method: "DELETE" },
28→			);
29→
30→			if (!response.ok) {
31→				throw new Error("Failed to remove selection");
32→			}
33→		},
34→		[],
35→	);
36→
37→	const clearSelections = useCallback(
38→		async (evaluationId: string): Promise<void> => {
39→			const response = await fetch(
40→				`/api/selections/evaluation/${evaluationId}`,
41→				{ method: "DELETE" },
42→			);
43→
44→			if (!response.ok) {
45→				throw new Error("Failed to clear selections");
46→			}
47→		},
48→		[],
49→	);
50→
51→	const getSelectionsForEvaluation = useCallback(
52→		async (evaluationId: string): Promise<Set<string>> => {
53→			const response = await fetch(
54→				`/api/selections/evaluation/${evaluationId}`,
55→			);
56→
57→			if (!response.ok) {
58→				throw new Error("Failed to fetch selections");
59→			}
60→
61→			const { selections } = await response.json();
62→			return new Set(selections);
63→		},
64→		[],
65→	);
66→
67→	return useMemo(
68→		() => ({
69→			addSelections,
70→			removeSelection,
71→			clearSelections,
72→			getSelectionsForEvaluation,
73→		}),
74→		[
75→			addSelections,
76→			removeSelection,
77→			clearSelections,
78→			getSelectionsForEvaluation,
79→		],
80→	);
81→}
