380â†’// Severity helpers
381â†’// Thresholds: High (8-10), Medium (6-7), Low (5)
382â†’// Note: Evaluator prompts instruct AI to not report issues with severity â‰¤4
383â†’export function getSeverityLevel(severity: number): "high" | "medium" | "low" {
384â†’	if (severity >= 8) return "high";
385â†’	if (severity >= 6) return "medium";
386â†’	return "low";
387â†’}
