399â†’	if (severity >= 6) return "rgb(234, 179, 8)"; // yellow-500
400â†’	return "rgb(148, 163, 184)"; // slate-400
401â†’}
402â†’
403â†’export function getSeverityEmoji(severity: number): string {
404â†’	if (severity >= 8) return "ðŸŸ ";
405â†’	if (severity >= 6) return "ðŸŸ¡";
406â†’	return "âšª";
407â†’}
