418â†’	if (impactLevel === "High") return "severity-badge severity-high"; // ORANGE
419â†’	if (impactLevel === "Medium") return "severity-badge severity-medium"; // YELLOW
420â†’	return "severity-badge severity-low"; // GRAY
421â†’}
422â†’
423â†’// For sorting/filtering - convert impact to numeric value
424â†’// Updated to align with 3-level severity thresholds:
425â†’// High: 7-10, Medium: 5-6, Low: 1-4
426â†’export function impactToSeverity(
427â†’	impactLevel: "High" | "Medium" | "Low",
428â†’): number {
429â†’	if (impactLevel === "High") return 9; // Maps to high (7-10)
430â†’	if (impactLevel === "Medium") return 6; // Maps to medium (5-6)
431â†’	return 3; // Maps to low (1-4)
432â†’}
433â†’
434â†’// Helper to get sortable severity value from any issue
435â†’export function getIssueSeverity(issue: Issue): number {
436â†’	if (issue.issueType === "error") {
437â†’		return issue.severity;
438â†’	}
439â†’	return impactToSeverity(issue.impactLevel);
440â†’}
441â†’
442â†’// Parse evaluator result string to extract issues
443â†’export function parseEvaluatorResult(resultString: string): Issue[] {
444â†’	try {
445â†’		// First, try to parse as JSON directly
446â†’		const parsed = JSON.parse(resultString);
447â†’
448â†’		// Handle unified format: {perFileIssues: {...}, crossFileIssues: [...]}
449â†’		if (parsed && typeof parsed === "object" && !Array.isArray(parsed)) {
450â†’			const allIssues: Issue[] = [];
451â†’
452â†’			// Extract per-file issues
453â†’			if (parsed.perFileIssues && typeof parsed.perFileIssues === "object") {
454â†’				for (const fileIssues of Object.values(parsed.perFileIssues)) {
455â†’					if (Array.isArray(fileIssues)) {
456â†’						allIssues.push(...(fileIssues as Issue[]));
457â†’					}
458â†’				}
459â†’			}
460â†’
461â†’			// Extract cross-file issues
462â†’			if (Array.isArray(parsed.crossFileIssues)) {
463â†’				allIssues.push(...parsed.crossFileIssues);
464â†’			}
465â†’
466â†’			return allIssues;
467â†’		}
468â†’
469â†’		// Handle array format directly
470â†’		if (Array.isArray(parsed)) {
471â†’			return parsed as Issue[];
472â†’		}
473â†’
474â†’		return [];
475â†’	} catch {
476â†’		// If JSON parse fails, try to find JSON array in the result
477â†’		// The result might contain markdown or other text before the JSON array
478â†’		try {
479â†’			const jsonMatch = resultString.match(/\[[\s\S]*\]/);
480â†’			if (!jsonMatch) {
481â†’				return [];
482â†’			}
483â†’
484â†’			const issues = JSON.parse(jsonMatch[0]) as Issue[];
485â†’			return Array.isArray(issues) ? issues : [];
486â†’		} catch {
487â†’			return [];
488â†’		}
489â†’	}
490â†’}
491â†’
492â†’// Format location for display
493â†’export function formatLocation(location: Location | Location[]): string {
494â†’	if (Array.isArray(location)) {
495â†’		return location
496â†’			.map((loc) => {
497â†’				if (loc.file) {
498â†’					return `${loc.file}:${loc.start}-${loc.end}`;
499â†’				}
500â†’				return `Lines ${loc.start}-${loc.end}`;
501â†’			})
502â†’			.join(", ");
503â†’	} else {
504â†’		if (location.file) {
505â†’			return `${location.file}:${location.start}-${location.end}`;
506â†’		}
507â†’		return `Lines ${location.start}-${location.end}`;
508â†’	}
509â†’}
