541â†’// Issue type helpers
542â†’
543â†’/**
544â†’ * Evaluators categorized as "suggestions" (opportunities for improvement).
545â†’ * All other evaluators are categorized as "errors" (problems to fix).
546â†’ */
547â†’const SUGGESTION_EVALUATORS = new Set([
548â†’	"11-subdirectory-coverage",
549â†’	"12-context-gaps",
550â†’]);
551â†’
552â†’/**
553â†’ * Get the issue type for a given issue, with backward compatibility.
554â†’ * If the issue has an explicit issueType, use that.
555â†’ * Otherwise, infer from the evaluator name.
556â†’ */
557â†’export function getIssueType(issue: Issue): "error" | "suggestion" {
558â†’	// Use explicit issueType if available
559â†’	if (issue.issueType) {
560â†’		return issue.issueType;
561â†’	}
562â†’	// Infer from evaluator name for backward compatibility
563â†’	if (issue.evaluatorName && SUGGESTION_EVALUATORS.has(issue.evaluatorName)) {
564â†’		return "suggestion";
565â†’	}
566â†’	return "error";
567â†’}
568â†’
569â†’/**
570â†’ * Determines if an issue affects multiple files or has no specific file
