 33â†’	const [searchQuery, setSearchQuery] = React.useState("");
 34â†’	const [sortField, setSortField] = React.useState<
 35â†’		"date" | "name" | "grade" | "errors"
 36â†’	>("date");
 37â†’	const [sortDirection, setSortDirection] = React.useState<"asc" | "desc">(
 38â†’		"desc",
 39â†’	);
 40â†’
 41â†’	// Combined loading state
 42â†’	const isLoading = isLoadingHistory || isLoadingActive;
 43â†’
 44â†’	// Combined refresh function
 45â†’	const refresh = async () => {
 46â†’		await Promise.all([refreshHistory(), refreshActive()]);
 47â†’	};
 48â†’
 49â†’	// Filter history based on search query
 50â†’	const filteredHistory = history.filter((item) => {
 51â†’		if (!searchQuery.trim()) return true;
 52â†’		const repoName = extractRepoName(item.repositoryUrl).toLowerCase();
 53â†’		return repoName.includes(searchQuery.toLowerCase());
 54â†’	});
 55â†’
 56â†’	const handleSort = (field: "date" | "name" | "grade" | "errors") => {
 57â†’		if (sortField === field) {
 58â†’			setSortDirection((d) => (d === "asc" ? "desc" : "asc"));
 59â†’		} else {
 60â†’			setSortField(field);
 61â†’			const defaults: Record<string, "asc" | "desc"> = {
 62â†’				date: "desc",
 63â†’				name: "asc",
 64â†’				grade: "desc",
 65â†’				errors: "desc",
 66â†’			};
 67â†’			setSortDirection(defaults[field]);
 68â†’		}
 69â†’	};
 70â†’
 71â†’	const sortedHistory = React.useMemo(() => {
 72â†’		const sorted = [...filteredHistory].sort((a, b) => {
 73â†’			const dir = sortDirection === "asc" ? 1 : -1;
 74â†’
 75â†’			if (sortField === "date") {
 76â†’				return (
 77â†’					dir *
 78â†’					(new Date(a.completedAt).getTime() -
 79â†’						new Date(b.completedAt).getTime())
 80â†’				);
 81â†’			}
 82â†’
 83â†’			if (sortField === "name") {
 84â†’				const nameA = extractRepoName(a.repositoryUrl).toLowerCase();
 85â†’				const nameB = extractRepoName(b.repositoryUrl).toLowerCase();
 86â†’				const cmp = nameA.localeCompare(nameB);
 87â†’				if (cmp !== 0) return dir * cmp;
 88â†’				// Secondary sort: newest first
 89â†’				return (
 90â†’					new Date(b.completedAt).getTime() - new Date(a.completedAt).getTime()
 91â†’				);
 92â†’			}
 93â†’
 94â†’			if (sortField === "grade") {
 95â†’				// Failed evaluations and null scores always sort last
 96â†’				const scoreA =
 97â†’					a.status === "failed" || a.contextScore == null
 98â†’						? null
 99â†’						: a.contextScore;
100â†’				const scoreB =
101â†’					b.status === "failed" || b.contextScore == null
102â†’						? null
103â†’						: b.contextScore;
104â†’				if (scoreA == null && scoreB == null)
105â†’					return (
106â†’						new Date(b.completedAt).getTime() -
107â†’						new Date(a.completedAt).getTime()
108â†’					);
109â†’				if (scoreA == null) return 1;
110â†’				if (scoreB == null) return -1;
111â†’				const cmp = scoreA - scoreB;
112â†’				if (cmp !== 0) return dir * cmp;
113â†’				return (
114â†’					new Date(b.completedAt).getTime() - new Date(a.completedAt).getTime()
115â†’				);
116â†’			}
117â†’
118â†’			// errors
119â†’			const isFailed = (item: IEvaluationHistoryItem) =>
120â†’				item.status === "failed";
121â†’			if (isFailed(a) && isFailed(b))
122â†’				return (
123â†’					new Date(b.completedAt).getTime() - new Date(a.completedAt).getTime()
124â†’				);
125â†’			if (isFailed(a)) return 1;
126â†’			if (isFailed(b)) return -1;
127â†’			const cmp = a.totalIssues - b.totalIssues;
128â†’			if (cmp !== 0) return dir * cmp;
129â†’			return (
130â†’				new Date(b.completedAt).getTime() - new Date(a.completedAt).getTime()
131â†’			);
132â†’		});
133â†’		return sorted;
134â†’	}, [filteredHistory, sortField, sortDirection]);
