  1â†’/**
  2â†’ * Compact card for a single past remediation with expand-to-full-details.
  3â†’ */
  4â†’
  5â†’import { useMemo, useState } from "react";
  6â†’import type {
  7â†’	FileChange,
  8â†’	RemediationAction,
  9â†’	RemediationHistoryItem,
 10â†’	RemediationResult,
 11â†’} from "../types/remediation";
 12â†’import {
 13â†’	countPackmindArtifacts,
 14â†’	formatArtifactCount,
 15â†’} from "../utils/packmind-artifacts";
 16â†’import { DiffViewer } from "./DiffViewer";
 17â†’import { PatchDownload } from "./PatchDownload";
 18â†’import { PackmindLogo } from "./shared/PackmindLogo";
 19â†’import { PackmindProductTourModal } from "./shared/PackmindProductTourModal";
 20â†’
 21â†’export type ImpactEvalStatus = "idle" | "running" | "completed" | "failed";
 22â†’
 23â†’interface RemediationHistoryCardProps {
 24â†’	item: RemediationHistoryItem;
 25â†’	index: number;
 26â†’	total: number;
 27â†’	expanded: boolean;
 28â†’	onToggle: () => void;
 29â†’	onDelete: () => void;
 30â†’	onEvaluateImpact?: () => void;
 31â†’	cloudMode?: boolean;
 32â†’	parentScore?: number;
 33â†’	parentGrade?: string;
 34â†’	impactEvalStatus?: ImpactEvalStatus;
 35â†’	impactJobId?: string;
 36â†’	impactScore?: number;
 37â†’	impactGrade?: string;
 38â†’	hasRepoUrl?: boolean;
 39â†’}
 40â†’
 41â†’function hasPackmindArtifacts(actions: RemediationAction[]): boolean {
 42â†’	return actions.some(
 43â†’		(a) => a.outputType === "standard" || a.outputType === "skill",
 44â†’	);
 45â†’}
 46â†’
 47â†’function formatTimestamp(iso: string): string {
 48â†’	const d = new Date(iso);
 49â†’	return d.toLocaleString(undefined, {
 50â†’		month: "short",
 51â†’		day: "numeric",
 52â†’		hour: "2-digit",
 53â†’		minute: "2-digit",
 54â†’	});
 55â†’}
 56â†’
 57â†’export function RemediationHistoryCard({
 58â†’	item,
 59â†’	index,
 60â†’	total,
 61â†’	expanded,
 62â†’	onToggle,
 63â†’	onDelete,
 64â†’	onEvaluateImpact,
 65â†’	cloudMode = false,
 66â†’	parentScore,
 67â†’	impactEvalStatus = "idle",
 68â†’	impactJobId,
 69â†’	impactScore,
 70â†’	impactGrade,
 71â†’	hasRepoUrl = true,
 72â†’}: RemediationHistoryCardProps) {
 73â†’	const [showTourModal, setShowTourModal] = useState(false);
 74â†’	const label = `Remediation #${total - index}`;
 75â†’	const statLabel = `${item.errorCount} error${item.errorCount !== 1 ? "s" : ""}, ${item.suggestionCount} suggestion${item.suggestionCount !== 1 ? "s" : ""}`;
 76â†’	const isFailed = item.status === "failed";
 77â†’
 78â†’	const result: RemediationResult | null = useMemo(() => {
 79â†’		if (isFailed) return null;
 80â†’		return {
 81â†’			fullPatch: item.fullPatch ?? "",
 82â†’			fileChanges: item.fileChanges ?? [],
 83â†’			totalAdditions: item.totalAdditions,
 84â†’			totalDeletions: item.totalDeletions,
 85â†’			filesChanged: item.filesChanged,
 86â†’			totalDurationMs: item.totalDurationMs,
 87â†’			totalCostUsd: item.totalCostUsd,
 88â†’			totalInputTokens: item.totalInputTokens,
 89â†’			totalOutputTokens: item.totalOutputTokens,
 90â†’			summary: item.summary ?? undefined,
 91â†’			errorFixStats: item.promptStats?.errorFixStats,
 92â†’			suggestionEnrichStats: item.promptStats?.suggestionEnrichStats,
 93â†’			errorPlanStats: item.promptStats?.errorPlanStats,
 94â†’			suggestionPlanStats: item.promptStats?.suggestionPlanStats,
 95â†’		};
 96â†’	}, [item, isFailed]);
 97â†’
 98â†’	return (
 99â†’		<div className="bg-slate-800/50 rounded-lg border border-slate-700/50">
100â†’			{/* Collapsed header */}
101â†’			<button
102â†’				onClick={onToggle}
103â†’				className="w-full flex items-center gap-3 p-3 text-left"
104â†’			>
105â†’				<svg
106â†’					className={`w-4 h-4 text-slate-400 transition-transform flex-shrink-0 ${expanded ? "rotate-90" : ""}`}
107â†’					fill="none"
108â†’					viewBox="0 0 24 24"
109â†’					stroke="currentColor"
110â†’				>
111â†’					<path
112â†’						strokeLinecap="round"
113â†’						strokeLinejoin="round"
114â†’						strokeWidth={2}
115â†’						d="M9 5l7 7-7 7"
116â†’					/>
117â†’				</svg>
118â†’
119â†’				<div className="flex-1 min-w-0">
120â†’					<div className="flex items-center gap-2">
121â†’						<span className="text-sm font-medium text-slate-200">{label}</span>
122â†’						<span
123â†’							className={`w-2 h-2 rounded-full flex-shrink-0 ${isFailed ? "bg-red-400" : "bg-green-400"}`}
124â†’						/>
125â†’						<span className="text-xs text-slate-500">{statLabel}</span>
126â†’					</div>
127â†’					<div className="flex items-center gap-3 text-xs text-slate-500 mt-0.5">
128â†’						<span>{formatTimestamp(item.createdAt)}</span>
129â†’						{!isFailed && (
130â†’							<>
131â†’								<span>
132â†’									{item.filesChanged} file
133â†’									{item.filesChanged !== 1 ? "s" : ""}
134â†’								</span>
135â†’								<span>
136â†’									<span className="text-green-400">+{item.totalAdditions}</span>
137â†’									<span className="text-red-400 ml-1">
138â†’										-{item.totalDeletions}
139â†’									</span>
140â†’								</span>
141â†’								{item.totalDurationMs > 0 && (
142â†’									<span>{Math.round(item.totalDurationMs / 1000)}s</span>
143â†’								)}
144â†’								{item.totalCostUsd > 0 && (
145â†’									<span>${item.totalCostUsd.toFixed(4)}</span>
146â†’								)}
147â†’							</>
148â†’						)}
149â†’						{isFailed && <span className="text-red-400">Failed</span>}
150â†’					</div>
151â†’				</div>
152â†’
153â†’				{/* Quick actions (stop propagation so clicks don't toggle) */}
154â†’				<div
155â†’					className="flex items-center gap-2 flex-shrink-0"
156â†’					onClick={(e) => e.stopPropagation()}
157â†’					onKeyDown={(e) => {
158â†’						if (e.key === "Enter" || e.key === " ") e.stopPropagation();
159â†’					}}
160â†’				>
161â†’					{!isFailed && (
