379â†’interface UnifiedFileEntry {
380â†’	key: string;
381â†’	actions: RemediationAction[];
382â†’	fileChanges: FileChange[];
383â†’	totalAdditions: number;
384â†’	totalDeletions: number;
385â†’	displayStatus: "added" | "modified" | "deleted" | null;
386â†’}
387â†’
388â†’function buildUnifiedEntries(result: RemediationResult): {
389â†’	entries: UnifiedFileEntry[];
390â†’	skippedActions: RemediationAction[];
391â†’} {
392â†’	const allActions = result.summary?.parsed
393â†’		? [
394â†’				...result.summary.errorFixActions,
395â†’				...result.summary.suggestionEnrichActions,
396â†’			]
397â†’		: [];
398â†’
399â†’	const addressedActions = allActions.filter((a) => a.status !== "skipped");
400â†’	const skippedActions = allActions.filter((a) => a.status === "skipped");
401â†’
402â†’	// Group addressed actions by file key
403â†’	const groupMap = new Map<string, RemediationAction[]>();
404â†’	for (const action of addressedActions) {
405â†’		const key = action.file ?? "General changes";
406â†’		if (!groupMap.has(key)) groupMap.set(key, []);
407â†’		groupMap.get(key)?.push(action);
408â†’	}
409â†’
410â†’	// Track which fileChanges have been matched
411â†’	const matchedFilePaths = new Set<string>();
412â†’
413â†’	const entriesFromActions: UnifiedFileEntry[] = [...groupMap.keys()].map(
414â†’		(key) => {
415â†’			const filenames = key.split(" + ").map((f) => f.trim());
416â†’			const matched: FileChange[] = [];
417â†’			for (const filename of filenames) {
418â†’				for (const fc of result.fileChanges) {
419â†’					if (fc.path.endsWith(filename)) {
420â†’						matched.push(fc);
421â†’						matchedFilePaths.add(fc.path);
422â†’					}
423â†’				}
424â†’			}
425â†’			const totalAdditions = matched.reduce((s, fc) => s + fc.additions, 0);
426â†’			const totalDeletions = matched.reduce((s, fc) => s + fc.deletions, 0);
427â†’			const displayStatus = computeDisplayStatus(matched);
428â†’			return {
429â†’				key,
430â†’				actions: groupMap.get(key) ?? [],
431â†’				fileChanges: matched,
432â†’				totalAdditions,
433â†’				totalDeletions,
434â†’				displayStatus,
435â†’			};
436â†’		},
437â†’	);
438â†’
439â†’	// Add fileChanges not matched by any action group
440â†’	const unmatchedEntries: UnifiedFileEntry[] = result.fileChanges
441â†’		.filter((fc) => !matchedFilePaths.has(fc.path))
442â†’		.map((fc) => ({
443â†’			key: fc.path,
444â†’			actions: [],
445â†’			fileChanges: [fc],
446â†’			totalAdditions: fc.additions,
447â†’			totalDeletions: fc.deletions,
448â†’			displayStatus: fc.status,
449â†’		}));
450â†’
451â†’	// Sort: "General changes" last, then alphabetical
452â†’	const allEntries = [...entriesFromActions, ...unmatchedEntries].sort(
453â†’		(a, b) => {
454â†’			if (a.key === "General changes") return 1;
455â†’			if (b.key === "General changes") return -1;
456â†’			return a.key.localeCompare(b.key);
457â†’		},
458â†’	);
459â†’
460â†’	return { entries: allEntries, skippedActions };
461â†’}
462â†’
463â†’function computeDisplayStatus(
464â†’	fileChanges: FileChange[],
465â†’): "added" | "modified" | "deleted" | null {
466â†’	if (fileChanges.length === 0) return null;
467â†’	// Priority: modified > deleted > added
468â†’	if (fileChanges.some((fc) => fc.status === "modified")) return "modified";
469â†’	if (fileChanges.some((fc) => fc.status === "deleted")) return "deleted";
470â†’	return "added";
471â†’}
