525â†’								strokeLinecap="round"
526â†’								strokeLinejoin="round"
527â†’								strokeWidth={2}
528â†’								d="M9 5l7 7-7 7"
529â†’							/>
530â†’						</svg>
531â†’						Skipped ({skippedActions.length})
532â†’					</button>
533â†’					{skippedOpen && (
534â†’						<div className="space-y-1 pl-4 mt-1">
535â†’							{skippedActions.map((action) => (
536â†’								<div
537â†’									key={`${action.issueIndex}-${action.status}`}
538â†’									className="flex items-start gap-2 text-xs"
539â†’								>
540â†’									<span className="text-slate-500 mt-0.5">â€”</span>
541â†’									<span className="text-slate-500 flex-1">
542â†’										{action.summary}
543â†’									</span>
544â†’								</div>
545â†’							))}
546â†’						</div>
547â†’					)}
548â†’				</div>
549â†’			)}
550â†’		</div>
551â†’	);
552â†’}
