651â†’function ScoreComparison({
652â†’	before,
653â†’	after,
654â†’	afterGrade,
655â†’}: {
656â†’	before: number;
657â†’	after: number;
658â†’	afterGrade?: string;
659â†’}) {
660â†’	const delta = after - before;
661â†’	const deltaSign = delta > 0 ? "+" : "";
662â†’	const deltaColor =
663â†’		delta > 0
664â†’			? "text-green-400"
665â†’			: delta < 0
666â†’				? "text-red-400"
