674â†’			<span className="text-slate-200 font-medium">{after.toFixed(1)}</span>
675â†’			<span className={`font-medium ${deltaColor}`}>
676â†’				({deltaSign}
677â†’				{delta.toFixed(1)})
678â†’			</span>
