	metadata,
	repositoryUrl,
	actualIssueCount,
	actualPerFileIssueCount,
	actualCrossFileIssueCount,
	actualHighCount,
	actualMediumCount,
	actualLowCount,
	actualErrorCount,
	actualSuggestionCount,
