import React, { useState } from "react";
import { useFeatureFlags } from "../contexts/FeatureFlagContext";
import type { ProviderName } from "../hooks/useEvaluationApi";
import { extractRepoName, formatCost, formatDuration } from "../lib/formatters";
import type {
	ICuratedIssue,
	ICurationSummary,
	Metadata,
} from "../types/evaluation";
import { StatCard } from "./shared";
import { ContextScoreCard } from "./summary/ContextScoreCard";

/** Available AI providers */
const PROVIDERS: { name: ProviderName; displayName: string }[] = [
	{ name: "claude", displayName: "Claude Code" },
	{ name: "codex", displayName: "OpenAI Codex" },
	{ name: "opencode", displayName: "OpenCode" },
	{ name: "cursor", displayName: "Cursor Agent" },
	{ name: "github-copilot", displayName: "GitHub Copilot" },
];

// Log entry type for activity logs
interface LogEntry {
	timestamp: Date;
	type: string;
	message: string;
}

interface SummaryProps {
	metadata: Metadata;
	repositoryUrl?: string | null;
	actualIssueCount?: number;
	actualPerFileIssueCount?: number;
	actualCrossFileIssueCount?: number;
	actualHighCount?: number;
	actualMediumCount?: number;
	actualLowCount?: number;
	actualErrorCount?: number;
	actualSuggestionCount?: number;
	curationSummary?: ICurationSummary | null;
	curatedCount?: number;
	evaluationId?: string;
	onDelete?: () => void;
	onReRun?: (provider: ProviderName) => void;
	curation?: {
		errors?: {
			curatedIssues: ICuratedIssue[];
			summary: ICurationSummary;
		};
		suggestions?: {
			curatedIssues: ICuratedIssue[];
			summary: ICurationSummary;
		};
	};
	evaluationLogs?: LogEntry[];
	parentEvaluationId?: string;
	parentScore?: number;
	parentGrade?: string;
}

export const Summary: React.FC<SummaryProps> = ({
	metadata,
	repositoryUrl,
	actualIssueCount,
	actualPerFileIssueCount,
	actualCrossFileIssueCount,
	actualHighCount,
	actualMediumCount,
	actualLowCount,
	actualErrorCount,
	actualSuggestionCount,
	curationSummary: _curationSummary,
	curatedCount,
	evaluationId,
	onDelete,
	onReRun,
	curation,
	evaluationLogs,
	parentEvaluationId,
	parentScore,
	parentGrade,
}) => {
	const { cloudMode } = useFeatureFlags();
	const [showRawContext, setShowRawContext] = useState(false);
	const [copied, setCopied] = useState(false);
	const [showActivityLog, setShowActivityLog] = useState(false);
	const [reRunProvider, setReRunProvider] = useState<ProviderName>(
		metadata.agent === "random" ? "claude" : (metadata.agent as ProviderName),
	);
	const formattedDate = new Date(metadata.generatedAt).toLocaleString();
	const shareUrl = evaluationId
		? `${window.location.origin}/evaluation/${evaluationId}`
		: null;

