// Scrolls to top on every route change
function ScrollToTop() {
	const { pathname } = useLocation();
	// biome-ignore lint/correctness/useExhaustiveDependencies: re-run scroll on route change
	useEffect(() => {
		window.scrollTo(0, 0);
	}, [pathname]);
	return null;
}

// Router wrapper component
function App() {
	return (
		<FeatureFlagProvider>
			<BrowserRouter>
				<ScrollToTop />
				<AppRoutes />
			</BrowserRouter>
		</FeatureFlagProvider>
	);
}

