     1→/**
     2→ * Compact card for a single past remediation with expand-to-full-details.
     3→ */
     4→
     5→import { useMemo } from "react";
     6→import type {
     7→	FileChange,
     8→	RemediationAction,
     9→	RemediationHistoryItem,
    10→	RemediationResult,
    11→} from "../types/remediation";
    12→import { FileChangeCard } from "./FileChangeCard";
    13→import { PatchDownload } from "./PatchDownload";
    14→
    15→export type ImpactEvalStatus = "idle" | "running" | "completed" | "failed";
    16→
    17→interface RemediationHistoryCardProps {
    18→	item: RemediationHistoryItem;
    19→	index: number;
    20→	total: number;
    21→	expanded: boolean;
    22→	onToggle: () => void;
    23→	onDelete: () => void;
    24→	onEvaluateImpact?: () => void;
    25→	cloudMode?: boolean;
    26→	parentScore?: number;
    27→	parentGrade?: string;
    28→	impactEvalStatus?: ImpactEvalStatus;
    29→	impactScore?: number;
    30→	impactGrade?: string;
    31→	hasRepoUrl?: boolean;
    32→}
    33→
    34→function formatTimestamp(iso: string): string {
    35→	const d = new Date(iso);
    36→	return d.toLocaleString(undefined, {
    37→		month: "short",
    38→		day: "numeric",
    39→		hour: "2-digit",
    40→		minute: "2-digit",
    41→	});
    42→}
    43→
    44→export function RemediationHistoryCard({
    45→	item,
    46→	index,
    47→	total,
    48→	expanded,
    49→	onToggle,
    50→	onDelete,
    51→	onEvaluateImpact,
    52→	cloudMode = false,
    53→	parentScore,
    54→	impactEvalStatus = "idle",
    55→	impactScore,
    56→	impactGrade,
    57→	hasRepoUrl = true,
    58→}: RemediationHistoryCardProps) {
    59→	const label = `Remediation #${total - index}`;
    60→	const statLabel = `${item.errorCount} error${item.errorCount !== 1 ? "s" : ""}, ${item.suggestionCount} suggestion${item.suggestionCount !== 1 ? "s" : ""}`;
    61→	const isFailed = item.status === "failed";
    62→
    63→	const result: RemediationResult | null = useMemo(() => {
    64→		if (isFailed) return null;
    65→		return {
    66→			fullPatch: item.fullPatch ?? "",
    67→			fileChanges: item.fileChanges ?? [],
    68→			totalAdditions: item.totalAdditions,
    69→			totalDeletions: item.totalDeletions,
    70→			filesChanged: item.filesChanged,
    71→			totalDurationMs: item.totalDurationMs,
    72→			totalCostUsd: item.totalCostUsd,
    73→			totalInputTokens: item.totalInputTokens,
    74→			totalOutputTokens: item.totalOutputTokens,
    75→			summary: item.summary ?? undefined,
    76→			errorFixStats: item.promptStats?.errorFixStats,
    77→			suggestionEnrichStats: item.promptStats?.suggestionEnrichStats,
    78→		};
    79→	}, [item, isFailed]);
    80→
    81→	return (
    82→		<div className="bg-slate-800/50 rounded-lg border border-slate-700/50">
    83→			{/* Collapsed header */}
    84→			<button
    85→				onClick={onToggle}
    86→				className="w-full flex items-center gap-3 p-3 text-left"
    87→			>
    88→				<svg
    89→					className={`w-4 h-4 text-slate-400 transition-transform flex-shrink-0 ${expanded ? "rotate-90" : ""}`}
    90→					fill="none"
    91→					viewBox="0 0 24 24"
    92→					stroke="currentColor"
    93→				>
    94→					<path
    95→						strokeLinecap="round"
    96→						strokeLinejoin="round"
    97→						strokeWidth={2}
    98→						d="M9 5l7 7-7 7"
    99→					/>
   100→				</svg>
   101→
   102→				<div className="flex-1 min-w-0">
   103→					<div className="flex items-center gap-2">
   104→						<span className="text-sm font-medium text-slate-200">{label}</span>
   105→						<span
   106→							className={`w-2 h-2 rounded-full flex-shrink-0 ${isFailed ? "bg-red-400" : "bg-green-400"}`}
   107→						/>
   108→						<span className="text-xs text-slate-500">{statLabel}</span>
   109→					</div>
   110→					<div className="flex items-center gap-3 text-xs text-slate-500 mt-0.5">
   111→						<span>{formatTimestamp(item.createdAt)}</span>
   112→						{!isFailed && (
   113→							<>
   114→								<span>
   115→									{item.filesChanged} file
   116→									{item.filesChanged !== 1 ? "s" : ""}
   117→								</span>
   118→								<span>
   119→									<span className="text-green-400">+{item.totalAdditions}</span>
   120→									<span className="text-red-400 ml-1">
   121→										-{item.totalDeletions}
   122→									</span>
   123→								</span>
   124→								{item.totalDurationMs > 0 && (
   125→									<span>{Math.round(item.totalDurationMs / 1000)}s</span>
   126→								)}
   127→								{item.totalCostUsd > 0 && (
   128→									<span>${item.totalCostUsd.toFixed(4)}</span>
   129→								)}
   130→							</>
   131→						)}
   132→						{isFailed && <span className="text-red-400">Failed</span>}
   133→					</div>
   134→				</div>
   135→
   136→				{/* Quick actions (stop propagation so clicks don't toggle) */}
   137→				<div
   138→					className="flex items-center gap-2 flex-shrink-0"
   139→					onClick={(e) => e.stopPropagation()}
   140→					onKeyDown={(e) => {
   141→						if (e.key === "Enter" || e.key === " ") e.stopPropagation();
   142→					}}
   143→				>
   144→					{!isFailed && <PatchDownload remediationId={item.id} />}
   145→					{!cloudMode && (
   146→						<button
   147→							onClick={onDelete}
   148→							className="text-slate-500 hover:text-red-400 transition-colors p-1"
   149→							title="Delete remediation"
   150→						>
   151→							<svg
   152→								className="w-4 h-4"
   153→								fill="none"
   154→								viewBox="0 0 24 24"
   155→								stroke="currentColor"
   156→							>
   157→								<path
   158→									strokeLinecap="round"
   159→									strokeLinejoin="round"
   160→									strokeWidth={2}
   161→									d="M19 7l-.867 12.142A2 2 0 0116.138 21H7.862a2 2 0 01-1.995-1.858L5 7m5 4v6m4-6v6m1-10V4a1 1 0 00-1-1h-4a1 1 0 00-1 1v3M4 7h16"
   162→								/>
   163→							</svg>
   164→						</button>
   165→					)}
   166→				</div>
   167→			</button>
   168→
   169→			{/* Impact evaluation row */}
   170→			{!isFailed && (
   171→				<div className="px-3 pb-2 flex items-center gap-3">
   172→					{impactEvalStatus === "idle" &&
   173→						!item.resultEvaluationId &&
   174→						hasRepoUrl && (
   175→							<button
   176→								onClick={(e) => {
   177→									e.stopPropagation();
   178→									onEvaluateImpact?.();
   179→								}}
   180→								className="text-xs px-2.5 py-1 rounded bg-slate-700 text-slate-300 hover:bg-slate-600 hover:text-slate-100 transition-colors"
   181→							>
   182→								Evaluate Impact
   183→							</button>
   184→						)}
   185→					{impactEvalStatus === "running" && (
   186→						<span className="flex items-center gap-1.5 text-xs text-slate-400">
   187→							<svg
   188→								className="animate-spin h-3 w-3"
   189→								fill="none"
   190→								viewBox="0 0 24 24"
   191→							>
   192→								<circle
   193→									className="opacity-25"
   194→									cx="12"
   195→									cy="12"
   196→									r="10"
   197→									stroke="currentColor"
   198→									strokeWidth="4"
   199→								/>
   200→								<path
   201→									className="opacity-75"
   202→									fill="currentColor"
   203→									d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"
   204→								/>
   205→							</svg>
   206→							Evaluating impact...
   207→						</span>
   208→					)}
   209→					{impactEvalStatus === "failed" && (
   210→						<span className="text-xs text-red-400">
   211→							Impact evaluation failed
   212→						</span>
   213→					)}
   214→					{(impactEvalStatus === "completed" || item.resultEvaluationId) &&
   215→						impactScore !== undefined &&
   216→						parentScore !== undefined && (
   217→							<ScoreComparison
   218→								before={parentScore}
   219→								after={impactScore}
   220→								afterGrade={impactGrade}
   221→							/>
   222→						)}
   223→				</div>
   224→			)}
   225→
   226→			{/* Expanded details */}
   227→			{expanded && (
   228→				<div className="border-t border-slate-700/50 p-3 space-y-4">
   229→					{isFailed ? (
   230→						<div className="text-sm text-red-400">
   231→							{item.errorMessage || "Remediation failed"}
   232→						</div>
   233→					) : (
   234→						result && (
   235→							<>
   236→								{/* Action summary */}
   237→								{result.summary?.parsed && (
   238→									<CompactActionSummary summary={result.summary} />
   239→								)}
   240→
   241→								{/* File changes */}
   242→								{result.fileChanges.length === 0 ? (
   243→									<p className="text-sm text-slate-500">
   244→										No file changes were made.
   245→									</p>
   246→								) : (
   247→									<div className="space-y-2">
   248→										{result.fileChanges.map((file: FileChange) => {
   249→											const fileSummaries = getFileSummaries(file.path, result);
   250→											return (
   251→												<FileChangeCard
   252→													key={file.path}
   253→													file={file}
   254→													defaultExpanded={result.fileChanges.length <= 3}
   255→													summaries={fileSummaries}
   256→												/>
   257→											);
   258→										})}
   259→									</div>
   260→								)}
   261→							</>
   262→						)
   263→					)}
   264→				</div>
   265→			)}
   266→		</div>
   267→	);
   268→}
   269→
   270→function CompactActionSummary({
   271→	summary,
   272→}: {
   273→	summary: NonNullable<RemediationResult["summary"]>;
   274→}) {
   275→	const allActions = [
   276→		...summary.errorFixActions,
   277→		...summary.suggestionEnrichActions,
   278→	];
   279→
   280→	return (
   281→		<div className="bg-slate-900/30 rounded-lg p-3">
   282→			<div className="flex items-center justify-between mb-2">
   283→				<span className="text-xs font-semibold text-slate-300">
   284→					Action Summary
   285→				</span>
   286→				<span className="text-xs text-slate-500">
   287→					{summary.addressedCount} addressed
   288→					{summary.skippedCount > 0 && `, ${summary.skippedCount} skipped`}
   289→				</span>
   290→			</div>
   291→			<div className="space-y-1">
   292→				{allActions.map((action) => (
   293→					<div
   294→						key={`${action.issueIndex}-${action.status}`}
   295→						className="flex items-start gap-2 text-xs"
   296→					>
   297→						{action.status === "skipped" ? (
   298→							<span className="text-slate-500 mt-0.5">—</span>
   299→						) : (
   300→							<span className="text-green-400 mt-0.5">✓</span>
   301→						)}
   302→						<span className="text-slate-400 flex-1">{action.summary}</span>
   303→						{action.outputType && (
   304→							<span
   305→								className={`inline-flex items-center px-1.5 py-0.5 rounded text-xs font-medium flex-shrink-0 output-type-${action.outputType}`}
   306→							>
   307→								{action.outputType.charAt(0).toUpperCase() +
   308→									action.outputType.slice(1)}
   309→							</span>
   310→						)}
   311→						{action.file && (
   312→							<span className="text-slate-600 font-mono">{action.file}</span>
   313→						)}
   314→					</div>
   315→				))}
   316→			</div>
   317→		</div>
   318→	);
   319→}
   320→
   321→function ScoreComparison({
   322→	before,
   323→	after,
   324→	afterGrade,
   325→}: {
   326→	before: number;
   327→	after: number;
   328→	afterGrade?: string;
   329→}) {
   330→	const delta = after - before;
   331→	const deltaSign = delta > 0 ? "+" : "";
   332→	const deltaColor =
   333→		delta > 0
   334→			? "text-green-400"
   335→			: delta < 0
   336→				? "text-red-400"
   337→				: "text-slate-400";
   338→
   339→	return (
   340→		<div className="flex items-center gap-2 text-xs">
   341→			<span className="text-slate-400">Score:</span>
   342→			<span className="text-slate-300">{before.toFixed(1)}</span>
   343→			<span className="text-slate-500">&rarr;</span>
   344→			<span className="text-slate-200 font-medium">{after.toFixed(1)}</span>
   345→			<span className={`font-medium ${deltaColor}`}>
   346→				({deltaSign}
   347→				{delta.toFixed(1)})
   348→			</span>
   349→			{afterGrade && <span className="text-slate-500">{afterGrade}</span>}
   350→		</div>
   351→	);
   352→}
   353→
   354→function getFileSummaries(
   355→	filePath: string,
   356→	result: RemediationResult,
   357→): RemediationAction[] {
   358→	if (!result.summary?.parsed) return [];
   359→
   360→	const allActions = [
   361→		...result.summary.errorFixActions,
   362→		...result.summary.suggestionEnrichActions,
   363→	];
   364→
   365→	return allActions.filter(
   366→		(a) => a.file && a.status !== "skipped" && filePath.endsWith(a.file),
   367→	);
   368→}
   369→

