   324→					// Reset to idle, refresh history, clear selection
   325→					// Capture completed remediation ID for auto-expand
   326→					if (activeRemediationIdRef.current) {
   327→						setAutoExpandRemediationId(activeRemediationIdRef.current);
   328→					}
   329→					activeRemediationIdRef.current = null;
   330→					setActiveRemediationId(null);
   331→					setSseUrl(null);
   332→					setPhase("idle");
   333→					onClearAll();
   334→					refreshHistory();
   335→					break;
   336→				}
   337→
   338→				case "remediation.failed":
   339→					setProgressState((prev) => ({
   340→						...prev,
   341→						status: "failed",
   342→						error: event.data?.error,
   343→						logs: [
   344→							...prev.logs,
   345→							{
   346→								timestamp,
   347→								message: `Failed: ${event.data?.error?.message || "Unknown error"}`,
   348→							},
   349→						],
   350→					}));
   351→					setExecuteError(event.data?.error?.message || "Remediation failed");
   352→					setSseUrl(null);
   353→					activeRemediationIdRef.current = null;
   354→					setActiveRemediationId(null);
   355→					setPhase("idle");
   356→					refreshHistory();
   357→					break;
   358→			}
   359→		},
   360→		[onClearAll, refreshHistory],
   361→	);
   362→
   363→	useSSE({
   364→		url: sseUrl,
   365→		onMessage: handleSSEMessage,
   366→	});
   367→
   368→	// Execute remediation
   369→	const handleExecute = useCallback(async () => {
   370→		if (!evaluationId || totalSelected === 0) return;
   371→
   372→		setIsExecuting(true);
   373→		setExecuteError(null);
   374→		setPrompts(null);
   375→
   376→		try {
   377→			const issues = [
   378→				...errorEntries.map((e) => e.issue),
   379→				...suggestionEntries.map((e) => e.issue),
   380→			];
   381→
   382→			const response = await api.executeRemediation(
   383→				evaluationId,
   384→				issues,
   385→				targetAgent,
   386→				selectedProvider,
   387→			);
   388→
   389→			activeRemediationIdRef.current = response.remediationId;
   390→			setActiveRemediationId(response.remediationId);
   391→			setSseUrl(response.sseUrl);
   392→			setProgressState({ status: "queued", logs: [] });
   393→			setPhase("progress");
   394→		} catch (err) {
   395→			const message =
   396→				err instanceof Error ? err.message : "Failed to start remediation";
   397→			setExecuteError(message);
   398→		} finally {
   399→			setIsExecuting(false);
   400→		}
   401→	}, [
   402→		evaluationId,
   403→		totalSelected,
   404→		errorEntries,
   405→		suggestionEntries,
   406→		targetAgent,
   407→		selectedProvider,
   408→		api,
   409→	]);
   410→
   411→	// Generate prompts (legacy copy-paste mode)
   412→	const handleGeneratePrompts = useCallback(async () => {
   413→		if (!evaluationId || totalSelected === 0) return;
   414→
   415→		setIsGenerating(true);
   416→		setGenerateError(null);
   417→
   418→		try {
   419→			const issues = [
   420→				...errorEntries.map((e) => e.issue),
   421→				...suggestionEntries.map((e) => e.issue),
   422→			];
   423→			const result = await api.generateRemediationPrompts(
   424→				evaluationId,
   425→				issues,
   426→				targetAgent,
   427→			);
   428→			setPrompts(result);
   429→		} catch (err) {
   430→			setGenerateError(
   431→				err instanceof Error ? err.message : "Failed to generate prompts",
   432→			);
   433→		} finally {
   434→			setIsGenerating(false);
   435→		}
   436→	}, [
   437→		evaluationId,
   438→		totalSelected,
   439→		errorEntries,
   440→		suggestionEntries,
   441→		targetAgent,
   442→		api,
   443→	]);
   444→
   445→	// Delete a remediation from history
   446→	const handleDeleteRemediation = useCallback(
   447→		async (remediationId: string) => {
   448→			await api.deleteRemediation(remediationId);
   449→			setRemediations((prev) => prev.filter((r) => r.id !== remediationId));
   450→		},
   451→		[api],
   452→	);
   453→
   454→	if (!evaluationData) {
   455→		return (
   456→			<div className="card text-center py-12">
   457→				<p className="text-body-muted">
   458→					No evaluation data. Run an evaluation first to generate remediation
   459→					prompts.
   460→				</p>
   461→			</div>
   462→		);
   463→	}
   464→
   465→	// ─── PROGRESS PHASE ────────────────────────────────────────────────
   466→	if (phase === "progress") {
   467→		return (
   468→			<div className="space-y-6">
   469→				<div className="card">
   470→					<h2 className="text-heading text-slate-100">Executing Remediation</h2>
   471→					<p className="text-body-muted mt-1">
   472→						Running {selectedProvider} agent to fix{" "}
   473→						{totalSelected > 0 ? totalSelected : "selected"} issue
   474→						{totalSelected !== 1 ? "s" : ""}...
   475→					</p>
   476→				</div>
   477→				<RemediationProgress
   478→					currentStep={progressState.currentStep}
   479→					batchInfo={progressState.batchInfo}
   480→					logs={progressState.logs}
   481→					errorCount={progressState.errorCount}
   482→					suggestionCount={progressState.suggestionCount}
   483→					totalBatches={progressState.totalBatches}
   484→					completedBatches={progressState.completedBatches}
   485→					currentPhase={progressState.currentPhase}
   486→					runningTotalCostUsd={progressState.runningTotalCostUsd}
   487→					runningTotalDurationMs={progressState.runningTotalDurationMs}
   488→					runningTotalInputTokens={progressState.runningTotalInputTokens}
   489→					runningTotalOutputTokens={progressState.runningTotalOutputTokens}
   490→					currentBatchIssues={progressState.currentBatchIssues}
   491→				/>
   492→			</div>
   493→		);
   494→	}
   495→
   496→	// ─── IDLE PHASE (config + history) ─────────────────────────────────
   497→	return (
   498→		<div className="space-y-6">
   499→			<div className="card">
   500→				<h2 className="text-heading text-slate-100">Remediation</h2>
   501→				<p className="text-body-muted mt-1">
   502→					Execute remediation to fix issues automatically, or generate
   503→					copy-paste prompts
   504→				</p>
   505→			</div>
   506→
   507→			{/* Past Remediations History */}
   508→			<RemediationHistory
   509→				remediations={remediations}
   510→				onDelete={handleDeleteRemediation}
   511→				onRefresh={refreshHistory}
   512→				cloudMode={cloudMode}
   513→				autoExpandId={autoExpandRemediationId}
   514→				onAutoExpandHandled={() => setAutoExpandRemediationId(null)}
   515→				parentScore={evaluationData?.metadata?.contextScore?.score}
   516→				parentGrade={evaluationData?.metadata?.contextScore?.grade}
   517→				hasRepoUrl={(() => {
   518→					// repositoryUrl is present at runtime but not in frontend Metadata type
   519→					// biome-ignore lint/suspicious/noExplicitAny: runtime metadata field
   520→					const url = (evaluationData?.metadata as any)?.repositoryUrl as
   521→						| string
   522→						| undefined;
   523→					return (
   524→						!!url &&
   525→						url !== "unknown" &&
   526→						(url.startsWith("http") || url.startsWith("git@"))
   527→					);
   528→				})()}
   529→			/>
   530→
   531→			{/* Empty state when no issues selected */}
   532→			{totalSelected === 0 ? (
   533→				<div className="card text-center py-12">
   534→					<svg
   535→						className="w-12 h-12 text-slate-600 mx-auto mb-4"
   536→						fill="none"
   537→						stroke="currentColor"
   538→						viewBox="0 0 24 24"
   539→					>
   540→						<path
   541→							strokeLinecap="round"
   542→							strokeLinejoin="round"
   543→							strokeWidth={1.5}
   544→							d="M12 4v16m8-8H4"
   545→						/>
   546→					</svg>
   547→					<p className="text-body-muted">
   548→						No issues selected. Use the{" "}
   549→						<span className="inline-flex items-center justify-center w-5 h-5 rounded bg-slate-700 text-slate-300 text-xs align-middle mx-0.5">
   550→							+
   551→						</span>{" "}
   552→						button on issue cards in the Errors and Suggestions tabs.
   553→					</p>
   554→				</div>
   555→			) : (
   556→				<div className="card space-y-5">
   557→					{/* Target Agent */}
   558→					<div>
   559→						<label className="text-label text-slate-300 block mb-2">
   560→							Target for markdown file rendering
   561→						</label>
   562→						<div className="flex gap-2">
   563→							{(
   564→								[
   565→									["agents-md", "AGENTS.md"],
   566→									["claude-code", "Claude Code"],
   567→									["github-copilot", "GitHub Copilot"],
   568→									["cursor", "Cursor"],
   569→								] as const
   570→							).map(([value, label]) => (
   571→								<button
   572→									key={value}
   573→									onClick={() => setTargetAgent(value)}
   574→									className={`flex items-center gap-1.5 px-3 py-1.5 rounded text-sm transition-colors ${
   575→										targetAgent === value
