   637→						)}
   638→						{suggestionEntries.length > 0 && (
   639→							<IssueSection
   640→								title="Suggestions"
   641→								count={suggestionEntries.length}
   642→								entries={suggestionEntries}
   643→								onRemove={onRemoveIssue}
   644→							/>
   645→						)}
   646→						<div className="flex items-center justify-between">
