   657→						</div>
   658→					</div>
   659→
   660→					{/* Action Buttons */}
   661→					<div className="flex flex-wrap gap-3">
   662→						<button
   663→							onClick={() => setShowConfirmModal(true)}
   664→							disabled={isExecuting || isGenerating}
   665→							className="btn-primary"
   666→						>
   667→							{isExecuting ? (
   668→								<span className="flex items-center gap-2">
   669→									<svg
   670→										className="animate-spin h-4 w-4"
   671→										fill="none"
   672→										viewBox="0 0 24 24"
   673→									>
   674→										<circle
   675→											className="opacity-25"
   676→											cx="12"
   677→											cy="12"
   678→											r="10"
   679→											stroke="currentColor"
   680→											strokeWidth="4"
   681→										/>
   682→										<path
   683→											className="opacity-75"
   684→											fill="currentColor"
   685→											d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"
   686→										/>
   687→									</svg>
   688→									Starting...
   689→								</span>
   690→							) : (
   691→								`Execute Remediation (${totalSelected})`
   692→							)}
   693→						</button>
   694→						<button
   695→							onClick={handleGeneratePrompts}
   696→							disabled={isExecuting || isGenerating}
   697→							className="btn-secondary"
   698→						>
   699→							{isGenerating ? "Generating..." : "Generate Prompts"}
   700→						</button>
   701→					</div>
   702→
   703→					{executeError && (
   704→						<p className="text-sm text-red-400 mt-2">{executeError}</p>
