   706→					{generateError && (
   707→						<p className="text-sm text-red-400 mt-2">{generateError}</p>
   708→					)}
   709→				</div>
   710→			)}
   711→
   712→			{/* Confirmation Modal */}
   713→			<Modal
   714→				isOpen={showConfirmModal}
