   736→								</span>
   737→							</div>
   738→						)}
   739→						<div className="flex justify-between text-slate-300">
   740→							<span>Target for markdown file rendering</span>
   741→							<span className="font-semibold">
   742→								{targetAgent === "agents-md"
   743→									? "AGENTS.md"
   744→									: targetAgent === "claude-code"
   745→										? "Claude Code"
   746→										: "GitHub Copilot"}
   747→							</span>
   748→						</div>
   749→						<div className="flex justify-between text-slate-300">
   750→							<span>Provider</span>
   751→							<span className="font-semibold">{selectedProvider}</span>
   752→						</div>
   753→					</div>
   754→					<div className="flex justify-end gap-3 pt-2">
   755→						<button
   756→							onClick={() => setShowConfirmModal(false)}
   757→							className="btn-secondary"
   758→						>
   759→							Cancel
   760→						</button>
   761→						<button
   762→							onClick={() => {
   763→								setShowConfirmModal(false);
   764→								handleExecute();
   765→							}}
   766→							className="btn-primary"
   767→						>
   768→							Confirm
   769→						</button>
   770→					</div>
   771→				</div>
   772→			</Modal>
   773→
   774→			{/* Generated Prompts (legacy mode) */}
   775→			{prompts && (
   776→				<div className="space-y-6">
   777→					{prompts.errorFixPrompt && (
   778→						<PromptDisplay
   779→							title={`Fix Errors Prompt (${prompts.errorCount} issues)`}
   780→							content={prompts.errorFixPrompt}
   781→						/>
   782→					)}
   783→					{prompts.suggestionEnrichPrompt && (
   784→						<PromptDisplay
   785→							title={`Enrich Suggestions Prompt (${prompts.suggestionCount} issues)`}
   786→							content={prompts.suggestionEnrichPrompt}
   787→						/>
   788→					)}
   789→				</div>
   790→			)}
   791→		</div>
   792→	);
   793→}
   794→
   795→function IssueSection({
   796→	title,
   797→	count,
   798→	entries,
   799→	onRemove,
   800→}: {
   801→	title: string;
   802→	count: number;
   803→	entries: { key: string; issue: Issue }[];
   804→	onRemove: (key: string) => void;
   805→}) {
   806→	return (
   807→		<div className="bg-slate-800/50 rounded-lg border border-slate-700/50 p-3">
   808→			<div className="flex items-center justify-between mb-2">
   809→				<span className="text-sm font-semibold text-slate-200">
   810→					{title} ({count})
   811→				</span>
   812→			</div>
   813→			<div className="space-y-1 max-h-48 overflow-y-auto custom-scrollbar">
   814→				{entries.map(({ key, issue }) => {
   815→					const numericSeverity = getIssueSeverity(issue);
   816→					const severityLevel = getSeverityLevel(numericSeverity);
   817→					const issueTitle =
   818→						issue.title || issue.problem || issue.description || issue.category;
   819→
   820→					return (
   821→						<div
   822→							key={key}
   823→							className="flex items-center gap-2 py-1 px-1 rounded hover:bg-slate-700/30 group"
   824→						>
   825→							<div
   826→								className={`severity-dot severity-dot-${severityLevel} flex-shrink-0`}
   827→							/>
   828→							<div className="flex-shrink-0 w-[72px]">
   829→								{issue.issueType === "error" ? (
   830→									<span className={getSeverityColor(issue.severity)}>
   831→										{severityLevel.charAt(0).toUpperCase() +
   832→											severityLevel.slice(1)}
   833→									</span>
   834→								) : (
   835→									<span className={getImpactBadgeClass(issue.impactLevel)}>
   836→										{getImpactLabel(issue.impactLevel)}
   837→									</span>
   838→								)}
   839→							</div>
   840→							<span className="flex-1 min-w-0 text-sm text-slate-300 truncate">
   841→								{issueTitle}
   842→							</span>
   843→							{issue.evaluatorName && (
   844→								<span className="flex-shrink-0 text-xs text-slate-500 hidden sm:block">
   845→									{issue.evaluatorName}
   846→								</span>
   847→							)}
   848→							<button
   849→								onClick={() => onRemove(key)}
   850→								className="flex-shrink-0 text-slate-500 hover:text-red-400 transition-colors opacity-0 group-hover:opacity-100"
   851→								title="Remove from remediation"
   852→							>
   853→								<svg
   854→									className="h-4 w-4"
   855→									fill="none"
   856→									viewBox="0 0 24 24"
   857→									stroke="currentColor"
   858→								>
   859→									<path
   860→										strokeLinecap="round"
   861→										strokeLinejoin="round"
   862→										strokeWidth={2}
   863→										d="M6 18L18 6M6 6l12 12"
   864→									/>
   865→								</svg>
   866→							</button>
   867→						</div>
   868→					);
   869→				})}
   870→			</div>
   871→		</div>
   872→	);
   873→}
   874→
   875→function PromptDisplay({ title, content }: { title: string; content: string }) {
   876→	return (
   877→		<div className="card">
   878→			<div className="flex items-center justify-between mb-3">
   879→				<h3 className="text-subheading text-slate-200">{title}</h3>
   880→				<CopyButton text={content} variant="text" />
   881→			</div>
   882→			<div className="bg-slate-900/50 rounded-lg p-4 border border-slate-700/50 font-mono text-xs text-slate-300 max-h-96 overflow-y-auto custom-scrollbar whitespace-pre-wrap">
   883→				{content}
   884→			</div>
   885→		</div>
   886→	);
   887→}
   888→

